"use client";

import { Button } from "@/components/ui/button";
import BadgeCustom from "@/components/badge-custom";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Textarea } from "@/components/ui/textarea";
import {
  IItem,
  itemType,
  IItemZone,
  IPatrolChecklist,
  IPatrolResult,
  IUser,
  IDefect,
  IComment,
} from "@/app/type";
import React, { useState, useEffect } from "react";
import { fetchData, getInitials, getItemTypeVariant } from "@/lib/utils";
import { useParams } from "next/navigation";
import { useTranslations } from "next-intl";
import { Skeleton } from "./ui/skeleton";
import { formatTime } from "@/lib/utils";
import AlertDefect from "./alert-defect";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";

interface PatrolChecklistProps {
  user: IUser;
  patrolChecklist: IPatrolChecklist;
  disabled: boolean;
  handleResult: (result: {
    inspectorId: number;
    itemId: number;
    zoneId: number;
    status: boolean;
  }) => void;
  results: Array<{ itemId: number; zoneId: number; status: boolean }>;
  patrolResult: IPatrolResult[];
  response?: (defect: IDefect) => void
}

export default function PatrolChecklist({
  user,
  patrolChecklist,
  disabled,
  handleResult,
  results = [],
  patrolResult,
  response,
}: PatrolChecklistProps) {
  const [mounted, setMounted] = useState<boolean>(false);
  const [resultStatus, setResultStatus] = useState<{
    [key: string]: boolean | null;
  }>({});
  const [comment, setComment] = useState<string>("")
  const [patrolResultState, setPatrolResultState] = useState<IPatrolResult[]>(patrolResult);
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const s = useTranslations("Status");
  const z = useTranslations("Zone");
  const param = useParams()

  const checkStatus = (itemId: number, zoneId: number) => {
    const result = results.find(
      (res) => res.itemId === itemId && res.zoneId === zoneId
    );
    return result ? result.status : null;
  };

  useEffect(() => {
    if (results.length > 0) {
      const initialStatus = results.reduce((acc, result) => {
        acc[`${result.itemId}-${result.zoneId}`] = result.status;
        return acc;
      }, {} as { [key: string]: boolean | null });

      setResultStatus(initialStatus);
    }
  }, [results]);

  const handleCreateComment = async (
    message: string,
    patrolResultId: number,
    supervisorId: number
  ) => {

    const data = {
      message: message,
      patrolResultId: patrolResultId,
      supervisorId: supervisorId
    };

    try {
      const comment = await fetchData(
        "post",
        `/patrol/${param.id}/comment`,
        true,
        data,
      );
      if (!message) {
        toast({
          variant: "error",
          title: a("PatrolMissingCreateCommentTitle"),
          description: a("PatrolMissingCreateCommentDescription"),
        });
      } else {
        toast({
          variant: "default",
          title: a("PatrolCreateCommentTitle"),
          description: a("PatrolCreateCommentDescription"),
        });
      }
      fetchRealtimeComment(comment, patrolResultId)
    } catch (error) {
      console.error("Error creating Comment:", error);
    }
  };

  const fetchRealtimeComment = (comment: IComment, patrolResultId: number) => {
    setPatrolResultState(prevState => prevState.map(pr => {
      if (pr.id === patrolResultId) {
        return {
          ...pr,
          comments: pr.comments ? [...pr.comments, comment] : [comment]
        };
      }
      return pr;
    }));
  }

  const getExistingResult = (itemId: number, zoneId: number) => {
    const result = patrolResult.find(
      (res) => res.itemId === itemId && res.zoneId === zoneId
    );
    return result;
  };

  const fetchRealtimeData = (defect: IDefect) => {
    response(defect)
  }

  useEffect(() => {
    if (patrolResult && patrolChecklist.checklist.items) {
      const initialStatus = patrolChecklist.checklist.items.reduce((acc, item) => {
        item.itemZones.flatMap((itemZone: IItemZone) => {
          const matchingResult = patrolResult.find((result) => {
            return result.itemId === item.id && result.zoneId === itemZone.zone.id;
          });
          //  matchingResult  status  null  status
          if (matchingResult && matchingResult.status !== null) {
            acc[`${item.id}-${itemZone.zone.id}`] = matchingResult.status;
          }

        });
        return acc;
      }, {} as { [key: string]: boolean | null });

      setResultStatus(initialStatus); //  resultStatus 
    }
  }, [patrolChecklist.checklist, patrolResult]);

  const handleClick = (inspectorId: number, itemId: number, zoneId: number, status: boolean) => {
    if (!disabled) {
      handleResult({ inspectorId, itemId, zoneId, status });
      setResultStatus((prev) => ({
        ...prev,
        [`${itemId}-${zoneId}`]: status,
      }));
    }
  };

  useEffect(() => {
    if (patrolChecklist) {
      setMounted(true)
    }
  }, [])

  if (!mounted) {
    return (
      <Skeleton className="flex h-16 items-center">
        <Skeleton className="flex h-10 ms-4 w-56 bg-card"></Skeleton>
      </Skeleton>
    )
  }

  const checkResultStatusChecklist = (checklistId: number): boolean => {
    const checklistResults = patrolResult.filter(result =>
      patrolChecklist.checklist.id === checklistId &&
      patrolChecklist.checklist.items.some(item =>
        item.id === result.itemId &&
        item.itemZones.some(itemZone => itemZone.zone.id === result.zoneId)
      )
    );

    return checklistResults.every(result => result.status !== null);
  };

  return (
    <div className="bg-card rounded-md px-6 py-4">
      <Accordion type="single" collapsible defaultValue="item-1">
        <AccordionItem value="item-1" className="border-none">
          <AccordionTrigger className="flex flex-row  hover:no-underline text-2xl font-bold p-0">
            <div key={patrolChecklist.checklist.id} className="flex flex-row items-center gap-3">
              {checkResultStatusChecklist(patrolChecklist.checklist.id) ?
                null
                :
                <span className="material-symbols-outlined text-destructive">error</span>
              }
              <p>{patrolChecklist.checklist.title}</p>
            </div>
          </AccordionTrigger>
          <AccordionContent className="p-0" >
            <div className="flex items-center gap-2 mb-2 mt-2">
              <div className="flex items-center gap-1 text-muted-foreground">
                <span className="material-symbols-outlined">person_search</span>
                <p className="text-lg font-semibold">{t("inspector")}</p>
              </div>
              <div className="flex items-center gap-1">
                <Avatar className="custom-shadow h-[35px] w-[35px]">
                  <AvatarImage
                    src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${patrolChecklist.inspector.profile.image?.path}`}
                  />
                  <AvatarFallback id={patrolChecklist.inspector.id.toString()}>
                    {getInitials(patrolChecklist.inspector.profile.name)}
                  </AvatarFallback>
                </Avatar>

                <p className="text-card-foreground text-lg">{patrolChecklist.inspector.profile.name}</p>
              </div>
            </div>
            <div>
              {patrolChecklist.checklist.items?.map((item: IItem) => (
                <Accordion type="single" collapsible>
                  <AccordionItem value="item-1" className="border-none">
                    <AccordionTrigger className="hover:no-underline">
                      <div className="flex items-center justify-between w-full pe-2">
                        <p className="text-xl font-semibold">{item.name}</p>
                        <BadgeCustom
                          variant={getItemTypeVariant(item.type as itemType).variant}
                          iconName={getItemTypeVariant(item.type as itemType).iconName}
                          showIcon={true}
                          shape="square"
                        >
                          {s(item.type)}
                        </BadgeCustom>
                      </div>
                    </AccordionTrigger>
                    <AccordionContent className="flex flex-col gap-4">
                      {item.itemZones.flatMap((itemZones: IItemZone) => {
                        const status = checkStatus(item.id, itemZones.zone.id);
                        const existingResult = getExistingResult(
                          item.id,
                          itemZones.zone.id
                        );
                        return (
                          <div key={itemZones.zone.id} className="bg-background rounded-md px-4 py-2">
                            <div className="flex flex-row justify-between items-center">
                              <div className="flex flex-col">
                                <div className="flex flex-row items-center gap-2">
                                  <div className="flex flex-row items-center gap-1">
                                    <span className="material-symbols-outlined text-muted-foreground">
                                      location_on
                                    </span>
                                    <p className="font-semibold text-base text-muted-foreground">
                                      {t("Zone")}
                                    </p>
                                  </div>
                                  <p className="text-base">{z(itemZones.zone.name)}</p>
                                </div>

                                <div className="flex flex-row items-center gap-2">
                                  <div className="flex items-center text-muted-foreground gap-1">
                                    <span className="material-symbols-outlined">engineering</span>
                                    <p className="text-lg font-semibold">{t("supervisor")}</p>
                                  </div>

                                  <div className="flex items-center gap-1">
                                    <Avatar className="custom-shadow h-[35px] w-[35px]">
                                      <AvatarImage
                                        src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${itemZones.zone.supervisor.profile.image?.path}`}
                                      />
                                      <AvatarFallback id={itemZones.zone.supervisor.id.toString()}>
                                        {getInitials(itemZones.zone.supervisor.profile.name)}
                                      </AvatarFallback>
                                    </Avatar>
                                    <p className="text-card-foreground text-lg">{itemZones.zone.supervisor.profile.name}</p>
                                  </div>

                                </div>
                              </div>
                              <div className="flex gap-2">
                                <Button
                                  variant={
                                    resultStatus[`${item.id}-${itemZones.zone.id}`] ===
                                      true
                                      ? "success"
                                      : "secondary"
                                  }
                                  className={`w-[155px] ${resultStatus === null
                                    ? "bg-secondary text-card-foreground"
                                    : ""
                                    }
                                                                    ${existingResult?.status ===
                                      true
                                      ? "bg-[#27BC31] hover:bg-[#27BC31]"
                                      : ""
                                    }
                                                                    ${existingResult?.status ===
                                      false
                                      ? "bg-secondary hover:bg-secondary"
                                      : ""
                                    }
                                                                    ${disabled
                                      ? " cursor-not-allowed opacity-50"
                                      : ""
                                    }
                                                                    `}
                                  onClick={() => {
                                    if (!existingResult.status === true || existingResult.status === null) {
                                      handleClick(user.id, item.id, itemZones.zone.id, true);
                                    }
                                  }}
                                >
                                  <span className="material-symbols-outlined">
                                    check
                                  </span>
                                  <p>{t("Yes")}</p>
                                </Button>
                                <Button
                                  variant={
                                    resultStatus[`${item.id}-${itemZones.zone.id}`] ===
                                      false
                                      ? "fail"
                                      : "secondary"
                                  }
                                  className={`w-[155px] ${resultStatus === null
                                    ? "bg-secondary text-card-foreground"
                                    : ""
                                    }
                                                                    ${existingResult?.status ===
                                      false
                                      ? "bg-destructive hover:bg-destructive"
                                      : ""
                                    }
                                                                    ${existingResult?.status ===
                                      true
                                      ? "bg-secondary hover:bg-secondary"
                                      : ""
                                    }
                                                                    ${disabled
                                      ? " cursor-not-allowed opacity-50"
                                      : ""
                                    }
                                                                    `}
                                  onClick={() => {
                                    if (!existingResult.status === false || existingResult.status === null) {
                                      handleClick(user.id, item.id, itemZones.zone.id, false);
                                    }
                                  }}
                                >
                                  <span className="material-symbols-outlined">
                                    close
                                  </span>
                                  <p>{t("No")}</p>
                                </Button>
                              </div>
                            </div>

                            {(status === false ||
                              existingResult?.status === false) && (
                                <div className="flex flex-col items-start gap-4 mt-2">
                                  <AlertDefect
                                    item={item}
                                    type={"report"}
                                    result={existingResult}
                                    patrolResults={patrolResult}
                                    response={(defect: IDefect) => (
                                      fetchRealtimeData(defect)
                                    )}
                                  />

                                  <div className="flex flex-col items-start w-full gap-2">
                                    {patrolResultState.flatMap(pr => pr.comments ?? []).map((comment: IComment) =>
                                      comment.patrolResultId === existingResult.id ?
                                        (
                                          <div key={comment.timestamp} className="flex flex-row items-center bg-secondary rounded-md w-full px-6 py-4 gap-2" >
                                            <div className={`flex justify-center items-center w-3 h-3 rounded-full ${!comment.status ? 'bg-primary' : 'bg-green'}`} />
                                            <p className="text-muted-foreground text-xl font-semibold">{formatTime(comment.timestamp)}</p>
                                            <div className="flex items-end">
                                              <p className="text-xl">{comment.message}</p>
                                            </div>
                                          </div>
                                        )
                                        : null
                                    )}
                                    <Textarea
                                      className="min-h-[120px] bg-secondary border-none text-xl"
                                      placeholder={`${t("Comment")}...`}
                                      disabled={disabled}
                                      value={comment}
                                      onChange={(e) => setComment(e.target.value)}
                                    />
                                  </div>

                                  <div className="flex justify-end w-full mt-2">
                                    <Button
                                      variant={"primary"}
                                      size={"lg"}
                                      disabled={disabled}
                                      onClick={() => {
                                        handleCreateComment(comment, existingResult.id, itemZones.zone.supervisor.id)
                                        setComment("")
                                      }}
                                    >
                                      <span className="material-symbols-outlined me-2">
                                        send
                                      </span>
                                      {t("Send")}
                                    </Button>
                                  </div>
                                </div>
                              )}
                          </div>
                        )
                      })}
                    </AccordionContent>
                  </AccordionItem>
                </Accordion>
              ))}
            </div>
          </AccordionContent>
        </AccordionItem>
      </Accordion>
    </div>
  );
}

'use client'
import { useEffect, useState } from "react";
import { Skeleton } from "./ui/skeleton";

interface TimerProps {
    days: string;
    hours: string;
    minutes: string;
    seconds: string;
}

const getTimeElapsed = (startTime: number): TimerProps => {
    const difference = new Date().getTime() - startTime;

    if (difference <= 0) {
        return { days: "00", hours: "00", minutes: "00", seconds: "00" };
    }

    const days = String(Math.floor(difference / (1000 * 60 * 60 * 24))).padStart(2, '0');
    const hours = String(Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))).padStart(2, '0');
    const minutes = String(Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60))).padStart(2, '0');
    const seconds = String(Math.floor((difference % (1000 * 60)) / 1000)).padStart(2, '0');

    return { days, hours, minutes, seconds };
};

const formatDuration = (duration: string): TimerProps => {
    const match = duration.match(/(?:(\d+)d\s*)?(?:(\d+)h\s*)?(?:(\d+)m\s*)?(?:(\d+)s)?/);

    const days = match?.[1] ? match[1].padStart(2, '0') : "00";
    const hours = match?.[2] ? match[2].padStart(2, '0') : "00";
    const minutes = match?.[3] ? match[3].padStart(2, '0') : "00";
    const seconds = match?.[4] ? match[4].padStart(2, '0') : "00";

    return { days, hours, minutes, seconds };
};

const PatrolTimer = ({ launchDate, patrolStatus, patrolDuration }: { launchDate: string, patrolStatus: string, patrolDuration: string }) => {
    const [timeElapsed, setTimeElapsed] = useState<TimerProps>({ days: "00", hours: "00", minutes: "00", seconds: "00" });
    const [startTime, setStartTime] = useState<number>(new Date(launchDate).getTime());
    const [mounted, setMounted] = useState<boolean>(false);

    useEffect(() => {
        setMounted(true);
        setStartTime(new Date(launchDate).getTime());

        if (patrolStatus === "completed") {
            setTimeElapsed(formatDuration(patrolDuration));
            return; //  patrolStatus  completed
        }

        if (patrolStatus === "on_going") {
            setTimeElapsed(getTimeElapsed(new Date(launchDate).getTime()));

            const interval = setInterval(() => {
                setTimeElapsed(getTimeElapsed(new Date(launchDate).getTime()));
            }, 1000);

            return () => clearInterval(interval);
        }
    }, [patrolStatus, launchDate, patrolDuration]);

    if (!mounted) {
        return <Skeleton />;
    }

    return (
        <div className='flex flex-row items-center text-2xl font-semibold gap-2'>
            {patrolStatus === "on_going" ? (
                <>
                    <span className="material-symbols-outlined text-card-foreground w-[22px] h-[22px]">
                        timer
                    </span>
                    {timeElapsed.days !== "00" && <p>{timeElapsed.days} Days</p>}
                    {(timeElapsed.hours !== "00" || timeElapsed.days !== "00") && <p>{timeElapsed.hours} Hours</p>}
                    <p>{timeElapsed.minutes} Minutes</p>
                    <p>{timeElapsed.seconds} Seconds</p>
                </>
            ) : patrolStatus === "completed" && patrolDuration ? (
                <>
                    <span className="material-symbols-outlined text-card-foreground w-[22px] h-[22px]">
                        timer
                    </span>
                    {formatDuration(patrolDuration).days !== "00" && <p>{formatDuration(patrolDuration).days} Days</p>}
                    {(formatDuration(patrolDuration).hours !== "00" || formatDuration(patrolDuration).days !== "00") && <p>{formatDuration(patrolDuration).hours} Hours</p>}
                    <p>{formatDuration(patrolDuration).minutes} Minutes</p>
                    <p>{formatDuration(patrolDuration).seconds} Seconds</p>
                </>
            ) : null}
        </div>
    );
};

export default PatrolTimer;

"use client";
import React, { useEffect, useState } from "react";
import { defectStatus, IDefect, IImage, IZone } from "@/app/type";
import BadgeCustom from "@/components/badge-custom";
import Image from "next/image";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious, type CarouselApi } from "@/components/ui/carousel";
import { Card, CardContent } from "@/components/ui/card";
import { fetchData, formatPatrolId, formatTime, getDefectStatusVariant, getInitials, getItemTypeVariant } from "@/lib/utils";
import { useTranslations } from "next-intl";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import AlertDefect from "./alert-defect";
import { useLocale } from "next-intl";
import { useRouter } from "next/navigation";
import Map from "@/components/map";
import { AlertCustom } from "./alert-custom";
import { toast } from "@/hooks/use-toast";

interface ReportDefectProps {
  defect: IDefect,
  page: "patrol-view-detail" | "patrol-defect" | "patrol-view-report"
  response: (defect: IDefect) => void
}

export default function ReportDefect({ defect, page, response }: ReportDefectProps) {
  const s = useTranslations("Status");
  const t = useTranslations("General")
  const a = useTranslations("Alert")

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
    }
  };

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };


  const beforeImage = defect.images
    .sort((a, b) => b.image.id - a.image.id) //  id 
    .filter((image) => image.image.user.id === defect.userId)
    .map((image: any) => ({
      path: image.image.path,
    })) || null;

  const afterImage = defect.images
    .sort((a, b) => b.image.id - a.image.id) //  id 
    .filter((image) => image.image.user.id !== defect.userId)
    .map((image: any) => ({
      path: image.image.path,
    })) || null;

  const [isBeforeCarouselOpen, setIsBeforeCarouselOpen] = useState(false);
  const [isAfterCarouselOpen, setIsAfterCarouselOpen] = useState(false);
  const [beforeSlideIndex, setBeforeSlideIndex] = useState(0);
  const [afterSlideIndex, setAfterSlideIndex] = useState(0);

  const [alertBoxTitle, setAlertBoxTitle] = useState();
  const [alertBoxDescription, setAlertBoxDescription] = useState();
  const router = useRouter();
  const locale = useLocale();

  // Handle before image click
  const handleBeforeImageClick = (index: number) => {
    setBeforeSlideIndex(index);
    setIsBeforeCarouselOpen(true);
  };

  // Handle after image click
  const handleAfterImageClick = (index: number) => {
    setAfterSlideIndex(index);
    setIsAfterCarouselOpen(true);
  };

  // Close before carousel
  const handleCloseBeforeCarousel = () => {
    setIsBeforeCarouselOpen(false);
    setBeforeSlideIndex(0);
  };

  // Close after carousel
  const handleCloseAfterCarousel = () => {
    setIsAfterCarouselOpen(false);
    setAfterSlideIndex(0);
  };
  const handleReworkOrVerifyDefect = async (
    id: number,
    status: defectStatus,
    supervisorId: number
  ) => {
    let title
    let description
    if (status === 'pending_inspection') {
      title = 'ConfirmReworkTitle'
      description = 'ConfirmReworkDescription'
    } else if (status === 'completed') {
      title = 'ConfirmCompleteTitle'
      description = 'ConfirmCompleteDescription'
    }

    setPendingAction(() => () => {
      reworkOrVerifyDefect(id, status, supervisorId)
    });
    setAlertBoxTitle(title)
    setAlertBoxDescription(description)
    handleOpenDialog();
  };

  const reworkOrVerifyDefect = async (
    id: number,
    status: defectStatus,
    supervisorId: number
  ) => {
    const data = {
      status: status,
      supervisorId: supervisorId
    }

    try {
      let title
      let description
      if (data.status === 'pending_inspection') {
        title = 'ReworkDefectTitle'
        description = 'ReworkDefectDescription'
      } else if (status === 'completed') {
        title = 'CompleteDefectTitle'
        description = 'CompleteDefectDescription'
      }
      const updateStatusDefect = await fetchData(
        "put",
        `/defect/${id}`,
        true,
        data,
      );
      response(updateStatusDefect)
      toast({
        variant: "success",
        title: a(title),
        description: a(description),
      });
    } catch (error) {
      console.error("Error creating defect:", error);
    }
  }

  const [api, setApi] = useState<CarouselApi>()

  useEffect(() => {
    if (!api) {
      return
    }
    api.on("select", () => {
      setAfterSlideIndex(api.selectedScrollSnap())
      setBeforeSlideIndex(api.selectedScrollSnap())
    })
  }, [api])

  const fetchRealtimeData = (defect: IDefect) => {
    response(defect)
  }

  const navigateToPatrol = (patrolId: number) => {
    router.push(`/${locale}/patrol/${patrolId}/detail`)
  }

  return (
    <Accordion type="single" collapsible>
      <AccordionItem
        value="item-1"
        className="bg-card rounded-md w-full px-4 py-2 border-none"
      >
        <AccordionTrigger className="hover:no-underline">
          <div className="flex items-center space-x-2">
            <span className="material-symbols-outlined text-muted-foreground cursor-default ">
              schedule
            </span>
            <span className="text-lg font-bold text-muted-foreground cursor-default ">
              {formatTime(defect.startTime)}
            </span>
            <h2 className="text-lg font-bold text-card-foreground cursor-default ">
              {defect.name}
            </h2>
          </div>
        </AccordionTrigger>

        <div className="flex justify-between items-center mb-4 mt-2">
          <div className="flex space-x-2">
            {(() => {
              const { iconName, variant } = getDefectStatusVariant(defect.status);
              return (
                <BadgeCustom
                  variant={variant}
                  showIcon={true}
                  iconName={iconName}
                >
                  {s(defect.status)}
                </BadgeCustom>
              );
            })()}
            {(() => {
              const { iconName, variant } = getItemTypeVariant(defect.type);
              return (
                <BadgeCustom
                  variant={variant}
                  showIcon={true}
                  shape={"square"}
                  iconName={iconName}
                >
                  {s(defect.type)}
                </BadgeCustom>
              );
            })()}
          </div>
        </div>

        {/*  */}
        <AccordionContent>
          <div className="flex flex-col p-1">
            {/* supervisor */}
            <div className="flex flex-row justify-between w-full h-9 mb-4">
              <div className="flex items-center gap-2">
                <div className="flex items-center gap-1 text-muted-foreground">
                  <span className="material-symbols-outlined">engineering</span>
                  <p className="text-lg font-semibold">{t("supervisor")}</p>
                </div>
                <div className="flex items-center gap-1">
                  <Avatar className="custom-shadow h-[35px] w-[35px]">
                    <AvatarImage
                      src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${defect.patrolResult.itemZone.zone.supervisor.profile.image?.path}`}
                    />
                    <AvatarFallback id={defect.patrolResult.itemZone.zone.supervisor.id.toString()}>
                      {getInitials(defect.patrolResult.itemZone.zone.supervisor.profile.name)}
                    </AvatarFallback>
                  </Avatar>

                  <p className="text-card-foreground text-lg">{defect.patrolResult.itemZone.zone.supervisor.profile.name}</p>
                </div>
              </div>
              <div>
                {defect.status === "reported" as defectStatus && (
                  <AlertDefect
                    defect={defect}
                    type={page == "patrol-view-detail" ? "report" : "edit"}
                    response={(defect: IDefect) => fetchRealtimeData(defect)}
                  />
                )}
              </div>
            </div>

            {/* Patrol */}
            {page === "patrol-defect" && (<div className="flex flex-col mb-4">
              <div className="flex flex-row mb-1">
                <div className="flex flex-row pr-2 items-center pt-1">
                  <span className="material-symbols-outlined text-muted-foreground cursor-default user-select-none mr-1">
                    task
                  </span>
                  <p className="text-base font-semibold text-muted-foreground  cursor-default user-select-none">
                    Patrol
                  </p>
                </div>
              </div>

              <Button className="w-fit h-fit bg-secondary" variant="ghost" onClick={() => navigateToPatrol(defect.patrolResult.patrol.id)}>
                <div className="flex flex-col items-start py-4 px-6">
                  <p className="text-xl font-semibold text-card-foreground mb-2 cursor-default user-select-none ">
                    {defect.patrolResult.patrol.preset.title}
                  </p>

                  <div className="flex flex-row items-center">
                    <span className="material-symbols-outlined text-muted-foreground cursor-default user-select-none pr-2">
                      description
                    </span>
                    <p className="text-muted-foreground cursor-default user-select-none">
                      {formatPatrolId(defect.patrolResult.patrol.id)}
                    </p>
                  </div>
                </div>
              </Button>
            </div>)}

            {/* Detail */}
            <div className="flex flex-col mb-4">
              <div className="flex flex-row mb-1">
                <div className="flex flex-row pr-2 items-center pt-1">
                  <span className="material-symbols-outlined text-muted-foreground cursor-default user-select-none mr-1">
                    data_info_alert
                  </span>
                  <p className="text-base font-semibold text-muted-foreground cursor-default user-select-none">
                    Detail
                  </p>
                </div>
              </div>

              <div>
                <Textarea
                  className="text-xl text-text w-full h-40 border-none bg-secondary pointer-events-none"
                  placeholder="Description"
                  value={defect.description}
                  readOnly
                />
              </div>
            </div>

            {/* Zone */}
            <div className="flex flex-col mb-4">
              <div className="flex flex-row mb-1">
                <div className="flex flex-row pr-2 items-center pt-1">
                  <span className="material-symbols-outlined text-muted-foreground cursor-default user-select-none mr-1">
                    location_on
                  </span>
                  <p className="text-base font-semibold text-muted-foreground cursor-default user-select-none">
                    Zone
                  </p>
                </div>
              </div>

              <div className="p-2 rounded-md bg-secondary map-container cursor-default user-select-none ">
                <Map disable={true} initialSelectedZones={[defect.patrolResult.zoneId]} />
              </div>
            </div>

            {/* Before After */}
            <div className="flex w-full space-x-4 mb-4">
              <div className="flex space-x-4 w-full">
                {/* before */}
                <div className="w-full">
                  <div className="flex items-center">
                    <p className="text-base font-semibold text-muted-foreground mb-1 cursor-default user-select-none">
                      Before
                    </p>
                  </div>

                  <div>
                    <div className="p-4 rounded-md bg-secondary h-40 flex items-center justify-center cursor-default user-select-none" onClick={() => handleBeforeImageClick(0)}>
                      {beforeImage && beforeImage.length > 0 && beforeImage[0].path ? (
                        <Image
                          src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${beforeImage[0].path}`}
                          alt="First Image"
                          width={270}
                          height={250}
                          className="object-contain cursor-pointer w-full h-full"
                          unoptimized
                        />
                      ) : (
                        <p>Waiting for the results.</p>
                      )}
                    </div>
                    {isBeforeCarouselOpen && beforeImage && beforeImage?.length > 0 && (
                      <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
                        <div className="relative">
                          <Carousel setApi={setApi} className="w-full max-w-screen-lg">
                            <CarouselContent>
                              {Array.from({ length: beforeImage.length }).map((_, index) => (
                                <CarouselItem key={index}>
                                  <div className="flex justify-center">
                                    <Card>
                                      <CardContent className="flex items-center justify-center h-[600px] w-[900px] overflow-hidden p-4">
                                        <div className="flex items-center justify-center h-full w-full">
                                          <Image
                                            className="object-contain w-full h-full"
                                            src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${beforeImage[index].path}`}
                                            alt={`${beforeImage[index].path}`}
                                            width={800}
                                            height={500}
                                            priority
                                            unoptimized
                                          />
                                        </div>
                                      </CardContent>
                                    </Card>
                                  </div>
                                </CarouselItem>
                              ))}
                            </CarouselContent>
                            <CarouselPrevious />
                            <CarouselNext />
                          </Carousel>
                          <div className="flex justify-center mt-4">
                            {beforeImage.map((_, index) => (
                              <button
                                key={index}
                                onClick={() => {
                                  setBeforeSlideIndex(index);
                                }}
                                disabled
                                className={`h-3 w-3 rounded-full mx-1 ${beforeSlideIndex === index ? 'bg-white' : 'bg-gray-400'}`}
                                aria-label={`Slide ${index + 1}`}
                              />
                            ))}
                          </div>
                        </div>
                        <button onClick={handleCloseBeforeCarousel} className="absolute top-4 right-4 text-white">
                          Close
                        </button>
                      </div>
                    )}
                  </div>
                </div>

                {/* after */}
                <div className="w-full">
                  <div className="flex items-center">
                    <p className="text-base font-semibold text-muted-foreground mb-1 cursor-default user-select-none">
                      After
                    </p>
                  </div>

                  <div>
                    <div className="p-4 rounded-md bg-secondary h-40 flex items-center justify-center cursor-default user-select-none" onClick={() => handleAfterImageClick(0)}>
                      {afterImage && afterImage.length > 0 && afterImage[0].path ? (
                        <Image
                          src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${afterImage[0].path}`}
                          alt="First Image"
                          width={270}
                          height={250}
                          className="object-contain cursor-pointer w-full h-full"
                          unoptimized
                        />
                      ) : (
                        <p>Waiting for the results.</p>
                      )}
                    </div>
                    {isAfterCarouselOpen && afterImage && afterImage?.length > 0 && (
                      <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
                        <div className="relative">
                          <Carousel setApi={setApi} className="w-full max-w-screen-lg">
                            <CarouselContent>
                              {Array.from({ length: afterImage.length }).map((_, index) => (
                                <CarouselItem key={index}>
                                  <div className="flex justify-center">
                                    <Card>
                                      <CardContent className="flex items-center justify-center h-[600px] w-[900px] overflow-hidden p-4">
                                        <div className="flex items-center justify-center h-full w-full">
                                          <Image
                                            className="object-contain w-full h-full"
                                            src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${afterImage[index].path}`}
                                            alt={`${afterImage[index].path}`}
                                            width={800}
                                            height={500}
                                            priority
                                            unoptimized
                                          />
                                        </div>
                                      </CardContent>
                                    </Card>
                                  </div>
                                </CarouselItem>
                              ))}
                            </CarouselContent>
                            <CarouselPrevious />
                            <CarouselNext />
                          </Carousel>
                          <div className="flex justify-center mt-4">
                            {afterImage.map((_, index) => (
                              <button
                                key={index}
                                onClick={() => {
                                  setAfterSlideIndex(index);
                                }}
                                disabled
                                className={`h-3 w-3 rounded-full mx-1 ${afterSlideIndex === index ? 'bg-white' : 'bg-gray-400'}`}
                                aria-label={`Slide ${index + 1}`}
                              />
                            ))}
                          </div>
                        </div>
                        <button onClick={handleCloseAfterCarousel} className="absolute top-4 right-4 text-white">
                          Close
                        </button>
                      </div>
                    )}
                  </div>
                </div>

              </div>
            </div>

            {isDialogOpen && (
              <AlertCustom
                title={a(alertBoxTitle)}
                description={a(alertBoxDescription)}
                primaryButtonText={t("Confirm")}
                primaryIcon="check"
                primaryVariant="destructive"
                secondaryButtonText={t("Cancel")}
                backResult={handleDialogResult}
              ></AlertCustom>
            )}
            <div>
              {defect.status === "resolved" && page === "patrol-defect" && (
                <div className="flex space-x-2 justify-end">
                  <Button variant="destructive" size={"lg"} onClick={() => handleReworkOrVerifyDefect(defect.id, "pending_inspection", defect.patrolResult.itemZone.zone.supervisor.id)}>
                    <span className="material-symbols-outlined mr-2 text-[20px]">
                      cancel
                    </span>
                    Rework
                  </Button>
                  <Button variant="success" size={"lg"} onClick={() => handleReworkOrVerifyDefect(defect.id, "completed", defect.patrolResult.itemZone.zone.supervisor.id)}>
                    <span className="material-symbols-outlined mr-2 text-[20px]">
                      check_circle
                    </span>
                    Verify
                  </Button>
                </div>
              )}
            </div>
          </div>
        </AccordionContent>
      </AccordionItem>
    </Accordion >
  );
}

import React, { useEffect, useState } from "react";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { IUser } from "@/app/type";
import { getInitials } from "@/lib/utils";
import { useTranslations } from "next-intl";
import { ScrollArea } from '@/components/ui/scroll-area';

interface UserDropdownProps {
  userData: IUser[];
  onUserSelect: (selectUser: IUser) => void;
  selectUser: IUser | null;
  color?: string;
}

const UserDropdown: React.FC<UserDropdownProps> = ({ userData, onUserSelect, selectUser, color = "card", }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleSelectUser = (user: IUser) => {
    onUserSelect(user);
    setIsOpen(false);
  };
  const t = useTranslations("General");

  return (
    <DropdownMenu onOpenChange={(open) => setIsOpen(open)}>
      <DropdownMenuTrigger className="w-[300px] h-[65px]">
        <Button
          variant="outline"
          className={`w-full h-full justify-between bg-${color} hover:bg-background border-none`}
        >
          <div className="flex items-center gap-2">
            {selectUser && (
              <Avatar>
                <AvatarImage
                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${selectUser?.profile?.image?.path}`}
                />
                <AvatarFallback id={selectUser.id.toString()}>
                  {getInitials(selectUser.profile.name)}
                </AvatarFallback>
              </Avatar>
            )}
            <p className="font-normal text-muted-foreground">
              {selectUser ? selectUser.profile.name : t("SelectAUser")}
            </p>
          </div>
          <span
            className={`material-symbols-outlined text-muted-foreground inline-block transition-transform duration-300 ${isOpen ? "rotate-180" : "rotate-0"
              }`}
          >
            expand_more
          </span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className={`p-0`}>
        <ScrollArea className="w-full h-72 rounded-md">
          {userData.map((user) => {
            return (
              <DropdownMenuItem
                key={user.id}
                className="flex items-center w-[300px] gap-2"
                onClick={() => handleSelectUser(user)}
              >
                <Avatar>
                  <AvatarImage
                    src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${user?.profile?.image?.path}`}
                  />
                  <AvatarFallback id={user.id.toString()}>
                    {getInitials(user.profile.name)}
                  </AvatarFallback>
                </Avatar>
                <p className="font-normal text-lg text-muted-foreground">
                  {user.profile.name}
                </p>
              </DropdownMenuItem>
            );
          })}
        </ScrollArea>
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

export default UserDropdown;

import { IDefect, IPatrol, IPatrolChecklist, IPatrolResult, IUser } from "@/app/type";
import Loading from "@/components/loading";
import { useSocket } from "@/components/socket-provider";
import { Skeleton } from "@/components/ui/skeleton";
import { fetchData } from "@/lib/utils";
import { useParams } from "next/navigation";
import React, { createContext, useContext, useState, ReactNode, useEffect, useRef, useDebugValue } from "react";
import { boolean, IpVersion } from "zod";
import { AlertCustom } from "@/components/alert-custom";
import { toast } from "@/hooks/use-toast";
import { useTranslations } from "next-intl";

interface PatrolContextProps {
    patrol: IPatrol | null;
    patrolResults: IPatrolResult[];
    results: IPatrolResult[];
    otherResults: IPatrolResult[];
    user: IUser | null;
    lock: boolean;
    isAlertOpen: boolean;
    handleResult: (result: IPatrolResult) => void;
    mergeResults: (newResults: IPatrolResult[]) => void;
    toggleLock: () => void;
    handleStartPatrol: () => void;
    handleFinishPatrol: () => void;
    fetchRealtimeData: (defect: IDefect, actionType: string) => void;
    calculateProgress: () => number;
    handleOpenDialog: () => void
    handleCloseDialog: () => void
    itemCounts: (patrol: IPatrol, results: IPatrolResult[]) => void
    mounted: boolean;
    canFinish: boolean;
    defects: IDefect[]
    countItems: number
    countFails: number
    countDefects: number
    patrolUser: IUser[]
    isHovered: boolean
    handleMouseLeave: () => void
    handleMouseEnter: () => void
    formatZone: (patrol: IPatrol) => string
    formatTimeDate: (dateStr: string) => string
    formatId: (id: number) => string
    formatDate: (dateStr: string) => string

}

const PatrolContext = createContext<PatrolContextProps | undefined>(undefined);

export const PatrolProvider: React.FC<{ children: React.ReactNode }> = ({
    children,
}) => {
    const [patrol, setPatrol] = useState<IPatrol | null>(null);
    const [results, setResults] = useState<IPatrolResult[]>([]);
    const [defects, setDefects] = useState<IDefect[]>([]);
    const [otherResults, setOtherResults] = useState<IPatrolResult[]>([]);
    const [patrolResults, setPatrolResults] = useState<IPatrolResult[]>([]);
    const [user, setUser] = useState<IUser | null>(null);
    const { socket, isConnected } = useSocket();
    const [lock, setLock] = useState(false);
    const [mounted, setMounted] = useState(false);
    const params = useParams();
    const a = useTranslations("Alert");

    const [patrolUser, setPatrolUser] = useState<IUser[] | null>([])
    const [isAlertOpen, setIsAlertOpen] = useState(false);
    const [isHovered, setIsHovered] = useState(false);

    const [countItems, setCountItems] = useState(0);
    const [countFails, setCountFails] = useState(0);
    const [countDefects, setCountDefects] = useState(0);

    const itemCounts = (patrol: IPatrol, results: IPatrolResult[]) => {
        let itemCounts = 0
        patrol?.patrolChecklists.forEach(patrolChecklist => {
            //  item 
            patrolChecklist.checklist.items.forEach(item => {
                item.itemZones.forEach(itemZone => {
                    itemCounts++
                })
            });
        });

        let countItems = itemCounts;
        let countFails = 0;
        let countDefects = 0;

        if (patrol?.status !== "pending" && patrol?.status !== "scheduled") {
            if (results) {
                for (const patrolResult of results) {
                    if (patrolResult.status === false) {
                        countFails++;
                        if (patrolResult.defects && patrolResult.defects.length !== 0) {
                            countDefects += patrolResult.defects.length;
                        }
                    }
                }
            }
        }

        setCountItems(countItems)
        setCountFails(countFails)
        setCountDefects(countDefects)

        return {countItems, countFails, countDefects}
    }


    const formatDate = (dateStr: string): string => {
        const date = new Date(dateStr);
        return date.toLocaleDateString('en-GB');
    };

    const formatId = (id: number): string => {
        return `P${id.toString().padStart(4, '0')}`;
    };
   
    const formatTimeDate = (dateStr: string): string => {
        const date = new Date(dateStr);
        const hours = date.getHours().toString().padStart(2, '0'); 
        const minutes = date.getMinutes().toString().padStart(2, '0'); 
        return `${hours}.${minutes}`;
    };

    const formatZone = (patrol: IPatrol) => {
        const zones = new Set<string>();
        patrol.patrolChecklists.forEach((checklist: any) => {
            checklist.checklist.items.forEach((item: any) => {
                item.itemZones.forEach((zoneObj: any) => {
                    const zoneName = zoneObj.zone.name;
                    if (typeof zoneName === "string") {
                        zones.add(zoneName);
                    }
                });
            });
        });
        return Array.from(zones)
            .map(
                (zone) =>
                    zone
                        .split("_") //  `_`
                        .map(
                            (word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
                        ) // 
                        .join(" ") // 
            )
            .join(", "); // 
    };

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const handleOpenDialog = () => {
        setIsAlertOpen(true);
    };

    const handleCloseDialog = () => {
        setIsAlertOpen(false)
    }

    const totalResults = patrolResults.length
    const checkedResults =
        patrolResults.filter((res) => res.status !== null).length || 0;
    const canFinish = checkedResults === totalResults;

    const getPatrolData = async () => {
        if (params.id) {
            try {
                const data = await fetchData("get", `/patrol/${params.id}?preset=true`, true);
                const result = await fetchData("get", `/patrol/${params.id}?result=true`, true);
                const savedResults = localStorage.getItem(`patrolResults_${data.id}`);
                const otherResults = localStorage.getItem(`otherResults_${data.id}`);
                if (savedResults) {
                    setResults(JSON.parse(savedResults));
                }
                if (otherResults) {
                    setOtherResults(JSON.parse(otherResults));
                }
                setPatrol(data);
                setPatrolResults(result.results)
            } catch (error) {
                console.error("Failed to fetch patrol data:", error);
            }
        }
    };

    const getDefectData = async () => {
        try {
            const defectFetch = await fetchData("get", `/patrol/${params.id}/defects`, true);
            setDefects(defectFetch);
        } catch (error) {
            console.error("Failed to fetch defects data:", error);
        }
    };

    const calculateProgress = () => {
        if (!patrol) return 0;
        const checkedResults = patrolResults.filter(
            (res) => res.status !== null
        ).length;

        if (totalResults === 0) return 0;
        return (checkedResults / totalResults) * 100;
    };

    const toggleLock = () => setLock((prev) => !prev);

    const mergeResults = (newResults: IPatrolResult[]) => {
        setPatrolResults((prevPatrolResult = []) => {
            const updatedResults = prevPatrolResult.map((res) => {
                const matchingResult = newResults.find(
                    (newRes) =>
                        res.itemId === newRes.itemId && res.zoneId === newRes.zoneId
                );
                return matchingResult ? { ...res, ...matchingResult } : res;
            });

            const newUniqueResults = newResults.filter(
                (newRes) =>
                    !updatedResults.some(
                        (res) =>
                            res.itemId === newRes.itemId && res.zoneId === newRes.zoneId
                    )
            );

            return [...updatedResults, ...newUniqueResults];
        });
    }

    const handleResult = (result: IPatrolResult) => {
        setResults((prevResults) => {
            const existingIndex = prevResults.findIndex(
                (r) => r.itemId === result.itemId && r.zoneId === result.zoneId
            );

            if (existingIndex !== -1) {
                const updatedResults = [...prevResults];
                updatedResults[existingIndex] = result;
                return updatedResults;
            } else {
                return [...prevResults, result];
            }
        });
    };

    const handleStartPatrol = async () => {
        setIsAlertOpen(false)

        if (!patrol) return;
        const patrolId = patrol.id;
        const data = {
            status: patrol?.status,
            checklists: patrol?.patrolChecklists,
        };

        try {
            const startPatrol = await fetchData(
                "put",
                `/patrol/${patrolId}/start`,
                true,
                data
            );
            setPatrol(startPatrol)
            setPatrolResults(startPatrol.results)
            toast({
                variant: "default",
                title: a("PatrolStartTitle"),
                description: a("PatrolStartDescription"),
            });
        } catch (error) {
            console.error("Error starting patrol:", error);
        }
    };

    const handleFinishPatrol = async () => {
        if (!patrol) return;

        const updatedResults = patrolResults.map((result) => {
            const matchedResult = patrolResults.find(
                (res) => res.itemId === result.itemId && res.zoneId === result.zoneId
            );

            if (matchedResult && result.status !== null) {
                return {
                    id: matchedResult.id,
                    status: result.status,
                };
            }

            return null;
        });

        const data = {
            status: patrol.status,
            checklists: patrol.patrolChecklists,
            results: updatedResults,
            startTime: patrol.startTime
        };

        let resultCount = 0;
        for (const checklist of patrol.patrolChecklists) {
            for (const item of checklist.checklist.items) {
                // eslint-disable-next-line @typescript-eslint/no-unused-vars
                for (const zone of item.itemZones) {
                    resultCount++;
                }
            }
        }

        if (data.results.length === resultCount) {
            try {
                localStorage.removeItem(`patrolResults_${patrol.id}`);
                localStorage.removeItem(`otherResults_${patrol.id}`);
                const finishPatrol = await fetchData(
                    "put",
                    `/patrol/${patrol.id}/finish`,
                    true,
                    data
                );
                toast({
                    variant: "default",
                    title: a("PatrolFinishTitle"),
                    description: a("PatrolFinishDescription"),
                });
                setPatrol(finishPatrol)
            } catch (error) {
                console.error("Error finishing patrol:", error);
            }
        }
        // window.location.reload()
    };

    const getUserData = async () => {
        try {
            const userfetch = await fetchData("get", "/user?profile=true&image=true", true);
            setUser(userfetch);
        } catch (error) {
            console.error("Failed to fetch profile data:", error);
        }
    };

    const getPatrolUserData = async () => {
        try {
            const patrolUserFetch = await fetchData("get", `/patrol/${params.id}/user`, true);
            setPatrolUser(patrolUserFetch);
        } catch (error) {
            console.error("Failed to fetch profile data:", error);
        }
    };

    const fetchRealtimeData = (defect: IDefect, actionType: string) => {
        if (actionType === "create") {
            setDefects((prevDefects) => {
                //  defects 
                if (prevDefects && prevDefects.length > 0) {
                    return [...prevDefects, defect]; //  defect 
                } else {
                    return [defect]; //  defect   defect 
                }
            });
        } else {
            setDefects((prevDefects) =>
                prevDefects.map((d) => (d.id === defect.id ? defect : d))
            );
        }
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                await getUserData();
                await getPatrolData();
                await getDefectData()
                await getPatrolUserData()
            } catch (error) {
                console.error("Error loading data: ", error);
            }
        };
        fetchData();
        setMounted(true);
    }, []);

    useEffect(() => {
        if (otherResults) {
            mergeResults(otherResults);
        }
    }, [otherResults]);

    useEffect(() => {
        if (socket && isConnected && patrol?.id && user?.id) {
            socket.emit("join_room", patrol.id);

            const handleResultUpdate = (updatedResults: IPatrolResult[]) => {
                const currentUserResults = updatedResults.filter(
                    (res) => res.inspectorId === user.id
                );

                const otherUserResults = updatedResults.filter(
                    (res) => res.inspectorId !== user.id
                );

                // Avoid unnecessary updates to localStorage
                if (currentUserResults.length > 0) {
                    const savedResults = localStorage.getItem(
                        `patrolResults_${patrol.id}`
                    );
                    const parsedResults: IPatrolResult[] = savedResults
                        ? JSON.parse(savedResults)
                        : [];
                    if (
                        JSON.stringify(parsedResults) !== JSON.stringify(currentUserResults)
                    ) {
                        localStorage.setItem(
                            `patrolResults_${patrol.id}`,
                            JSON.stringify(currentUserResults)
                        );
                    }
                }

                if (otherUserResults.length > 0) {
                    const savedOtherResults = localStorage.getItem(
                        `otherResults_${patrol.id}`
                    );
                    const parsedOtherResults: IPatrolResult[] = savedOtherResults
                        ? JSON.parse(savedOtherResults)
                        : [];
                    if (
                        JSON.stringify(parsedOtherResults) !==
                        JSON.stringify(otherUserResults)
                    ) {
                        localStorage.setItem(
                            `otherResults_${patrol.id}`,
                            JSON.stringify(otherUserResults)
                        );
                    }
                }

                setOtherResults(otherUserResults);
                mergeResults([...results, ...otherUserResults]);
            };

            socket.on("patrol_result_update", handleResultUpdate);

            return () => {
                socket.off("patrol_result_update", handleResultUpdate);
            };
        }
    }, [socket, isConnected, patrol?.id, user?.id, results]);

    const lastEmittedResults = useRef<IPatrolResult[]>([]);

    useEffect(() => {
        if (socket && patrol?.id) {
            // eslint-disable-next-line @typescript-eslint/no-unused-vars
            const handleNewUserJoin = (userId: string) => {

                const savedResults = localStorage.getItem(`patrolResults_${patrol.id}`);
                if (savedResults) {
                    try {
                        const parsedResults: IPatrolResult[] = JSON.parse(savedResults);
                        socket.emit("patrol_result_update", parsedResults, patrol.id);
                    } catch (error) {
                        console.error("Error parsing saved results:", error);
                    }
                }
            };

            socket.on("new_user_joined", handleNewUserJoin);

            return () => {
                socket.off("new_user_joined", handleNewUserJoin);
            };
        }
    }, [socket, patrol?.id]);

    useEffect(() => {
        if (socket && isConnected && results.length > 0 && patrol) {
            const uniqueResults = results.map(
                ({ inspectorId, id, itemId, zoneId, status }) => ({
                    inspectorId,
                    id,
                    itemId,
                    zoneId,
                    status,
                })
            );

            const hasChanged =
                uniqueResults.length !== lastEmittedResults.current.length ||
                uniqueResults.some(
                    (result, index) =>
                        result.id !== lastEmittedResults.current[index]?.id ||
                        result.itemId !== lastEmittedResults.current[index]?.itemId ||
                        result.zoneId !== lastEmittedResults.current[index]?.zoneId ||
                        result.status !== lastEmittedResults.current[index]?.status
                );

            if (hasChanged) {
                socket.emit("patrol_result_update", uniqueResults, patrol.id);
                mergeResults([...results, ...uniqueResults]);
                localStorage.setItem(
                    `patrolResults_${patrol.id}`,
                    JSON.stringify(results)
                );

                lastEmittedResults.current = results;
            }
        }
    }, [results]);

    useEffect(() => {
        const savedResults = localStorage.getItem(`patrolResults_${patrol?.id}`);
        if (savedResults) {
            const parsedResults = JSON.parse(savedResults);
            setResults(parsedResults);
        }
    }, [patrol?.id]);

    return (
        <PatrolContext.Provider
            value={{
                patrol,
                patrolResults,
                results,
                otherResults,
                user,
                lock,
                isAlertOpen,
                mounted,
                canFinish,
                defects,
                countItems,
                countFails,
                countDefects,
                patrolUser,
                isHovered,
                formatDate,
                formatId,
                formatTimeDate,
                formatZone,
                handleMouseEnter,
                handleMouseLeave,
                itemCounts,
                toggleLock,
                calculateProgress,
                handleResult,
                mergeResults,
                handleStartPatrol,
                fetchRealtimeData,
                handleFinishPatrol,
                handleOpenDialog,
                handleCloseDialog,
            }}
        >
            {children}
        </PatrolContext.Provider>
    );
}

export const usePatrol = () => {
    const context = useContext(PatrolContext);
    if (!context) {
        throw new Error('usePatrol must be used within a PatrolProvider');
    }
    return context;
};

