"use client";

import {
  IDefect,
  IDefectCategory,
  IPatrol,
  IPatrolResult,
  IUser,
} from "@/app/type";
import BadgeCustom from "@/components/badge-custom";
import Loading from "@/components/loading";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import {
  HoverCard,
  HoverCardContent,
  HoverCardTrigger,
} from "@/components/ui/hover-card";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
  TooltipProvider,
} from "@/components/ui/tooltip";
import {
  exportData,
  fetchData,
  formatTime,
  getDefectStatusVariant,
  getInitials,
  getItemTypeVariant,
  getPatrolStatusVariant,
} from "@/lib/utils";
import { useLocale, useTranslations } from "next-intl";
import { notFound, useParams, useRouter } from "next/navigation";
import Map from "@/components/map";
import React, { useEffect, useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { DonutGraph } from "@/components/donut-graph";
import { RadialChart } from "@/components/radial-chart";
import { ScrollArea, ScrollBar } from "@/components/ui/scroll-area";
import {
  AlertDialog,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogCancel,
  AlertDialogAction,
  AlertDialogFooter,
} from "@/components/ui/alert-dialog";
import { CardFooter } from "@/components/ui/card";
import { toast } from "@/hooks/use-toast";
import NotFound from "@/components/not-found";
import { UserTooltip } from "@/components/user-tooltip";

export default function page() {
  const a = useTranslations("Alert");
  const z = useTranslations("Zone");
  const t = useTranslations("General");
  const s = useTranslations("Status");
  const d = useTranslations("Dashboard");

  const [patrol, setPatrol] = useState<IPatrol>(null);
  const params = useParams();
  const [mounted, setMounted] = useState(false);
  const [defects, setDefects] = useState<IDefect[]>([]);
  const [defectCategory, setDefectCategory] = useState<IDefectCategory>();
  const router = useRouter();
  const locale = useLocale();

  const [isAlertOpen, setIsAlertOpen] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [hoverState, setHoverState] = useState<{
    [key: string]: { isHovered: boolean; isClicked: boolean };
  }>({});

  const getPatrolData = async () => {
    if (params.id) {
      try {
        const data = await fetchData(
          "get",
          `/patrol/${params.id}?preset=true&result=true`,
          true
        );
        setPatrol(data);
      } catch (error) {
        console.error("Failed to fetch patrol data:", error);
      }
    }
  };

  const getDefectReported = async () => {
    if (params.id) {
      try {
        const data = await fetchData(
          "get",
          `/dashboard/overview/${params.id}`,
          true
        );
        setDefects(data);
      } catch (error) {
        console.error("Failed to fetch defect data:", error);
      }
    }
  };

  const getDefectCategory = async () => {
    if (params.id) {
      try {
        const data = await fetchData(
          "get",
          `/dashboard/defect-category?patrolId=${params.id}`,
          true
        );
        setDefectCategory(data);
      } catch (error) {
        console.error("Failed to fetch defect data:", error);
      }
    }
  };

  const handleOpenDialog = () => {
    setIsAlertOpen(true);
  };

  const handleCloseDialog = () => {
    setIsAlertOpen(false);
  };

  const formatId = (id: number): string => {
    return `P${id.toString().padStart(4, "0")}`;
  };

  const countPatrolResult = (results: IPatrolResult[]) => {
    let total = 0;
    let pass = 0;
    let fail = 0;
    let defect = 0;
    let comment = 0;

    results?.forEach((result) => {
      if (result.status === false) {
        fail++;
      }

      if (result.status === true) {
        pass++;
      }

      if (Array.isArray(result.defects) && result.defects.length > 0) {
        defect++;
      }

      if (Array.isArray(result.comments) && result.comments.length > 0) {
        comment++;
      }

      total++;
    });

    return { total, pass, fail, defect, comment };
  };

  function getZoneAllDefects(): number[] {
    const zoneIds = new Set<number>();

    defects.forEach((item) => {
      if (item.zone && item.zone.id) {
        zoneIds.add(item.zone.id);
      }
    });

    return Array.from(zoneIds);
  }

  const handleMouseEnter = (id: string) => {
    setHoverState((prev) => ({
      ...prev,
      [id]: { ...prev[id], isHovered: true },
    }));
  };

  const handleMouseLeave = (id: string) => {
    setHoverState((prev) => ({
      ...prev,
      [id]: { ...prev[id], isHovered: false },
    }));
  };

  const handleClick = (id: string, e: React.MouseEvent) => {
    e.stopPropagation();
    setHoverState((prev) => ({
      ...prev,
      [id]: { ...prev[id], isClicked: !prev[id]?.isClicked },
    }));
  };

  useEffect(() => {
    // 
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    //  event listener  resize
    window.addEventListener("resize", handleResize);

    //  event listener 
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await getPatrolData();
        await getDefectReported();
        await getDefectCategory();
      } catch (error) {
        console.error("Error loading data: ", error);
      } finally {
        setMounted(true);
      }
    };
    fetchData();
  }, []);

  if (!mounted) {
    return <Loading />;
  }

  if (mounted && !patrol?.id) {
    return notFound();
  }

  const inspectors = patrol?.patrolChecklists
    .map((checklist) => checklist.inspector) //  inspector  patrolChecklists
    .filter(
      (inspector, index, self) =>
        self.findIndex((i) => i.id === inspector.id) === index //  inspector 
    );

  return (
    <div className="flex flex-col gap-4">
      {/* //first block */}
      <div className="flex flex-col gap-4 bg-card px-6 py-4 rounded-md">
        <div className="flex flex-row justify-between  rounded-md">
          <p className="text-muted-foreground text-lg font-semibold text-">
            {formatTime(patrol.date, locale, false)}
          </p>
          <div className="flex flex-row gap-2">
            <Button
              size="lg"
              variant="secondary"
              className="flex flex-row items-center"
              onClick={() =>
                router.push(`/${locale}/admin/dashboard/overview/`)
              }
            >
              <p>{t("Back")}</p>
            </Button>

            <AlertDialog open={isAlertOpen}>
              <AlertDialogTrigger asChild>
                <Button
                  size="lg"
                  variant="primary"
                  className="flex flex-row items-center"
                  onClick={() => handleOpenDialog()}
                >
                  <span className="material-symbols-outlined text-card w-[22px] h-[22px]">
                    ios_share
                  </span>
                  <p>{t("Export")}</p>
                </Button>
              </AlertDialogTrigger>

              <AlertDialogContent className="w-[400px] h-fit px-6 py-4 ">
                <AlertDialogHeader className="flex flex-col gap-2">
                  <div className="flex flex-col gap-1">
                    <p className="text-lg font-semibold text-muted-foreground">
                      {formatTime(patrol.date, locale, false)}
                    </p>
                    <AlertDialogTitle>
                      <p className="text-2xl font-semibold">
                        {patrol.preset.title}
                      </p>
                    </AlertDialogTitle>
                  </div>

                  <AlertDialogDescription className="flex flex-col gap-2">
                    <div className="flex flex-row gap-1">
                      <span className="material-symbols-outlined">
                        description
                      </span>
                      <p className="text-muted-foreground text-xl font-semibold">
                        {formatId(patrol.id)}
                      </p>
                    </div>

                    <div>
                      <HoverCard
                        open={
                          hoverState["export"]?.isClicked ||
                          hoverState["export"]?.isHovered
                        }
                      >
                        <HoverCardTrigger
                          onClick={(e) => {
                            e.stopPropagation();
                            handleClick("export", e);
                          }}
                          onMouseEnter={() => handleMouseEnter("export")}
                          onMouseLeave={() => handleMouseLeave("export")}
                          asChild
                        >
                          <div className="flex text-muted-foreground items-center cursor-pointer">
                            <span className="material-symbols-outlined me-1">
                              person_search
                            </span>
                            {inspectors.length > 0 && (
                              <div className="flex items-center me-1 truncate max-w-[190px]">
                                <p className="text-xl me-2.5 truncate">
                                  {inspectors[0].profile.name}
                                </p>
                              </div>
                            )}
                            {inspectors.map((inspector, idx) => {
                              return (
                                <Avatar
                                  key={idx}
                                  className="custom-shadow ms-[-10px]"
                                >
                                  <AvatarImage
                                    src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                                  />
                                  <AvatarFallback id={inspector.id.toString()}>
                                    {getInitials(inspector.profile.name)}
                                  </AvatarFallback>
                                </Avatar>
                              );
                            })}

                            {inspectors.length > 5 && (
                              <Avatar className="custom-shadow flex items-center justify-center ms-[-10px]">
                                <AvatarImage src="" />
                                <span className="absolute text-card-foreground text-[16px] font-semibold">
                                  +{inspectors.length - 5}
                                </span>
                                <AvatarFallback id={"0"}></AvatarFallback>
                              </Avatar>
                            )}
                          </div>
                        </HoverCardTrigger>
                        <HoverCardContent
                          side="bottom"
                          align="start"
                          className="flex flex-col w-[300px] border-none gap-4 px-6 py-4 custom-shadow"
                        >
                          <div className="flex items-center justify-center gap-1">
                            <span className="material-symbols-outlined">
                              person_search
                            </span>
                            <p className="text-lg font-semibold">
                              {t("InspectorList")}
                            </p>
                          </div>
                          {inspectors.map((inspector, idx) => {
                            return (
                              <div
                                key={idx}
                                className="flex items-center cursor-pointer w-full py-2 gap-1 border-b-2 border-secondary"
                              >
                                <UserTooltip user={inspector}>
                                  <Avatar className="custom-shadow ms-[-10px] me-2.5">
                                    <AvatarImage
                                      src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                                    />
                                    <AvatarFallback id={inspector.id.toString()}>
                                      {getInitials(inspector.profile.name)}
                                    </AvatarFallback>
                                  </Avatar>
                                </UserTooltip>
                                <p className="text-lg truncate">{inspector.profile.name}</p>
                              </div>
                            );
                          })}
                          <div className="flex items-center justify-between w-full text-muted-foreground">
                            <p className="text-lg font-semibold">
                              {t("Total")}
                            </p>
                            <p className="text-lg font-semibold">
                              {inspectors.length}
                            </p>
                          </div>
                        </HoverCardContent>
                      </HoverCard>
                    </div>

                    <CardFooter className="p-0 gap-0">
                      <div className="flex gap-2 items-center w-full">
                        <div className="flex gap-1 text-primary items-center">
                          <span className="material-symbols-outlined">
                            checklist
                          </span>
                          <p className="text-xl font-semibold">
                            {countPatrolResult(patrol.results).total}
                          </p>
                        </div>
                        <div className="flex gap-1 text-orange items-center">
                          <span className="material-symbols-outlined">
                            close
                          </span>
                          <p className="text-xl font-semibold">
                            {countPatrolResult(patrol.results).fail}
                          </p>
                        </div>
                        <div className="flex gap-1 text-destructive items-center">
                          <span className="material-symbols-outlined">
                            error
                          </span>
                          <p className="text-xl font-semibold">
                            {countPatrolResult(patrol.results).defect}
                          </p>
                        </div>
                      </div>
                    </CardFooter>
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel onClick={() => handleCloseDialog()}>
                    {t("Cancel")}
                  </AlertDialogCancel>
                  <AlertDialogAction
                    className="bg-primary text-primary-foreground shadow hover:bg-primary/90 h-10 rounded-md px-4 text-lg font-bold"
                    onClick={() => {
                      patrol.status !== "completed"
                        ? (toast({
                          variant: "error",
                          title: a("ExportPatrolNoData"),
                          description: a(
                            "ExportPatrolStatusNotCompleteDescription"
                          ),
                        }),
                          handleCloseDialog())
                        : (exportData(patrol, patrol.results),
                          toast({
                            variant: "success",
                            title: a("ExportReportPatrolTitle"),
                            description: a("ExportReportPatrolDescription"),
                          }));
                    }}
                  >
                    <span className="material-symbols-outlined text-2xl me-2">
                      ios_share
                    </span>
                    {t("Export")}
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </div>
        </div>

        <div className="text-2xl font-bold">
          <p>{patrol?.preset.title}</p>
        </div>

        <div className="flex flex-col gap-2">
          <div className="flex flex-row justify-between">
            <div className="flex flex-row gap-1 justify-start">
              <span className="material-symbols-outlined text-muted-foreground w-[22px] h-[22px]">
                description
              </span>
              <p className="text-lg text-muted-foreground">
                {formatId(patrol.id)}
              </p>
            </div>

            <div>
              <BadgeCustom
                iconName={getPatrolStatusVariant(patrol.status).iconName}
                showIcon={true}
                variant={getPatrolStatusVariant(patrol.status).variant}
              >
                {s(patrol.status)}
              </BadgeCustom>
            </div>
          </div>

          <div className="flex flex-row justify-between">
            <div className="flex flex-col gap-2 p-0">
              <HoverCard
                open={
                  hoverState["overview"]?.isClicked ||
                  hoverState["overview"]?.isHovered
                }
              >
                <HoverCardTrigger
                  onClick={(e) => {
                    e.stopPropagation();
                    handleClick("overview", e);
                  }}
                  onMouseEnter={() => handleMouseEnter("overview")}
                  onMouseLeave={() => handleMouseLeave("overview")}
                  asChild
                >
                  <div className="flex text-muted-foreground cursor-pointer items-center">
                    <span className="material-symbols-outlined me-1">
                      person_search
                    </span>
                    {inspectors.length > 0 && (
                      <div className="flex items-center me-1 truncate max-w-[190px]">
                        <p className="text-xl me-2.5 truncate">
                          {inspectors[0].profile.name}
                        </p>
                      </div>
                    )}
                    {inspectors.map((inspector, idx) => {
                      return (
                        <Avatar key={idx} className="custom-shadow ms-[-10px]">
                          <AvatarImage
                            src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                          />
                          <AvatarFallback id={inspector.id.toString()}>
                            {getInitials(inspector.profile.name)}
                          </AvatarFallback>
                        </Avatar>
                      );
                    })}

                    {inspectors.length > 5 && (
                      <Avatar className="custom-shadow flex items-center justify-center ms-[-10px]">
                        <AvatarImage src="" />
                        <span className="absolute text-card-foreground text-base font-semibold">
                          +{inspectors.length - 5}
                        </span>
                        <AvatarFallback id={"0"}></AvatarFallback>
                      </Avatar>
                    )}
                  </div>
                </HoverCardTrigger>
                <HoverCardContent
                  side="bottom"
                  align="start"
                  className="flex flex-col w-[300px] border-none gap-4 px-6 py-4 custom-shadow"
                >
                  <div className="flex items-center justify-center gap-1">
                    <span className="material-symbols-outlined">
                      person_search
                    </span>
                    <p className="text-lg font-semibold">
                      {t("InspectorList")}
                    </p>
                  </div>
                  {inspectors.map((inspector, idx) => {
                    return (
                      <div
                        key={idx}
                        className="flex items-center cursor-pointer w-full py-2 gap-1 border-b-2 border-secondary"
                      >
                        <UserTooltip user={inspector}>
                          <Avatar className="custom-shadow ms-[-10px] me-2.5">
                            <AvatarImage
                              src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                            />
                            <AvatarFallback id={inspector.id.toString()}>
                              {getInitials(inspector.profile.name)}
                            </AvatarFallback>
                          </Avatar>
                        </UserTooltip>
                        <p className="text-lg truncate">{inspector.profile.name}</p>
                      </div>
                    );
                  })}
                  <div className="flex items-center justify-between w-full text-muted-foreground">
                    <p className="text-lg font-semibold">
                      {t("Total")}
                    </p>
                    <p className="text-lg font-semibold">
                      {inspectors.length}
                    </p>
                  </div>
                </HoverCardContent>
              </HoverCard>
            </div>

            <div>
              <div className="flex flex-row gap-2">
                <div className="flex gap-1 text-primary items-center">
                  <span className="material-symbols-outlined">checklist</span>
                  <p className="text-xl font-semibold">
                    {countPatrolResult(patrol.results).total}
                  </p>
                </div>
                <div className="flex gap-1 text-orange items-center">
                  <span className="material-symbols-outlined">close</span>
                  <p className="text-xl font-semibold">
                    {countPatrolResult(patrol.results).fail}
                  </p>
                </div>
                <div className="flex gap-1 text-destructive items-center">
                  <span className="material-symbols-outlined">error</span>
                  <p className="text-xl font-semibold">
                    {countPatrolResult(patrol.results).defect}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <ScrollArea
        className="h-full w-full rounded-md flex-1 
            [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-400px)]"
      >
        <div className="flex flex-col gap-4">
          {/* second block */}
          <div className="grid xl:grid-cols-4 gap-4 sm:grid-cols-2 ">
            <div className="flex flex-col gap-4 bg-card px-6 py-4 rounded-md">
              <div className="flex flex-row justify-between">
                <p className="text-xl">{d("TotalPass")}</p>
                <div className="flex items-center justify-center bg-green/20 rounded-full h-9 w-9">
                  <span className="flex justify-center items-center material-symbols-outlined text-green w-[22px] h-[22px]">
                    check
                  </span>
                </div>
              </div>
              <p className="text-2xl font-bold">
                {countPatrolResult(patrol.results).pass}
              </p>
            </div>

            <div className="flex flex-col gap-4 bg-card px-6 py-4 rounded-md">
              <div className="flex flex-row justify-between">
                <p className="text-xl">{d("TotalFails")}</p>
                <div className="flex items-center justify-center bg-destructive/20 rounded-full h-9 w-9">
                  <span className="flex justify-center items-center material-symbols-outlined text-destructive w-[22px] h-[22px]">
                    close
                  </span>
                </div>
              </div>
              <p className="text-2xl font-bold">
                {countPatrolResult(patrol.results).fail}
              </p>
            </div>

            <div className="flex flex-col gap-4 bg-card px-6 py-4 rounded-md">
              <div className="flex flex-row justify-between">
                <p className="text-xl">{d("TotalReports")}</p>
                <div className="flex items-center justify-center bg-orange/20 rounded-full h-9 w-9">
                  <span className="flex justify-center items-center material-symbols-outlined text-orange w-[22px] h-[22px]">
                    campaign
                  </span>
                </div>
              </div>
              <p className="text-2xl font-bold">
                {countPatrolResult(patrol.results).defect}
              </p>
            </div>

            <div className="flex flex-col gap-4 bg-card px-6 py-4 rounded-md">
              <div className="flex flex-row justify-between">
                <p className="text-xl">{d("TotalComments")}</p>
                <div className="flex items-center justify-center bg-yellow/20 rounded-full h-9 w-9">
                  <span className="flex justify-center items-center material-symbols-outlined text-yellow w-[22px] h-[22px]">
                    chat
                  </span>
                </div>
              </div>
              <p className="text-2xl font-bold">
                {countPatrolResult(patrol.results).comment}
              </p>
            </div>
          </div>

          {/* third block */}
          <div className="flex flex-col rounded-md px-6 py-4 bg-card gap-4">
            <p className="text-2xl font-bold">{d("DefectReported")}</p>

            <div className="rounded-md map-container cursor-default user-select-none ">
              <Map disable={true} initialSelectedZones={getZoneAllDefects()} />
            </div>

            <ScrollArea className="rounded-md sm:w-[654px] lg:w-full whitespace-nowrap">
              <Table className="overflow-hidden sm:w-max lg:w-full">
                <TableHeader>
                  <TableRow className="grid grid-cols-12 w-full">
                    <TableHead className="sm:col-span-3 sm:w-[200px] lg:col-span-2">
                      {t("Name")}
                    </TableHead>
                    <TableHead className="sm:col-span-1 lg:col-span-2">
                      {t("Type")}
                    </TableHead>
                    <TableHead className="sm:col-span-2 lg:col-span-2">
                      {t("Reporter")}
                    </TableHead>
                    <TableHead className="sm:col-span-2 lg:col-span-2">
                      {t("Timestamp")}
                    </TableHead>
                    <TableHead className="sm:col-span-2 lg:col-span-2">
                      {t("Zone")}
                    </TableHead>
                    <TableHead className="sm:col-span-2 lg:col-span-2">
                      {t("Status")}
                    </TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody className="block overflow-hidden">
                  <ScrollArea className="h-[300px]">
                    {defects?.length === 0 ? (
                      <tr className="flex sm:w-[654px] sm:h-[388px] lg:w-full lg:h-full">
                        <td colSpan={5} className="w-full text-center py-0">
                          <NotFound
                            icon="campaign"
                            title="NoDefectsReported"
                            description="NoDefectsDescription"
                          />
                        </td>
                      </tr>
                    ) : (
                      defects.map((defect, index) => (
                        <TableRow
                          key={index}
                          className="grid grid-cols-12 w-full items-center"
                        >
                          <TableCell className="sm:text-sm lg:text-base sm:col-span-3 sm:w-[200px] lg:col-span-2 flex items-center min-w-0">
                            <TooltipProvider>
                              <Tooltip>
                                <TooltipTrigger asChild>
                                  <span className="truncate">
                                    {defect.name}
                                  </span>
                                </TooltipTrigger>
                                <TooltipContent
                                  side="bottom"
                                  className="ml-[129px]"
                                >
                                  <p className="max-w-[200px] break-words">
                                    {defect.name}
                                  </p>
                                </TooltipContent>
                              </Tooltip>
                            </TooltipProvider>
                          </TableCell>
                          <TableCell className="sm:text-sm lg:text-base sm:col-span-1 lg:col-span-2 flex items-center">
                            <BadgeCustom
                              iconName={
                                getItemTypeVariant(defect.type).iconName
                              }
                              showIcon={true}
                              shape="square"
                              variant={getItemTypeVariant(defect.type).variant}
                              hideText={windowWidth > 1024 ? false : true}
                            >
                              {s(defect.type)}
                            </BadgeCustom>
                          </TableCell>
                          <TableCell className="sm:col-span-2 lg:col-span-2">
                            <UserTooltip user={defect.user}>
                              <div className="flex items-center gap-2">
                                <div className="flex items-center gap-1">
                                  <Avatar className="custom-shadow h-[35px] w-[35px]">
                                    <AvatarImage
                                      src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${defect.user.profile.image?.path}`}
                                    />
                                    <AvatarFallback
                                      id={defect.user.id.toString()}
                                    >
                                      {getInitials(defect.user.profile.name)}
                                    </AvatarFallback>
                                  </Avatar>

                                  <p className="text-card-foreground text-lg xl:block truncate">
                                    {defect.user.profile.name}
                                  </p>
                                </div>
                              </div>
                            </UserTooltip>
                          </TableCell>
                          <TableCell className="sm:col-span-2 lg:col-span-2 ps-4">
                            <p>{formatTime(defect.startTime, locale)}</p>
                          </TableCell>
                          <TableCell className="sm:col-span-2 lg:col-span-2">
                            <p>{z(defect.zone.name)}</p>
                          </TableCell>
                          <TableCell className="sm:text-sm lg:text-base sm:col-span-2 lg:col-span-2 flex items-center">
                            <BadgeCustom
                              iconName={
                                getDefectStatusVariant(defect.status).iconName
                              }
                              showIcon={true}
                              showTime={false}
                              variant={
                                getDefectStatusVariant(defect.status).variant
                              }
                              hideText={windowWidth > 1024 ? false : true}
                            >
                              {s(defect.status)}
                            </BadgeCustom>
                          </TableCell>
                        </TableRow>
                      ))
                    )}
                    <ScrollBar orientation="vertical" />
                  </ScrollArea>
                </TableBody>
              </Table>
              <ScrollBar orientation="horizontal" />
            </ScrollArea>
          </div>
          {/* forth block */}
          <div className="flex xl:flex-row gap-4 justify-between sm:flex-col">
            {/* Patrol Duration */}
            <div className="flex flex-col xl:flex-row gap-4 justify-between w-full">
              <div className="flex flex-col py-4 px-6 w-full rounded-md custom-shadow bg-card">
                <h1 className="text-2xl font-semibold text-card-foreground">
                  {d("PatrolDuration")}
                </h1>
                <div className="flex flex-col">
                  <RadialChart duration={patrol.duration} />
                </div>
              </div>
            </div>

            {/* Defect Category */}
            <div className="flex flex-col xl:flex-row gap-4 justify-between w-full">
              <div className="flex flex-col py-4 px-6 w-full rounded-md custom-shadow bg-card">
                <h1 className="text-2xl font-semibold text-card-foreground">
                  {d("DefectCategory")}
                </h1>
                <DonutGraph
                  key={Date.now()}
                  chartData={defectCategory.chartData}
                  trend={null}
                />
              </div>
            </div>
          </div>
        </div>
      </ScrollArea>
    </div>
  );
}

'use client'

import React, { useCallback, useMemo, useRef } from 'react'
import { useEffect, useState } from "react";
import Textfield from "@/components/textfield";
import { Button } from "@/components/ui/button";
import { ScrollArea, ScrollBar } from "@/components/ui/scroll-area";

import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useLocale, useTranslations } from "next-intl";
import { countPatrolResult, exportData, fetchData, formatTime, getPatrolStatusVariant } from "@/lib/utils";
import {
  DatePickerWithRange,
} from "@/components/date-picker";
import BadgeCustom from "@/components/badge-custom";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectLabel,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  IPatrol,
  IPreset,
  IPatrolResult,
} from "@/app/type";
import { IFilterPatrol } from "@/app/type";
import { sortData } from "@/lib/utils";
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { DateRange, DateRange as DayPickerDateRange } from 'react-day-picker';
import Loading from "@/components/loading";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";
import NotFound from "@/components/not-found";
import { Tooltip, TooltipContent, TooltipTrigger, TooltipProvider } from "@/components/ui/tooltip";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Checkbox } from '@/components/ui/checkbox';
import { Progress } from '@/components/ui/progress';
import { useSocket } from '@/components/socket-provider';
import { useRouter } from 'next/navigation';

export default function Page() {
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const s = useTranslations("Status");

  const [socketData, setSocketData] = useState<IPatrolResult[]>([]);
  const [allPatrols, setAllPatrols] = useState<IPatrol[]>([]);
  const [allPresets, setAllPresets] = useState<IPreset[]>();
  const [selectedPatrolId, setSelectedPatrolId] = useState<number | null>(null);

  const [dateError, setDateError] = useState<string | null>(null);
  const [mounted, setMounted] = useState<boolean>(false);
  const [selectedDate, setSelectedDate] = useState<string>();
  const [checkedItems, setCheckedItems] = useState<Record<string, boolean>>({});

  const [isSortOpen, setIsSortOpen] = useState(false);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isAlertOpen, setIsAlertOpen] = useState(false);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const { socket, isConnected } = useSocket();
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const router = useRouter();
  const locale = useLocale();

  const mergedPatrols = useMemo(() => {
    if (!socketData || socketData.length === 0) {
      return allPatrols;
    }

    return allPatrols.map((patrol) => {
      if (!patrol.results || patrol.results.length === 0) {
        return patrol;
      }

      const updatedResults = patrol.results
        .map((existingResult) => {
          const matchingSocketResult = socketData.find((result) => result.id === existingResult.id);
          return matchingSocketResult ? { ...existingResult, ...matchingSocketResult } : existingResult;
        })
        .filter((result) => result.id); //  id 

      return { ...patrol, results: updatedResults };
    });
  }, [allPatrols, socketData]);

  //  useEffect  allPatrols  mergedPatrols 
  useEffect(() => {
    if (JSON.stringify(allPatrols) !== JSON.stringify(mergedPatrols)) {
      setAllPatrols(mergedPatrols);
    }
  }, [mergedPatrols]);

  const getPatrolData = async () => {
    try {
      const queryString = buildQueryString(filter, searchTerm);
      const patrols = await fetchData("get", `/patrols?${queryString}`, true);
      setAllPatrols(patrols);
    } catch (error) {
      console.error("Failed to fetch patrol data:", error);
    }
  };

  const getPresetData = async () => {
    try {
      const preset = await fetchData("get", "/presets?latest=true", true);
      setAllPresets(preset);
    } catch (error) {
      console.error("Failed to fetch patrol data:", error);
    }
  };

  const joinedRoomsRef = useRef(new Set());

  const handleOpenAlert = () => {
    setIsAlertOpen(true);
  };

  const handleCloseAlert = () => {
    setIsAlertOpen(false)
  }

  const handleOpenDialog = (patrolId: number) => {
    setSelectedPatrolId(patrolId);
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setSelectedPatrolId(null)
    setIsDialogOpen(false);
  };

  const handleRemovePatrol = (patrolId: number) => {
    removePatrol(patrolId)
  };

  const handleDetail = (patrolId: number) => {
    router.push(`/${locale}/admin/dashboard/overview/${patrolId}`)
  }

  const removePatrol = async (patrolId: number) => {
    try {
      await fetchData("delete", `/patrol/${patrolId}`, true);
      socket.emit("delete_patrol", patrolId);
      toast({
        variant: "success",
        title: a("PatrolRemoveSuccessTitle"),
        description: a("PatrolRemoveSuccessDescription"),
      });
      getPatrolData()
    } catch (error) {
      console.error(error)
    }
  }

  // search sort filter
  const handleSortChange = (type: string, value: string) => {
    setSort((prevSort) => ({
      ...prevSort,
      [type]: value,
    }));
  };

  const initialFilter = {
    presetTitle: "All",
    patrolStatuses: ["pending", "on_going", "scheduled"],
    dateRange: { start: undefined, end: undefined },
  };

  const getStoredFilter = () => {
    if (typeof window !== 'undefined') {
      const storedFilter = localStorage.getItem('filter');
      if (storedFilter) {
        return JSON.parse(storedFilter);
      }
    }
    return initialFilter;
  };

  const [filter, setFilter] = useState<IFilterPatrol | null>(getStoredFilter())

  const [sort, setSort] = useState<{ by: string; order: string }>({
    by: "Doc No.",
    order: "Ascending",
  });

  const toggleStatusFilter = (status: string, checked: boolean) => {
    if (checked) {
      setFilter((prev) => {
        if (prev) {
          return {
            ...prev,
            patrolStatuses: [...prev.patrolStatuses, status],
          };
        } else {
          return {
            presetTitle: "All",
            patrolStatuses: [],
            dateRange: { start: undefined, end: undefined }
          }
        }
      });
    }
    else {
      setFilter((prev) => {
        if (prev) {
          return {
            ...prev,
            patrolStatuses: prev.patrolStatuses.filter((s) => s !== status),
          };
        }
        return prev;
      });
    }
  };

  const applyFilter = () => {
    const fetchData = async () => {
      await getPatrolData()
    };
    fetchData();
  };

  const resetFilter = () => {
    setFilter(initialFilter)
  };

  const handleDateSelect = (dateRange: DateRange) => {
    const startDate = dateRange.from ?? null;
    const endDate = dateRange.to
      ? new Date(new Date(dateRange.to).setHours(23, 59, 59, 999))
      : null;
    setFilter({
      presetTitle: filter?.presetTitle || null,
      patrolStatuses: filter?.patrolStatuses || [],
      dateRange: {
        start: startDate || undefined,
        end: endDate || undefined
      }
    });
  };

  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event: any) => {
    setSearchTerm(event.target.value);
  };

  const buildQueryString = (filter: IFilterPatrol | null, searchTerm: string) => {
    const params: Record<string, string | undefined> = {};

    //  search term 
    if (searchTerm) params.search = searchTerm;

    //  preset filter  "All"
    if (filter?.presetTitle && filter.presetTitle !== "All") {
      params.preset = filter.presetTitle;
    }

    //  status filter
    if (filter?.patrolStatuses?.length) {
      params.status = filter.patrolStatuses.join(",");
    }

    //  startDate
    if (filter?.dateRange?.start) {
      params.startDate = filter?.dateRange.start.toISOString();
    }

    //  endDate 
    if (filter?.dateRange?.end) {
      params.endDate = filter?.dateRange?.end.toISOString();
    }

    return new URLSearchParams(params).toString();
  };

  const formatId = (id: number): string => {
    return `P${id.toString().padStart(4, '0')}`;
  };

  const handleCheckboxChange = (patrolId: number, checked: boolean) => {
    setCheckedItems((prev) => ({
      ...prev,
      [patrolId]: checked,
    }));
  };

  const handleSelectAllCheckbox = (checked: boolean) => {
    const newCheckedItems = allPatrols.reduce((acc, patrol) => {
      acc[patrol.id] = checked;
      return acc;
    }, {} as Record<string, boolean>);
    setCheckedItems(newCheckedItems);
  };

  const exportPatrol = (allPatrols: IPatrol[], checkedItems: Record<string, boolean>) => {
    const selectedPatrols = allPatrols.filter((p) => checkedItems[p.id]);

    if (allPatrols.length === 0 || selectedPatrols.length === 0) {
      toast({
        variant: "error",
        title: a("ExportPatrolNoData"),
        description: a("ExportPatrolNoDataDescription"),
      });
      return;
    }

    const hasIncompletePatrol = selectedPatrols.some((patrol) => patrol.status !== "completed");

    if (hasIncompletePatrol) {
      toast({
        variant: "error",
        title: a("ExportPatrolNoData"),
        description: a("ExportPatrolStatusNotCompleteDescription"),
      });
      return;
    }

    selectedPatrols.forEach((patrol) => {
      const results = patrol.results;

      try {
        exportData(patrol, results);
        toast({
          variant: "success",
          title: a("ExportReportPatrolTitle"),
          description: a("ExportReportPatrolDescription"),
        });
      } catch (error) {
        console.error(`Export failed for patrol ID: ${patrol.id}`, error);
      }
    });
  };

  const calculateProgress = (patrolResults: IPatrolResult[]) => {
    if (!patrolResults) return 0;
    const checkedResults = patrolResults.filter(
      (res) => res.status !== null
    ).length;
    const totalResults = patrolResults.length;

    if (totalResults === 0) return 0;
    return (checkedResults / totalResults) * 100;
  };

  useEffect(() => {
    const fetchData = async () => {
      await getPresetData();
      await getPatrolData();
    };
    fetchData();
    // 
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    //  event listener  resize
    window.addEventListener('resize', handleResize);

    //  event listener 
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      await getPatrolData();
    };
    fetchData();
  }, [searchTerm]);

  useEffect(() => {
    const initializeSocketListeners = () => {
      //  socket
      const handleInitialData = (initialResults: IPatrolResult[]) => {
        if (initialResults.length <= 0) {
          return;
        }
        //  socketData  socket
        setSocketData(prevData => {
          // 
          const updatedResults = initialResults.map((incomingResult) => {
            //  incomingResult.id  prevData 
            const existingIndex = prevData.findIndex(result => result.id === incomingResult.id);

            if (existingIndex !== -1) {
              //  id ,  existing result
              return {
                ...prevData[existingIndex],  // 
                ...incomingResult,            //  initialResults
              };
            }

            //  id , 
            return incomingResult;
          });

          //  prevData
          const newResults = initialResults.filter(result =>
            !prevData.some(existingResult => existingResult.id === result.id)
          );

          // 
          return [...updatedResults, ...newResults];
        });
      };

      // 
      const handleResultUpdate = (incomingResult: IPatrolResult) => {
        setSocketData(prevData => {
          //  incomingResult.id  prevData 
          const updatedResults = prevData.map(existingResult => {
            if (existingResult.id === incomingResult.id) {
              return {
                ...existingResult,   // 
                ...incomingResult,   //  incomingResult
              };
            }
            return existingResult;
          });

          //  incomingResult  prevData, 
          if (!prevData.some(existingResult => existingResult.id === incomingResult.id)) {
            return [...updatedResults, incomingResult];
          }

          return updatedResults;
        });
      };

      //  patrol 
      const handlePatrolStarted = async (data: { patrolId: string; patrolData: IPatrol }) => {
        if (!joinedRoomsRef.current.has(data.patrolId)) {
          socket.emit("join_patrol", data.patrolId);
          joinedRoomsRef.current.add(data.patrolId);
        }
        await getPatrolData();
      };

      //  patrol 
      const handlePatrolFinished = async (data: { patrolId: string; patrolData: IPatrol }) => {
        await getPatrolData();
      };

      //  Patrol 
      const handleNewPatrol = (newPatrol) => {
        if (!joinedRoomsRef.current.has(newPatrol.id)) {
          socket.emit("join_patrol", newPatrol.id);
          joinedRoomsRef.current.add(newPatrol.id);
        }

        setAllPatrols((prev) => {
          const existingIndex = prev.findIndex((patrol) => patrol.id === newPatrol.id);

          if (existingIndex !== -1) {
            const updatedPatrols = [...prev];
            updatedPatrols[existingIndex] = { ...prev[existingIndex], ...newPatrol };
            return updatedPatrols;
          } else {
            return [...prev, newPatrol];
          }
        });


      };

      //  Patrol  
      const handlePatrolDeleted = (patrolId) => {
        setAllPatrols((prevPatrols) => prevPatrols.filter((patrol) => patrol.id !== patrolId));
        if (!joinedRoomsRef.current.has(patrolId)) {
          socket.emit("join_patrol", patrolId);
          joinedRoomsRef.current.add(patrolId);
        }
      };
      socket.on("initial_patrol_data", handleInitialData);
      socket.on("patrol_result_update", handleResultUpdate);
      socket.on("patrol_started", handlePatrolStarted);
      socket.on("patrol_finished", handlePatrolFinished);
      socket.on("patrol_created", handleNewPatrol);
      socket.on("patrol_deleted", handlePatrolDeleted);
      setMounted(true);
      return () => {
        socket.off("initial_patrol_data", handleInitialData);
        socket.off("patrol_result_update", handleResultUpdate);
        socket.off("patrol_started", handlePatrolStarted);
        socket.off("patrol_finished", handlePatrolFinished);
        socket.off("patrol_created", handleNewPatrol);
        socket.off("patrol_deleted", handlePatrolDeleted);

      };
    };

    initializeSocketListeners();
  }, [socket, isConnected]);

  useEffect(() => {
    allPatrols.forEach((patrol) => {
      if (!joinedRoomsRef.current.has(patrol.id) && patrol.status === 'on_going' || patrol.status === 'scheduled') {
        socket.emit("join_patrol", patrol.id);
        joinedRoomsRef.current.add(patrol.id);
      }
    });
  }, [allPatrols])

  useEffect(() => {
    localStorage.setItem('filter', JSON.stringify(filter));
  }, [filter]);

  useEffect(() => {
    const sortedData = sortData(allPatrols, sort);
    if (JSON.stringify(sortedData) !== JSON.stringify(allPatrols)) {
      setAllPatrols(sortedData);
    }
  }, [sort]);

  useEffect(() => {
    if (selectedDate !== null || selectedDate !== undefined) {
      setDateError(null)
    }
  }, [selectedDate])

  if (!mounted) {
    return <Loading />
  }

  return (
    <div className='flex flex-col gap-4'>
      {/* Patrol List */}
      <div className='flex flex-row justify-between items-center'>
        <p className='text-2xl font-bold'>{t("PatrolList")}</p>
        <Button size='lg' variant='primary' className='flex flex-row items-center' onClick={() => handleOpenAlert()}>
          <span className="material-symbols-outlined text-card w-[22px] h-[22px]">
            ios_share
          </span>
          <p>{t("Export")}</p>
        </Button>
      </div>

      {/* Search Sort Filter */}
      <div className="flex items-center gap-2">
        <Textfield
          iconName="search"
          showIcon={true}
          placeholder={t("Search")}
          onChange={handleSearch}
        />

        <DropdownMenu onOpenChange={(open) => setIsSortOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
    ${isSortOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">swap_vert</span>
            <div className="text-lg">{t('Sort')}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="p-2 gap-2">
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">{t('SortBy')}</DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.by}
              onValueChange={(value) => handleSortChange('by', value)}
            >
              <DropdownMenuRadioItem value="Doc No." className="text-base" onSelect={(e) => e.preventDefault()}>
                {t('DocNo')}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem value="Date" className="text-base" onSelect={(e) => e.preventDefault()}>
                {t('Date')}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>

            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">{t('Order')}</DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.order}
              onValueChange={(value) => handleSortChange('order', value)}
            >
              <DropdownMenuRadioItem value="Ascending" className="text-base" onSelect={(e) => e.preventDefault()}>
                {t('Ascending')}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem value="Descending" className="text-base" onSelect={(e) => e.preventDefault()}>
                {t('Descending')}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu onOpenChange={(open) => setIsFilterOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
    ${isFilterOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">page_info</span>
            <div className="text-lg">{t('Filter')}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="flex flex-col justify-center gap-2 p-2 z-50"
            align="end"
          >
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">{t('Date')}</DropdownMenuLabel>
              <DatePickerWithRange
                startDate={filter?.dateRange.start}
                endDate={filter?.dateRange.end}
                onSelect={handleDateSelect}
                className="my-date-picker"
              />
            </div>
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">{t('Status')}</DropdownMenuLabel>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("pending")}
                onCheckedChange={(checked) => toggleStatusFilter("pending", checked)}
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="blue"
                  showIcon={true}
                  iconName="hourglass_top"
                  children="Pending"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("scheduled")}
                onCheckedChange={(checked) => toggleStatusFilter("scheduled", checked)}
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="yellow"
                  showIcon={true}
                  iconName="event_available"
                  children="Scheduled"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("on_going")}
                onCheckedChange={(checked) => toggleStatusFilter("on_going", checked)}
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="purple"
                  showIcon={true}
                  iconName="cached"
                  children="On Going"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("completed")}
                onCheckedChange={(checked) => toggleStatusFilter("completed", checked)}
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="green"
                  showIcon={true}
                  iconName="check"
                  children="Complete"
                />
              </DropdownMenuCheckboxItem>

            </div>
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">{t('Preset')}</DropdownMenuLabel>
              <Select
                value={filter?.presetTitle || 'All'}
                onValueChange={(value) =>
                  setFilter({
                    presetTitle: value,
                    patrolStatuses: filter?.patrolStatuses || [],
                    dateRange: { start: filter?.dateRange.start, end: filter?.dateRange.end }
                  })
                }
              >
                <SelectTrigger className="">
                  <SelectValue
                    placeholder={filter?.presetTitle === 'All' ? t('All') : filter?.presetTitle}
                  />
                </SelectTrigger>
                <SelectContent>
                  <SelectGroup>
                    <SelectLabel>{t('Preset')}</SelectLabel>
                    <SelectItem value="All">{t('All')}</SelectItem>
                    {allPresets &&
                      allPresets.map((preset) => (
                        <SelectItem value={preset.title} key={preset.id}>
                          {preset.title}
                        </SelectItem>
                      ))}
                  </SelectGroup>
                </SelectContent>
              </Select>
            </div>
            <div className="flex w-full justify-end mt-4 gap-2">
              <Button size="sm" variant="secondary" onClick={resetFilter}>
                {t('Reset')}
              </Button>
              <Button variant="primary" size="sm" onClick={applyFilter}>{t('Apply')}</Button>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Table */}
      <div>
        <ScrollArea
          className="rounded-md w-full whitespace-nowrap">
          <Table className=' sm:w-max lg:w-full'>
            <TableHeader>
              <TableRow className="grid grid-cols-12 w-full">
                <TableHead className='sm:col-span-1 lg:col-span-1'>
                  <Checkbox className='rounded-sm'
                    checked={
                      allPatrols.length > 0 &&
                      allPatrols.every((patrol) => checkedItems[patrol.id])
                    }
                    onCheckedChange={handleSelectAllCheckbox} />
                </TableHead>
                <TableHead className='sm:col-span-2 lg:col-span-2'>
                  {t("DocID")}
                </TableHead>
                <TableHead className='sm:col-span-2 lg:col-span-2'>
                  {t("Preset")}
                </TableHead>
                <TableHead className='sm:col-span-1 lg:col-span-2'>
                  {t("Status")}
                </TableHead>
                <TableHead className='sm:col-span-2 lg:col-span-2'>
                  {t("Progress")}
                </TableHead>
                <TableHead className='sm:col-span-2 lg:col-span-1'>
                  {t("Date")}
                </TableHead>
                <TableHead className='sm:col-span-2 lg:col-span-2'>
                  {t("Result")}
                </TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              <ScrollArea className="rounded-md w-full lg:[&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-388px)] sm:[&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-360px)]"
              >
                {allPatrols.length === 0 || !allPatrols ? (
                  <tr className="flex w-full h-full">
                    <td colSpan={5} className="w-full text-center py-6">
                      <NotFound
                        icon="task" title="NoPatrolsAvailable" description="NoPatrolsDescription"
                      />
                    </td>
                  </tr>
                ) :
                  (
                    allPatrols.map((patrol, index) => (
                      <TableRow key={index} className='grid grid-cols-12 w-full items-center' onClick={() => handleDetail(patrol.id)}>
                        <TableCell className='sm:col-span-1 lg:col-span-1'>
                          <Checkbox className='w-5 h-5 rounded-sm'
                            onClick={(e) => e.stopPropagation()}
                            checked={checkedItems[patrol.id] || false}
                            onCheckedChange={(checked: boolean) => handleCheckboxChange(patrol.id, checked)}
                          />
                        </TableCell>
                        <TableCell className='sm:col-span-2 lg:col-span-2'>
                          <p>{formatId(patrol.id)}</p>
                        </TableCell>
                        <TableCell className='sm:text-sm lg:text-base sm:col-span-2 lg:col-span-2 flex min-w-0"'>
                          <TooltipProvider>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <span className="truncate">{patrol.preset.title}</span>
                              </TooltipTrigger>
                              <TooltipContent side="bottom" className="ml-[129px]">
                                <p className="max-w-[200px] break-words">{patrol.preset.title}</p>
                              </TooltipContent>
                            </Tooltip>
                          </TooltipProvider>
                        </TableCell>
                        <TableCell className='sm:col-span-1 lg:col-span-2 flex'>
                          <BadgeCustom
                            iconName={getPatrolStatusVariant(patrol.status).iconName}
                            showIcon={true}
                            showTime={false}
                            variant={getPatrolStatusVariant(patrol.status).variant}
                            hideText={windowWidth > 1024 ? false : true}
                          >
                            {s(patrol.status)}
                          </BadgeCustom>
                        </TableCell>
                        <TableCell className='sm:col-span-2 lg:col-span-2'>
                          <p>
                            <Progress value={calculateProgress(patrol.results)} />
                          </p>
                        </TableCell>
                        <TableCell className='sm:col-span-2 lg:col-span-1'>
                          <p>{formatTime(patrol.date, locale, false)}</p>
                        </TableCell>
                        <TableCell className='sm:col-span-2 lg:col-span-2'>
                          <div className='flex justify-between'>
                            <div className='flex flex-row gap-2' >
                              <div className="flex gap-1 text-primary items-center">
                                <span className="material-symbols-outlined">checklist</span>
                                <p className="text-xl font-semibold">{patrol.itemCounts}</p>
                              </div>
                              <div className="flex gap-1 text-orange items-center">
                                <span className="material-symbols-outlined">close</span>
                                <p className="text-xl font-semibold">{countPatrolResult(patrol.results).fail}</p>
                              </div>
                              <div className="flex gap-1 text-destructive items-center">
                                <span className="material-symbols-outlined">
                                  error
                                </span>
                                <p className="text-xl font-semibold">{countPatrolResult(patrol.results).defect}</p>
                              </div>
                            </div>

                            <div>
                              <DropdownMenu>
                                <DropdownMenuTrigger onClick={(e) => e.stopPropagation()}>
                                  <Button variant="ghost" className="w-[45px] h-[45px]">
                                    <span className="material-symbols-outlined items-center text-input">
                                      more_horiz
                                    </span>
                                  </Button>
                                </DropdownMenuTrigger>

                                <DropdownMenuContent align="end" className="p-0">
                                  <DropdownMenuItem onClick={(e) => {
                                    handleDetail(patrol.id)
                                  }}>
                                    <h1>{t("Detail")}</h1>
                                  </DropdownMenuItem>
                                  <DropdownMenuItem onClick={(e) => {
                                    e.stopPropagation()
                                    handleOpenDialog(patrol.id)
                                  }}>
                                    <h1 className="text-destructive cursor-pointer">{t("Delete")}</h1>
                                  </DropdownMenuItem>
                                </DropdownMenuContent>
                              </DropdownMenu>
                            </div>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )
                }
              </ScrollArea>
            </TableBody>
          </Table>
          <ScrollBar orientation="horizontal" />
        </ScrollArea>

        {isDialogOpen && (
          <AlertCustom
            title={a("PatrolRemoveConfirmTitle")}
            description={a("PatrolRemoveConfirmDescription")}
            primaryButtonText={t("Confirm")}
            primaryIcon="check"
            secondaryButtonText={t("Cancel")}
            backResult={(backResult) => {
              if (backResult) {
                handleRemovePatrol(selectedPatrolId)
              }
              handleCloseDialog()
            }}
          ></AlertCustom>
        )}

        {isAlertOpen && (
          <AlertCustom
            title={a("ExportPatrolAlert")}
            description={a("ExportPatrolAlertDescription")}
            primaryButtonText={t("Confirm")}
            primaryIcon="check"
            secondaryButtonText={t("Cancel")}
            backResult={(backResult) => {
              if (backResult) {
                exportPatrol(allPatrols, checkedItems)
              }
              handleCloseAlert()
            }}
          />
        )}
      </div>
    </div >
  )
}

"use client";
import { IUser, role } from "@/app/type";
import { AlertCustom } from "@/components/alert-custom";
import BadgeCustom from "@/components/badge-custom";
import Textfield from "@/components/textfield";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  AlertDialog,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Button } from "@/components/ui/button";

import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { fetchData, getInitials, getUserVariant } from "@/lib/utils";
import { DropdownMenuTrigger } from "@radix-ui/react-dropdown-menu";

import React, { useState, useEffect, useRef } from "react";
import { useForm, FormProvider } from "react-hook-form";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { toast } from "@/hooks/use-toast";
import { useTranslations } from "next-intl";
import NotFound from "@/components/not-found";
import { UserTooltip } from "@/components/user-tooltip";
import { ScrollArea } from "@/components/ui/scroll-area";

export default function Page() {
  const t = useTranslations("General");
  const a = useTranslations("Alert");
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [selectedRoles, setSelectedRoles] = useState<string[]>([]);
  const [selectedStatus, setSelectedStatus] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const roles = [
    {
      value: "admin",
      label: t("admin"),
      icon: "manage_accounts",
      variant: "blue",
    },
    {
      value: "inspector",
      label: t("inspector"),
      icon: "person_search",
      variant: "red",
    },
    {
      value: "supervisor",
      label: t("supervisor"),
      icon: "manage_accounts",
      variant: "yellow",
    },
  ];

  const statuses = [
    { value: "true", label: "Active", color: "bg-green", variant: "green" },
    {
      value: "false",
      label: "Inactive",
      color: "bg-destructive",
      variant: "red",
    },
  ];

  const [allUsers, setAllUsers] = useState<IUser[]>([]);
  const getData = async () => {
    try {
      // Construct query params for roles, active status, and search term
      const params = new URLSearchParams();

      // Add selected roles to params (if any)
      if (selectedRoles.length > 0) {
        params.append("roles", selectedRoles.join(","));
      } else {
      }

      // Add active status to params
      if (selectedStatus !== null) {
        params.append("active", selectedStatus.toString()); // Assuming `selectedStatus` is a boolean
      } else {
      }

      // Add search term to params (if any)
      if (searchTerm) {
        params.append("search", searchTerm);
      } else {
      }

      // Fetch data using fetchData utility with query params
      const data = await fetchData(
        "get",
        `/users?profile=true&image=true&user=true&${params.toString()}`,
        true
      );

      // Validate data format
      if (!Array.isArray(data)) {
        console.error("Invalid data format:", data);
        return []; // Return empty array if data format is incorrect
      }

      setAllUsers(data); // Update state with the fetched data
      setFilteredUsers(data); // Initially set filtered data to all data

      return data; // Return fetched data
    } catch (error) {
      if (error.name !== "AbortError") {
        console.error("Failed to fetch user data:", error); // Log error
      }
      return []; // Return empty array on error
    }
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);

    // 
    handleResize();

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // Fetch and apply search filter on search term change

  const handleApply = async () => {
    const freshData = await getData(); // Fetch fresh data
    setFilteredUsers(freshData);
  };

  // Reset handler function
  const handleReset = async () => {
    setSelectedRoles([]);
    setSelectedStatus(null);
  };

  const form = useForm();
  const { handleSubmit } = form;

  const onSubmit = () => { };

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);
  const [dialogType, setDialogType] = useState<string>("");

  const handleUserUpdateActive = async (userId: number) => {
    // Toggle status and create data payload
    setFilteredUsers((prevUsers) => {
      const user = prevUsers.find((user) => user.id === userId);
      if (!user) {
        return prevUsers;
      }

      const updatedStatus = !user.active;

      // Show toast based on the new status
      toast({
        variant: "success",
        title: updatedStatus
          ? a("UserActivationSuccessful")
          : a("UserDeactivationSuccessful"),
        description: updatedStatus
          ? a("UserActivationSuccessfulDescription")
          : a("UserDeactivationSuccessfulDescription"),
      });

      return prevUsers.map((user) =>
        user.id === userId ? { ...user, active: updatedStatus } : user
      );
    });

    try {
      // Perform the API update after state update
      await fetchData("put", `/user/${userId}`, true, {
        active: !filteredUsers.find((user) => user.id === userId)?.active,
      });
    } catch (error) {
      console.error("Update failed", error);
    }
  };

  const handleUserUpdate = async (
    userId: number,
    password: string,
    role: role,
    active?: boolean,
  ) => {
    setFilteredUsers((prevUsers) => {
      const user = prevUsers.find((user) => user.id === userId);
      if (!user) {
        console.error(`User with ID ${userId} not found.`);
        return prevUsers;
      }

      const data: { active: boolean; password?: string; role: role } = {
        active,
        role,
      };

      // Add password to data if provided
      if (password && password.trim() !== "") {
        data.password = password;
      }

      // Check if there is no update to apply
      if (role === user.role && password === "") {
        toast({
          variant: "default",
          title: a("NoUpdatesApplied"),
          description: a("NoUpdatesAppliedDescription"),
        });
        return prevUsers; // No updates, return previous state
      }

      // Proceed to update if changes are detected
      const updatedUsers = prevUsers.map((user) =>
        user.id === userId
          ? {
            ...user,
            ...(data.role && { role: data.role }), // Update role if needed
            ...(password && { password: data.password }), // Update password if needed
            active: data.active, // Update active status
          }
          : user
      );

      // Perform the API update
      const updateUserData = async () => {
        try {
          toast({
            variant: "success",
            title: a("UserUpdateSuccessful"),
            description: a("UserUpdateSuccessfulDescription"),
          });
          await fetchData("put", `/user/${userId}`, true, data);
          await getData(); // Fetch latest data if needed
        } catch (error) {
          console.error("Update failed", error);
          toast({
            variant: "error",
            title: a("UserUpdateFailed"),
            description: a("UserUpdateFailedDescription"),
          });
        }
      };

      // Call update only if there are changes
      if (role !== user.role || password !== "") {
        updateUserData(); // Execute async update call
      }

      return updatedUsers; // Return updated user list for optimistic rendering
    });
  };

  let userCreate = useRef<editedUser>({
    username: "",
    password: "",
    role: "inspector", // Set a default role
  });

  const handleUserCreate = async (
    username: string,
    password: string,
    role: role
  ) => {
    const data = {
      username: username,
      password: password,
      role: role,
      active: true,
    };
    try {
      toast({
        variant: "success",
        title: a("EmployeeAdded"),
        description: a("EmployeeAddedDescription"),
      });
      const response = await fetchData("post", `/user`, true, data);
      setFilteredUsers((prev) => [...prev, response]);
    } catch (error) {
      console.error("Update failed", error);
    }
  };

  // State to manage validation errors
  const [errorsForCreateUser, setErrorsForCreateUser] = React.useState({
    username: "",
    password: "",
  });

  const validateFieldsUserCreate = () => {
    const { username, password } = userCreate.current;
    const newErrors = { username: "", password: "" };
    if (!username) {
      newErrors.username = a("ValidUsernamePlease");
      toast({
        variant: "error",
        title: a("EmployeeAddedFailed"),
        description: a("EmployeeAddedFailedNotValidInfo"),
      });
    }
    if (!password) {
      newErrors.password = a("ValidPasswordPlease");
      toast({
        variant: "error",
        title: a("EmployeeAddedFailed"),
        description: a("EmployeeAddedFailedNotValidInfo"),
      });
    } else if (password.length < 8) {
      newErrors.password = a("PasswordNotLongEnough");
      toast({
        variant: "error",
        title: a("EmployeeAddedFailed"),
        description: a("PasswordNotLongEnough"),
      });
    }

    setErrorsForCreateUser(newErrors);

    return !newErrors.username && !newErrors.password;
  };

  const handleCreateUserDialog = () => {
    const { username, password, role } = userCreate.current;

    if (!validateFieldsUserCreate()) {
      return;
    }

    setPendingAction(() => () => handleUserCreate(username, password, role));
    setDialogType("create");
    handleOpenDialog();
  };

  const handleInactiveUserDialog = (userId: number) => {
    setPendingAction(() => () => handleUserUpdateActive(userId));
    setDialogType("deactivate");
    handleOpenDialog();
  };

  const [passwordErrorForEdit, setPasswordErrorForEdit] = useState<
    string | null
  >(null); // State for password error message

  const handleEditUserDialog = async (userId: number, index: number) => {
    const updatedUser = userRefs.current[index];

    if (
      updatedUser.username === sortedUser[index].username &&
      updatedUser.role === sortedUser[index].role &&
      updatedUser.password === ""
    ) {
      toast({
        variant: "default",
        title: a("NoUpdatesApplied"),
        description: a("NoUpdatesAppliedDescription"),
      });
      await setIsDialogOpen(false);
      return;
    }

    if (
      updatedUser &&
      updatedUser.password.length < 8 &&
      updatedUser.password !== ""
    ) {
      setPasswordErrorForEdit(
        a("EmployeeEditPasswordMinLengthErrorDescription")
      );
      toast({
        variant: "error",
        title: a("PasswordError"),
        description: a("PasswordNotLongEnough"),
      });
      return;
    } else {
      setPasswordErrorForEdit(null); // Clear error message when password is valid
      setPendingAction(() => () => handleSave(userId, index));
      setDialogType("edit");
      handleOpenDialog();
    }
  };

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
      setDialogType(""); // Reset the dialog type after action is completed
    }
  };

  type editedUser = {
    username: string;
    password: string;
    role: role;
  };

  const userRefs = useRef<editedUser[]>([]);

  // Initialize userRefs after first render

  // Handler to save the updated user data
  const handleSave = async (userId: number, index: number) => {
    const updatedUser = userRefs.current[index];
    const user = allUsers.find((user) => user.id === userId);

    if (updatedUser) {
      handleUserUpdate(
        userId,
        updatedUser.password,
        updatedUser.role
      );
    } else {
      console.error(`User at index ${userId} is undefined`);
    }
  };
  const [sortOption, setSortOption] = useState<"Name" | "Type" | "ID">("ID");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("asc");
  const [sortedUser, setSortedUser] = useState<IUser[]>([]);

  // Filter by search term
  const filterBySearchTerm = (users: IUser[]) => {
    if (!searchTerm) return users; // If no search term, return all users

    return users.filter(
      (user) =>
        user.id.toString().includes(searchTerm) || // Convert ID to string before searching
        user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (user.profile?.name?.toLowerCase().includes(searchTerm.toLowerCase()) ??
          false) // Ensure profile exists
    );
  };

  useEffect(() => {
    const fetchAndFilter = async () => {
      const freshData = await getData(); // Fetch fresh data
      const filtered = filterBySearchTerm(freshData); // Apply search term filter
      setFilteredUsers(filtered); // Update state with filtered data
    };

    fetchAndFilter();
  }, [searchTerm]); // Trigger fetch when search term changes

  useEffect(() => {
    const sorted = [...filteredUsers].sort((a, b) => {
      let comparison = 0;

      // Sort by Name
      if (sortOption === "Name") {
        const titleA = a.title || a.username || ""; // Handle undefined values
        const titleB = b.title || b.username || ""; // Handle undefined values
        comparison = titleA.localeCompare(titleB);
      }
      // Sort by Type
      else if (sortOption === "Type") {
        const typeA = a.role || ""; // Handle undefined values
        const typeB = b.role || ""; // Handle undefined values
        comparison = typeA.localeCompare(typeB);
      }
      // Sort by ID
      else if (sortOption === "ID") {
        comparison = a.id - b.id; // Assume ID is a number
      }

      return sortOrder === "asc" ? comparison : -comparison; // Ascending/Descending order
    });

    setSortedUser(sorted); // Set the sorted users list
  }, [filteredUsers, sortOption, sortOrder]); // Dependencies (no need for `allUsers` here)

  useEffect(() => {
    userRefs.current = sortedUser.map((employee) => ({
      username: employee.username,
      password: "",
      role: employee.role,
    }));
  }, [sortedUser, allUsers]);

  return (
    <div className="flex flex-col ">
      {/* Search and Actions */}
      <div className="flex justify-between">
        <h1 className="font-bold text-2xl">{t("ManageEmployees")}</h1>
        <div className="flex justify-between items-center mb-4">
          <AlertDialog
            open={
              isDialogOpen &&
              (dialogType === "add_user_menu" || dialogType === "create")
            }
            onOpenChange={(open) => {
              if (!open) {
                setIsDialogOpen(false);
              }
            }}
          >
            <AlertDialogTrigger asChild>
              <Button
                className="gap-2"
                variant="primary"
                onClick={() => {
                  userCreate.current = {
                    username: "",
                    password: "",
                    role: "inspector",
                  }; // Default values
                  setDialogType("add_user_menu");
                  setIsDialogOpen(true);
                  setErrorsForCreateUser({
                    username: "",
                    password: "",
                  });
                }}
                size="default"
              >
                <span className="material-symbols-outlined">add</span>
                {t("NewEmployee")}
              </Button>
            </AlertDialogTrigger>
            <AlertDialogContent
              onClick={(e) => {
                e.stopPropagation();
              }}
            >
              <AlertDialogTitle></AlertDialogTitle>
              <AlertDialogHeader>
                <h1 className="text-2xl font-bold">{t("AddNewEmployee")}</h1>
                <AlertDialogDescription>
                  {t("AddNewEmployeeDescription")}{" "}
                </AlertDialogDescription>
              </AlertDialogHeader>
              <FormProvider {...form}>
                <form
                  onSubmit={handleSubmit(onSubmit)}
                  className="flex flex-col gap-4"
                >
                  <div className="flex flex-col gap-1 w-96">
                    <label>{t("Username")}</label>
                    <Textfield
                      className="bg-secondary"
                      showIcon={true}
                      iconName="person"
                      placeholder={t("Username")}
                      onChange={(e) => {
                        if (userCreate.current) {
                          userCreate.current.username = e.target.value;
                        }
                        setErrorsForCreateUser((prev) => ({
                          ...prev,
                          username: "",
                        })); // Clear error on input change
                      }}
                    />
                    {errorsForCreateUser.username && (
                      <p className="text-red-500 text-sm mt-1">
                        {errorsForCreateUser.username}
                      </p>
                    )}
                  </div>

                  <div className="flex flex-col gap-1 w-96">
                    <label>{t("Password")}</label>
                    <Textfield
                      className="bg-secondary"
                      showIcon={true}
                      iconName="lock"
                      placeholder={t("Password")}
                      onChange={(e) => {
                        if (userCreate.current) {
                          userCreate.current.password = e.target.value;
                        }
                        setErrorsForCreateUser((prev) => ({
                          ...prev,
                          password: "",
                        })); // Clear error on input change
                      }}
                    />
                    {errorsForCreateUser.password && (
                      <p className="text-red-500 text-sm mt-1">
                        {errorsForCreateUser.password}
                      </p>
                    )}
                  </div>

                  <div className="flex flex-col gap-1">
                    <label>{t("Role")}</label>
                    <div className="relative rounded-md w-auto">
                      <Select
                        defaultValue="inspector"
                        onValueChange={(value) => {
                          userCreate.current.role = value as role;
                        }}
                      >
                        <SelectTrigger className="bg-secondary w-96 h-auto p-2 rounded-md border-none">
                          <SelectValue placeholder="" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectGroup>
                            <SelectItem value="supervisor">
                              <BadgeCustom
                                variant={"yellow"}
                                iconName="engineering"
                                showIcon={true}
                                shape="square"
                              >
                                {t("supervisor")}
                              </BadgeCustom>
                            </SelectItem>
                            <SelectItem value="inspector">
                              <BadgeCustom
                                variant={"red"}
                                iconName="person_search"
                                showIcon={true}
                                shape="square"
                              >
                                {t("inspector")}
                              </BadgeCustom>
                            </SelectItem>
                            <SelectItem value="admin">
                              <BadgeCustom
                                variant={"blue"}
                                iconName="manage_accounts"
                                showIcon={true}
                                shape="square"
                              >
                                {t("admin")}
                              </BadgeCustom>
                            </SelectItem>
                          </SelectGroup>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div className="mt-6 flex justify-end items-center gap-4">
                    <AlertDialogCancel asChild>
                      <Button variant="secondary" type="button">
                        {t("Back")}
                      </Button>
                    </AlertDialogCancel>
                    <Button
                      className="flex gap-2"
                      variant="primary"
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent click event from bubbling up
                        handleCreateUserDialog();
                      }}
                    >
                      <span className="material-symbols-outlined">add</span>
                      {t("AddNewEmployee")}{" "}
                    </Button>
                    {isDialogOpen && dialogType === "create" && (
                      <AlertCustom
                        title={a("SureToAddEmployee")}
                        description={a("SureToAddEmployeeDescription")}
                        primaryButtonText={t("Confirm")}
                        primaryIcon="check"
                        secondaryButtonText={t("Cancel")}
                        backResult={(result) => handleDialogResult(result)}
                      />
                    )}
                  </div>
                </form>
              </FormProvider>
            </AlertDialogContent>
          </AlertDialog>
        </div>
      </div>
      <div className="flex flex-col gap-4">
        <div className="flex items-center gap-2">
          <Textfield
            iconName="search"
            showIcon={true}
            placeholder={t("Search")}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <DropdownMenu>
            <DropdownMenuTrigger
              className={`custom-shadow px-[10px]
               bg-card w-auto h-[40px] gap-[10px] inline-flex items-center
               justify-center rounded-md text-sm font-medium`}
            >
              <span className="material-symbols-outlined">swap_vert</span>
              <div className="text-lg">{t("Sort")}</div>
            </DropdownMenuTrigger>

            <DropdownMenuContent align="end" className="p-2 gap-2">
              {/* Sort By label */}
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("SortBy")}
              </DropdownMenuLabel>

              <DropdownMenuRadioGroup
                value={sortOption}
                onValueChange={(value: string) =>
                  setSortOption(value as "Name" | "Type" | "ID")
                }
              >
                <DropdownMenuRadioItem value="Name" className="text-base">
                  {t("Name")}
                </DropdownMenuRadioItem>

                <DropdownMenuRadioItem value="Type" className="text-base">
                  {t("Type")}
                </DropdownMenuRadioItem>
                <DropdownMenuRadioItem value="ID" className="text-base">
                  {t("Id")}
                </DropdownMenuRadioItem>
              </DropdownMenuRadioGroup>

              {/* Order label */}
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Order")}
              </DropdownMenuLabel>

              <DropdownMenuRadioGroup
                value={sortOrder}
                onValueChange={(value: string) =>
                  setSortOrder(value as "asc" | "desc")
                }
              >
                <DropdownMenuRadioItem value="asc" className="text-base">
                  {t("Ascending")}
                </DropdownMenuRadioItem>
                <DropdownMenuRadioItem value="desc" className="text-base">
                  {t("Descending")}
                </DropdownMenuRadioItem>
              </DropdownMenuRadioGroup>
            </DropdownMenuContent>
          </DropdownMenu>

          <DropdownMenu>
            <DropdownMenuTrigger
              className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium`}
            >
              <span className="material-symbols-outlined">page_info</span>
              <div className="text-lg"> {t("Filter")}</div>
            </DropdownMenuTrigger>
            <DropdownMenuContent
              className="flex flex-col justify-center gap-2 p-2 z-50"
              align="end"
            >
              <div>
                <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                  {t("Role")}
                </DropdownMenuLabel>

                {roles.map((role) => (
                  <DropdownMenuCheckboxItem
                    key={role.value}
                    checked={selectedRoles.includes(role.value)}
                    className={`text-base ${selectedRoles.includes(role.value) ? "bg-muted/50" : ""
                      }`}
                    onSelect={(e) => {
                      e.preventDefault();
                      // Toggle role selection in the array
                      setSelectedRoles((prevSelectedRoles) =>
                        prevSelectedRoles.includes(role.value)
                          ? prevSelectedRoles.filter(
                            (item) => item !== role.value
                          )
                          : [...prevSelectedRoles, role.value]
                      );
                    }}
                  >
                    <BadgeCustom
                      shape="square"
                      variant={
                        role.variant as
                        | "blue"
                        | "red"
                        | "yellow"
                        | "green"
                        | "default"
                        | "secondary"
                        | "mint"
                        | "orange"
                        | "purple"
                      }
                      iconName={role.icon}
                      showIcon={true}
                    >
                      {t(role.value)}
                    </BadgeCustom>
                  </DropdownMenuCheckboxItem>
                ))}

                <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                  {t("Status")}
                </DropdownMenuLabel>
                <DropdownMenuRadioGroup value={selectedStatus}>
                  {statuses.map((status) => (
                    <DropdownMenuRadioItem
                      key={status.value}
                      value={status.value}
                      className={`text-base ${selectedStatus === status.value ? "bg-muted/50" : ""
                        }`}
                      onSelect={(e) => {
                        if (
                          status.value === "true" ||
                          status.value === "false"
                        ) {
                          setSelectedStatus(null);
                        }
                        e.preventDefault();
                        setSelectedStatus(status.value);
                      }}
                    >
                      <BadgeCustom
                        variant={
                          status.variant as
                          | "blue"
                          | "red"
                          | "yellow"
                          | "green"
                          | "default"
                          | "secondary"
                          | "mint"
                          | "orange"
                          | "purple"
                        }
                      >
                        <div className="flex flex-row justify-center items-center gap-2 w-[105px]">
                          <div
                            className={`flex w-2 h-2 rounded-full ${status.color}`}
                          ></div>
                          {t(status.label)}
                        </div>
                      </BadgeCustom>
                    </DropdownMenuRadioItem>
                  ))}
                </DropdownMenuRadioGroup>
              </div>

              <div className="flex w-full justify-end mt-4 gap-2">
                <Button size="sm" variant="secondary" onClick={handleReset}>
                  {t("Reset")}
                </Button>
                <Button variant="primary" size="sm" onClick={handleApply}>
                  {t("Apply")}
                </Button>
              </div>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        {/* Manage Employees Section */}

        {/* Header */}
        {sortedUser.length > 0 ? (
          <div className="bg-secondary rounded-lg border-none">
            <Table className="overflow-hidden min-h-[calc(100vh-860px)]">
              <TableHeader>
                <TableRow className="grid grid-cols-12 w-full">
                  <TableHead className="sm:col-span-1 lg:col-span-1">
                    {t("Id")}
                  </TableHead>
                  <TableHead className="sm:col-span-4 lg:col-span-3">
                    {t("FullName")}
                  </TableHead>
                  <TableHead className="sm:col-span-3 lg:col-span-3">
                    {t("Role")}
                  </TableHead>
                  <TableHead className="sm:col-span-3 lg:col-span-4">
                    {t("Status")}
                  </TableHead>
                  <TableHead className="sm:col-span-0 lg:col-span-0"></TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                <ScrollArea className="h-full w-full rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-300px)]">
                  {sortedUser.map((employee, index) => (
                    <TableRow key={index} className="grid grid-cols-12">
                      <TableCell className="sm:text-sm lg:text-base sm:col-span-1 lg:col-span-1 flex items-center min-w-0">
                        {employee.id}
                      </TableCell>
                      <TableCell className="sm:text-sm lg:text-base sm:col-span-4 lg:col-span-3 flex items-center">
                        <div className="flex items-center gap-2 w-full">
                          <UserTooltip user={employee}>
                            <div className="flex items-center gap-2 md:w-[220px] xl:w-[440px]">
                              <Avatar className="custom-shadow h-[35px] w-[35px]">
                                <AvatarImage
                                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${employee.profile.image?.path}`}
                                />
                                <AvatarFallback id={employee.id.toString()}>
                                  {getInitials(employee.profile.name)}
                                </AvatarFallback>
                              </Avatar>
                              {employee.profile.name ? (
                                <p className="truncate">
                                  {employee.profile.name}
                                </p>
                              ) : (
                                <p className="text-[14px] text-destructive truncate">
                                  {employee.username}
                                  {t("NoProfileProvided")}
                                </p>
                              )}
                            </div>
                          </UserTooltip>
                        </div>
                      </TableCell>
                      <TableCell className="sm:text-sm lg:text-base sm:col-span-3 lg:col-span-3 flex  items-center">
                        <BadgeCustom
                          shape="square"
                          variant={getUserVariant(employee.role).variant}
                          iconName={getUserVariant(employee.role).iconName}
                          showIcon={true}
                          hideText={windowWidth > 1024 ? false : true}
                        >
                          {t(employee.role)}
                        </BadgeCustom>
                      </TableCell>
                      <TableCell className="sm:text-sm lg:text-base sm:col-span-3 lg:col-span-4 flex  items-center">
                        <BadgeCustom
                          variant={employee.active ? "green" : "red"}
                          showIcon={true}
                          hideText={windowWidth > 1024 ? false : true}
                          iconName="radio_button_checked"
                        >
                          {employee.active ? t("Active") : t("Inactive")}
                        </BadgeCustom>
                      </TableCell>
                      <TableCell className="sm:text-sm lg:text-base sm:col-span-0 lg:col-span-0 flex items-end justify-end">
                        <DropdownMenu>
                          <DropdownMenuTrigger>
                            <Button
                              variant="ghost"
                              className="w-[45px] h-[45px]"
                              onClick={() => setDialogType("blank")}
                            >
                              <span className="material-symbols-outlined items-center text-muted-foreground">
                                more_horiz
                              </span>
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent
                            align="end"
                            className="px-4 py-2"
                          >
                            <div className=" cursor-pointer">
                              <AlertDialog
                                open={
                                  (isDialogOpen && dialogType === "editform") ||
                                  dialogType === "edit"
                                }
                              >
                                <AlertDialogTrigger
                                  asChild
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    setIsDialogOpen(true);
                                    setPasswordErrorForEdit(null);
                                    setDialogType("editform");
                                    userRefs.current[index].password = "";
                                  }}
                                >
                                  <div
                                    onClick={(e) => e.stopPropagation()}
                                    className="flex w-full text-[18px]"
                                  >
                                    {t("Edit")}
                                  </div>
                                </AlertDialogTrigger>
                                <AlertDialogContent
                                  className="flex flex-col gap-4 "
                                  onClick={(e) => {
                                    e.stopPropagation();
                                  }}
                                >
                                  <AlertDialogHeader>
                                    <h1 className="text-2xl font-bold">
                                      {t("EditEmployee")}
                                    </h1>
                                    <AlertDialogTitle></AlertDialogTitle>
                                    <AlertDialogDescription>
                                      {t("EditEmployeeDescription")}
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>

                                  <div className="flex flex-col gap-1 pointer-events-none w-96">
                                    <label> {t("Username")}</label>
                                    <Textfield
                                      className="bg-secondary cursor-not-allowed "
                                      showIcon={true}
                                      iconName="person"
                                      onChange={() => { }}
                                      value={""}
                                      placeholder={employee.username}
                                    />
                                  </div>

                                  <div className=" flex flex-col gap-4  w-96 h-auto">
                                    <div>
                                      <label>{t("Password")}</label>
                                      <Textfield
                                        placeholder={t("Password")}
                                        className={`bg-secondary`}
                                        showIcon={true}
                                        iconName="lock"
                                        onChange={(e) => {
                                          try {
                                            if (userRefs.current[index]) {
                                              userRefs.current[index].password =
                                                e.target.value;
                                            }

                                            // Validate password length
                                            if (e.target.value.length < 8) {
                                              setPasswordErrorForEdit(
                                                "Password must be at least 8 characters long."
                                              );
                                            } else {
                                              setPasswordErrorForEdit(null);
                                            }
                                          } catch (error) {
                                            console.error(
                                              "Error in onChange handler:",
                                              error
                                            );
                                          }
                                        }}
                                      />
                                    </div>

                                    {passwordErrorForEdit && (
                                      <p className="text-red-500 text-sm mt-1">
                                        {passwordErrorForEdit}
                                      </p>
                                    )}
                                  </div>

                                  <div className="w-96 h-auto">
                                    <label>{t("Role")}</label>
                                    <div className="relative bg-secondary rounded-md">
                                      <Select
                                        defaultValue={employee.role}
                                        onValueChange={(value) => {
                                          if (userRefs.current[index]) {
                                            userRefs.current[index].role =
                                              value as role; // Ensure the value matches the type
                                          }
                                        }}
                                      >
                                        <SelectTrigger className="bg-secondary w-96 h-auto p-2  rounded-md border-none">
                                          <SelectValue placeholder="" />
                                        </SelectTrigger>
                                        <SelectContent>
                                          <SelectGroup>
                                            <SelectItem value="supervisor">
                                              <BadgeCustom
                                                variant="yellow"
                                                iconName="engineering"
                                                showIcon={true}
                                                shape="square"
                                              >
                                                {t("supervisor")}
                                              </BadgeCustom>
                                            </SelectItem>
                                            <SelectItem value="inspector">
                                              <BadgeCustom
                                                variant="red"
                                                iconName="person_search"
                                                showIcon={true}
                                                shape="square"
                                              >
                                                {t("inspector")}
                                              </BadgeCustom>
                                            </SelectItem>
                                            <SelectItem value="admin">
                                              <BadgeCustom
                                                variant="blue"
                                                iconName="manage_accounts"
                                                showIcon={true}
                                                shape="square"
                                              >
                                                {t("admin")}
                                              </BadgeCustom>
                                            </SelectItem>
                                          </SelectGroup>
                                        </SelectContent>
                                      </Select>
                                    </div>
                                  </div>

                                  <AlertDialogFooter>
                                    <Button
                                      variant="secondary"
                                      size="lg"
                                      onClick={async () => {
                                        handleDialogResult(false);
                                        setPendingAction(null); // Clear the pending action
                                        setDialogType(""); // Reset the dialog type after action is completed
                                      }}
                                    >
                                      {t("Back")}
                                    </Button>
                                    <Button
                                      className=" flex  justify-center gap-2"
                                      variant="primary"
                                      size="lg"
                                      onClick={(e) => {
                                        e.stopPropagation(); // Prevent click event from bubbling up
                                        handleEditUserDialog(
                                          employee.id,
                                          index
                                        );
                                      }}
                                    >
                                      <span className="material-symbols-outlined">
                                        save
                                      </span>
                                      {t("Save")}
                                    </Button>
                                  </AlertDialogFooter>
                                </AlertDialogContent>

                                {isDialogOpen === true &&
                                  dialogType === "edit" && (
                                    <AlertCustom
                                      title={a("SureToEditEmployee")}
                                      description={a(
                                        "SureToEditEmployeeDescription"
                                      )}
                                      primaryButtonText={t("Confirm")}
                                      primaryIcon="check"
                                      secondaryButtonText={t("Cancel")}
                                      backResult={handleDialogResult}
                                    />
                                  )}
                              </AlertDialog>
                            </div>
                            <div className=" cursor-pointer">
                              <div
                                onClick={(e) => {
                                  e.stopPropagation();
                                  handleInactiveUserDialog(employee.id);
                                }}
                                className="flex w-full text-[18px] text-destructive"
                              >
                                {employee.active
                                  ? t("Deactivate")
                                  : t("Activate")}
                              </div>
                              {isDialogOpen === true &&
                                dialogType === "deactivate" && (
                                  <AlertCustom
                                    title={
                                      employee.active
                                        ? a("SureToDeactivate")
                                        : a("SureToActivate")
                                    }
                                    description={
                                      employee.active
                                        ? a("SureToDeactivateDescription")
                                        : a("SureToActivateDescription")
                                    }
                                    primaryButtonText={t("Confirm")}
                                    primaryIcon="check"
                                    secondaryButtonText={t("Cancel")}
                                    backResult={handleDialogResult}
                                  />
                                )}
                            </div>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))}
                </ScrollArea>
              </TableBody>
            </Table>
          </div>
        ) : (
          <div className="col-span-full min-h-[261px]">
            <NotFound
              icon="quick_reference_all"
              title="NoDataAvailable"
              description="NoDataAvailableDescription"
            />
          </div>
        )}
      </div>
    </div>
  );
}

/**
 * :
 *   Checklist   Checklist   Checklist  Zone  Date 
 * Input:
 * - 
 * Output:
 * -  Checklist   Checklist   Checklist   Checklist  Zone  Date 
 * -  Checklist 
 * -  Checklist 
 **/

"use client";
import { IChecklist, IZone } from "@/app/type";
import { AlertCustom } from "@/components/alert-custom";
import Textfield from "@/components/textfield";

import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Button, buttonVariants } from "@/components/ui/button";

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

import { fetchData, formatTime, getInitials } from "@/lib/utils";

import React, { useState, useEffect } from "react";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { AvatarImage } from "@radix-ui/react-avatar";
import { Skeleton } from "@/components/ui/skeleton";
import { toast } from "@/hooks/use-toast";
import { useLocale, useTranslations } from "next-intl";
import {
  TooltipProvider,
  Tooltip,
  TooltipTrigger,
  TooltipContent,
} from "@radix-ui/react-tooltip";
import { useRouter } from "next/navigation";
import { DatePickerWithRange } from "@/components/date-picker";
import dynamic from "next/dynamic";
import { ScrollArea } from "@/components/ui/scroll-area";
import NotFound from "@/components/not-found";
import { VersionTooltip } from "@/components/version-tooltip";
import { ZoneTooltip } from "@/components/zone-tooltip";
const Map = dynamic(() => import("@/components/map"), { ssr: false });

export default function Page() {
  const z = useTranslations("Zone");
  const t = useTranslations("General");
  const a = useTranslations("Alert");

  const router = useRouter();
  const locale = useLocale();
  interface IChecklistWithExtras extends IChecklist {
    zones: string[]; // New field
    itemCounts: Record<string, number>; // Another new field
    versionCount: number;
  }

  const handleDeleteChecklist = async (id: number) => {
    toast({
      variant: "success",
      title: a("ChecklistRemoveSuccessTitle"),
      description: a("ChecklistRemoveSuccessDescription"),
    });
    try {
      const response = await fetchData("delete", `/checklist/${id}`, true);

      if (response) {
        setFilteredChecklists((prevChecklists) =>
          prevChecklists.filter((checklist) => checklist.id !== id)
        );
      } else {
        console.error("Failed to delete checklist: No response from API");
      }
    } catch (error) {
      console.error("An error occurred while deleting the checklist:", error);
    }
  };

  const handleDeletePatrolChecklistDialog = async (id: number) => {
    setPendingAction(() => () => handleDeleteChecklist(id));
    setDialogType("delete");
    setIsDialogOpen(true);
  };

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);
  const [dialogType, setDialogType] = useState<string>("");

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
      setDialogType(""); // Reset the dialog type after action is completed
    }
  };

  const getChecklistColor = (checklist: IChecklistWithExtras): string => {
    let safety = checklist.itemCounts.safety || 0;
    let environment = checklist.itemCounts.environment || 0;
    let maintenance = checklist.itemCounts.maintenance || 0;

    // Find the highest value
    const maxValue = Math.max(safety, environment, maintenance);

    // Handle if all are equal
    if (safety === environment && environment === maintenance) {
      return "border-yellow"; // All are equal
    }

    // Handle cases where two values are tied and are the max
    if (safety === maxValue && environment === maxValue) {
      return "border-mint"; // Safety and environment are tied
    }
    if (environment === maxValue && maintenance === maxValue) {
      return "border-purple"; // Environment and maintenance are tied
    }
    if (safety === maxValue && maintenance === maxValue) {
      return "border-orange"; // Safety and maintenance are tied
    }

    // Handle single highest value
    if (safety === maxValue) {
      return "border-green"; // Safety has the highest value
    }
    if (environment === maxValue) {
      return "border-primary"; // Environment has the highest value
    }
    if (maintenance === maxValue) {
      return "border-destructive"; // Maintenance has the highest value
    }

    // Default fallback
    return ""; // Default color when none of the above conditions are met
  };

  const handleChecklist = (id: number) => {
    router.push(`/${locale}/admin/settings/patrol-checklist/${id}`);
  };

  const handleGoToCreateChecklist = () => {
    router.push(`/${locale}/admin/settings/patrol-checklist/create`);
  };
  // Modify the getData function to use fetchData
  const getData = async () => {
    try {
      // Construct query params for the filters
      const params = new URLSearchParams();

      // Add search term to params
      if (searchTerm) {
        params.append("search", searchTerm);
      }

      // Add selected zones to params
      if (selectedZones.length > 0) {
        params.append(
          "zones",
          selectedZones.map((zone) => zone.name).join(",")
        );
      }

      // Add date range to params
      if (selectedDateRange?.from) {
        params.append("startDate", selectedDateRange.from.toISOString());
      }
      if (selectedDateRange?.to) {
        params.append("endDate", selectedDateRange.to.toISOString());
      }

      // Fetch data using fetchData utility with query params
      const data = await fetchData(
        "get",
        `/checklists?${params.toString()}`,
        true
      );

      // Validate data format
      if (!Array.isArray(data)) {
        console.error("Invalid data format:", data);
        return []; // Return empty array if data format is incorrect
      }

      setFilteredChecklists(data); // Initially set filtered data to all data

      return data; // Return fetched data
    } catch (error) {
      console.error("Failed to fetch checklist data:", error); // Log error
      return []; // Return empty array on error
    }
  };

  // Trigger the fetch on component mount using useEffect
  useEffect(() => {
    getData();
  }, []); // Initial fetch when the component mounts

  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState<
    "Name" | "ModifiedDate" | "Type"
  >("Name");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("asc");
  const [selectedZones, setSelectedZones] = useState<IZone[]>([]);
  const [tempSelectedZones, setTempSelectedZones] = useState<IZone[]>([]);
  const [selectedDateRange, setSelectedDateRange] = useState<{
    from?: Date;
    to?: Date;
  }>({});
  const [tempDateRange, setTempDateRange] = useState<{
    from?: Date;
    to?: Date;
  }>({});
  const [filteredChecklists, setFilteredChecklists] = useState<
    IChecklistWithExtras[]
  >([]);

  // Filter by search term
  const filterBySearchTerm = (checklists: IChecklistWithExtras[]) => {
    if (!searchTerm) return checklists; // If no search term, return all checklists
    return checklists.filter((checklist) =>
      checklist.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  // Fetch and apply search filter on search term change
  useEffect(() => {
    const fetchAndFilter = async () => {
      const freshData = await getData(); // Fetch fresh data
      const filtered = filterBySearchTerm(freshData); // Apply search term filter
      setFilteredChecklists(filtered); // Update state with filtered data
    };

    fetchAndFilter();
  }, [searchTerm]); // Trigger fetch when search term changes

  // Apply filters (zones, date range, and search term)
  const applyFilters = async () => {
    const freshData = await getData(); // Fetch fresh data

    // Update selected filters
    setSelectedZones(tempSelectedZones);
    setSelectedDateRange(tempDateRange);

    // Apply filters for zones and date range
    const filtered = freshData.filter((checklist) => {
      const matchesZones =
        tempSelectedZones.length === 0 || // If no zones are selected, show all
        checklist.zones.some((zone) =>
          tempSelectedZones.some(
            (selectedZone) =>
              selectedZone.name.toLowerCase() === zone.toLowerCase()
          )
        );

      const matchesDateRange =
        (!tempDateRange?.from ||
          new Date(checklist.updatedAt) >= tempDateRange.from) &&
        (!tempDateRange?.to ||
          new Date(checklist.updatedAt) <= tempDateRange.to);

      return matchesZones && matchesDateRange;
    });

    // Apply search term filter
    const filteredWithSearch = filterBySearchTerm(filtered);
    setFilteredChecklists(filteredWithSearch); // Update the filtered state
  };

  // Reset filters to default
  const resetFilters = async () => {
    setTempSelectedZones([]); // Reset temp selected zones
    setTempDateRange({}); // Reset temp date range
    setSelectedZones([]); // Clear applied zones
    setSelectedDateRange({}); // Clear applied date range
  };

  const [sortedChecklists, setSortedChecklists] = useState<
    IChecklistWithExtras[]
  >([]);

  useEffect(() => {
    const sorted = [...filteredChecklists].sort((a, b) => {
      let comparison = 0;

      if (sortOption === "Name") {
        comparison = a.title.localeCompare(b.title);
      } else if (sortOption === "ModifiedDate") {
        comparison =
          new Date(a.updatedAt).getTime() - new Date(b.updatedAt).getTime();
      } else if (sortOption === "Type") {
        const typeA = getChecklistColor(a);
        const typeB = getChecklistColor(b);

        comparison = typeA.localeCompare(typeB);
      }

      return sortOrder === "asc" ? comparison : -comparison;
    });

    setSortedChecklists(sorted); // Update sorted checklists state
  }, [filteredChecklists, sortOption, sortOrder]); // Dependencies to trigger the effect
  return (
    <div className="flex flex-col gap-4 ">
      <div className="flex flex-row justify-between pt-2">
        <div className="text-2xl font-bold">{t("Checklist")}</div>
        <Button
          onClick={() => {
            handleGoToCreateChecklist();
          }}
          className="flex flex-row gap-2 custom-shadow"
          variant="primary"
        >
          <span className="material-symbols-outlined text-2xl">add</span>
          <div className="text-lg">{t("CreateChecklist")}</div>
        </Button>
      </div>
      <div className="flex items-center gap-2">
        <Textfield
          iconName="search"
          showIcon={true}
          placeholder={t("Search")}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <DropdownMenu>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px]
                     bg-card w-auto h-[40px] gap-[10px] inline-flex items-center
                     justify-center rounded-md text-sm font-medium`}
          >
            <span className="material-symbols-outlined">swap_vert</span>
            <div className="text-lg">{t("Sort")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="p-2 gap-2">
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("SortBy")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sortOption}
              onValueChange={(value) =>
                setSortOption(value as "Name" | "ModifiedDate" | "Type")
              }
            >
              <DropdownMenuRadioItem
                value="Name"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Name")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="ModifiedDate"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("DateModified")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="Type"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Type")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>

            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("Order")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sortOrder}
              onValueChange={(value) => setSortOrder(value as "desc" | "asc")}
            >
              <DropdownMenuRadioItem
                value="asc"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Ascending")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="desc"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Descending")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
                `}
          >
            <span className="material-symbols-outlined">page_info</span>
            <div className="text-lg">{t("Filter")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="flex flex-col justify-center gap-2 p-2 z-50"
            align="end"
          >
            <div>
              <div>
                <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                  {t("Date")}
                </DropdownMenuLabel>
                <DatePickerWithRange
                  className="my-date-picker"
                  startDate={tempDateRange?.from || selectedDateRange?.from}
                  endDate={tempDateRange?.to || selectedDateRange?.to}
                  onSelect={(range) => {
                    if (range?.from || range?.to) {
                      setTempDateRange(range);
                    }
                  }}
                />
              </div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Zone")}
              </DropdownMenuLabel>
              <AlertDialog>
                <AlertDialogTrigger
                  asChild
                  className="flex items-center gap-2 rounded cursor-pointer"
                >
                  <Button
                    variant={"secondary"}
                    className="w-full h-[36px] rounded-md bg-secondary justify-start text-left gap-[2px] font-normal text-base"
                  >
                    <span className="material-symbols-outlined">
                      location_on
                    </span>
                    <p className="truncate w-[200px]">
                      {selectedZones.length > 0
                        ? selectedZones.map((zone) => z(zone.name)).join(", ")
                        : t("SelectZones")}
                    </p>
                  </Button>
                </AlertDialogTrigger>

                <AlertDialogContent className="w-full sm:w-[40%] md:w-[50%] lg:w-[100%] max-w-[1200px] rounded-md">
                  <AlertDialogHeader>
                    <AlertDialogTitle className="text-2xl">
                      {t("FilterByZoneTitle")}
                    </AlertDialogTitle>
                    <AlertDialogDescription className="text-base">
                      {t("FilterByZoneDescription")}
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <div>
                    <div className="text-muted-foreground flex items-center">
                      <span className="material-symbols-outlined">
                        location_on
                      </span>
                      {t("Zone")}
                    </div>
                    <div className="flex justify-center bg-secondary rounded-lg py-4">
                      <Map
                        disable={false}
                        initialSelectedZones={selectedZones.map(
                          (zone) => zone.id
                        )}
                        onZoneSelect={(zones: IZone[]) =>
                          setTempSelectedZones(zones)
                        }
                      />
                    </div>
                  </div>

                  <AlertDialogFooter>
                    <AlertDialogAction
                      className="bg-primary"
                      onClick={() => {
                        setSelectedZones(tempSelectedZones); // Apply selected zones
                      }}
                    >
                      {t("Done")}
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            </div>

            <div className="flex w-full justify-end mt-4 gap-2">
              <Button size="sm" variant="secondary" onClick={resetFilters}>
                {t("Reset")}
              </Button>
              <Button variant="primary" size="sm" onClick={applyFilters}>
                {t("Apply")}
              </Button>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Checklist Cards */}
      <ScrollArea className="h-full w-full rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-280px)]">
        <div className="space-y-4  rounded-lg">
          {sortedChecklists.length > 0 ? (
            sortedChecklists.map((checklist) => (
              <div key={checklist.id}>
                <div
                  onClick={() => {
                    handleChecklist(checklist.id);
                  }}
                  className={`flex flex-row border-l-[10px] truncate h-full cursor-pointer ${getChecklistColor(
                    checklist
                  )} border-destructive h-[166px] bg-card rounded-lg custom-shadow px-6 py-4 justify-between`}
                >
                  <div className="flex flex-col gap-4  min-w-0">
                    {/* Left Section */}
                    <div className="gap-1">
                      <VersionTooltip object={checklist}>
                        <Button
                          variant="ghost"
                          className="text-card-foreground text-base flex items-center hover:bg-card m-0 p-0"
                        >
                          <span className="material-symbols-outlined mr-1">
                            history
                          </span>
                          {t("Version")} {checklist.version}
                        </Button>
                      </VersionTooltip>

                      <h2 className="text-2xl font-semibold truncate">
                        {checklist.title}
                      </h2>
                    </div>

                    {/* Stats */}
                    <div className="flex flex-col gap-2 text-gray-500">
                      <div className="flex flex-row gap-2">
                        <span className="material-symbols-outlined  text-muted-foreground">
                          location_on
                        </span>

                        <ZoneTooltip zonesName={checklist.zones}>
                          <p className="text-base text-muted-foreground  truncate w-[700px] whitespace-nowrap min-w-0">
                            {checklist.zones
                              .map((zone) => z(zone))
                              .join(", ")}
                          </p>
                        </ZoneTooltip>
                      </div>
                      <div className="flex gap-2">
                        <div className="flex items-center">
                          <span className="material-symbols-outlined text-green text-xl">
                            verified_user
                          </span>
                          <span className="ml-1 text-green text-xl">
                            {checklist.itemCounts.safety || 0}
                          </span>
                        </div>
                        <div className="flex items-center">
                          <span className="material-symbols-outlined text-blue-500 text-xl">
                            psychiatry
                          </span>
                          <span className="ml-1 text-blue-500 text-xl">
                            {checklist.itemCounts.environment || 0}
                          </span>
                        </div>
                        <div className="flex items-center">
                          <span className="material-symbols-outlined text-destructive text-xl">
                            build
                          </span>
                          <span className="ml-1 text-destructive text-xl">
                            {checklist.itemCounts.maintenance || 0}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="sticky right-[0px]  flex flex-row items-end ">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild
                        className={
                          buttonVariants({
                            variant: "ghost",
                            size: "icon",
                          })}
                      >
                        <span className="material-symbols-outlined text-input">
                          more_vert
                        </span>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent
                        align="end"
                        onClick={(e) => {
                          e.stopPropagation();
                        }}
                        className="w-[80px] px-4 py-2"
                        side="bottom"
                      >
                        <div className="text-lg cursor-pointer ">
                          {t("Edit")}
                        </div>
                        <div
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeletePatrolChecklistDialog(checklist.id);
                          }}
                          className="text-destructive text-lg cursor-pointer hover:text-transparent-50"
                        >
                          {t("Delete")}
                        </div>
                        {isDialogOpen && dialogType === "delete" && (
                          <AlertCustom
                            title={a("ChecklistRemoveConfirmTitle")}
                            description={a("ChecklistRemoveConfirmDescription")}
                            primaryButtonText={t("Confirm")}
                            primaryIcon="check"
                            secondaryButtonText={t("Cancel")}
                            backResult={(result) => handleDialogResult(result)}
                          />
                        )}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-span-full min-h-[261px]">
              <NotFound
                icon="quick_reference_all"
                title="NoChecklistsAvailable"
                description="NoChecklistsDescription"
              />
            </div>
          )}
        </div>
      </ScrollArea>
    </div>
  );
}

