/**
 * :
 *   Preset   Preset   Preset  Zone  Date 
 * Input:
 * - 
 * Output:
 * -  Preset   Preset   Preset   Preset  Zone  Date 
 * -  Preset 
 * -  Preset 
 **/

"use client";
import { IPreset, IZone } from "@/app/type";
import Textfield from "@/components/textfield";
import { Button, buttonVariants } from "@/components/ui/button";
import { useLocale, useTranslations } from "next-intl";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Skeleton } from "@/components/ui/skeleton";
import { fetchData, formatTime, getInitials } from "@/lib/utils";

import { useRouter } from "next/navigation";
import React, { useEffect, useState } from "react";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";
import dynamic from "next/dynamic";
import { DatePickerWithRange } from "@/components/date-picker";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import {
  TooltipProvider,
  Tooltip,
  TooltipTrigger,
  TooltipContent,
} from "@radix-ui/react-tooltip";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { ScrollArea } from "@/components/ui/scroll-area";
import NotFound from "@/components/not-found";
import { UserTooltip } from "@/components/user-tooltip";
import { VersionTooltip } from "@/components/version-tooltip";
import { ZoneTooltip } from "@/components/zone-tooltip";
import { TextTooltip } from "@/components/text-tooltip";

const Map = dynamic(() => import("@/components/map"), { ssr: false });

export default function Page() {
  //
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const z = useTranslations("Zone");

  const router = useRouter();
  const locale = useLocale();
  interface IPresetWithExtras extends IPreset {
    updateByUserName: string;
    updateByUserImagePath: string;
  }

  //  API removePreset
  const removePreset = async (id: number) => {
    toast({
      variant: "success",
      title: a("PresetRemoveSuccessTitle"),
      description: a("PresetRemoveSuccessDescription"),
    });
    try {
      const response = await fetchData("delete", `/preset/${id}`, true);

      if (response) {
        setAllPreset((prevPresets) =>
          prevPresets.filter((preset) => preset.id !== id)
        );
        setFilteredPresets((prev) => prev.filter((preset) => preset.id !== id));
      } else {
        console.error("Failed to delete Preset: No response from API");
      }
    } catch (error) {
      console.error("An error occurred while deleting the preset:", error);
    }
  };

  const handleRemovePreset = (id: number) => {
    setPendingAction(() => () => removePreset(id)); //  Action 
    setDialogType("delete");
    setIsDialogOpen(true);
  };

  const [allPreset, setAllPreset] = useState<IPresetWithExtras[]>([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false); // State / Dialog
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null); //  Action 
  const [dialogType, setDialogType] = useState<string>("");

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
      setDialogType("");
    }
  };

  const handleCreatePreset = () => {
    router.push(`/${locale}/admin/settings/patrol-preset/create`);
  };

  const getData = async () => {
    try {
      // Construct query params for the filters
      const params = new URLSearchParams();

      // Add search term to params
      if (searchTerm) {
        params.append("search", searchTerm);
      }

      // Add selected zones to params
      if (selectedZones.length > 0) {
        params.append(
          "zones",
          selectedZones.map((zone) => zone.name).join(",")
        );
      }

      // Add date range to params
      if (selectedDateRange?.from) {
        params.append("startDate", selectedDateRange.from.toISOString());
      }
      if (selectedDateRange?.to) {
        params.append("endDate", selectedDateRange.to.toISOString());
      }

      // Fetch data using fetchData utility with query params
      const data = await fetchData(
        "get",
        `/presets?${params.toString()}`,
        true
      );

      // Validate data format
      if (!Array.isArray(data)) {
        console.error("Invalid data format:", data);
        return []; // Return empty array if data format is incorrect
      }

      setFilteredPresets(data); // Initially set filtered data to all data

      return data; // Return fetched data
    } catch (error) {
      console.error("Failed to fetch preset data:", error); // Log error
      return []; // Return empty array on error
    }
  };

  useEffect(() => {
    getData();
  }, []);

  // search bar
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [sortOption, setSortOption] = useState<"Name" | "ModifiedDate">("Name");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("asc");
  const [selectedZones, setSelectedZones] = useState<IZone[]>([]);
  const [tempSelectedZones, setTempSelectedZones] = useState<IZone[]>([]);
  const [selectedDateRange, setSelectedDateRange] = useState<{
    from?: Date;
    to?: Date;
  }>({});
  const [tempDateRange, setTempDateRange] = useState<{
    from?: Date;
    to?: Date;
  }>({});
  const [filteredPreset, setFilteredPresets] =
    useState<IPresetWithExtras[]>(allPreset);

  // Filter by search term
  const filterBySearchTerm = (presets: IPresetWithExtras[]) => {
    if (!searchTerm) return presets;

    const cleanSearch = searchTerm.toLowerCase().trim();

    return presets.filter((preset) => {
      const titleMatch = preset.title.toLowerCase().includes(cleanSearch);
      const descMatch = preset.description?.toLowerCase().includes(cleanSearch);
      return titleMatch || descMatch;
    });
  };
  // Fetch and apply search filter on search term change
  useEffect(() => {
    const fetchAndFilter = async () => {
      const freshData = await getData(); // Fetch fresh data
      const filtered = filterBySearchTerm(freshData); // Apply search term filter
      setFilteredPresets(filtered); // Update state with filtered data
    };
    fetchAndFilter();
  }, [searchTerm]); // Trigger fetch when search term changes

  // Apply filters (zones, date range, and search term)
  const applyFilters = async () => {
    const freshData = await getData(); // Fetch fresh data

    // Update selected filters
    setSelectedZones(tempSelectedZones);
    setSelectedDateRange(tempDateRange);

    // Apply filters for zones and date range
    const filtered = freshData.filter((preset) => {
      const matchesZones =
        tempSelectedZones.length === 0 || // If no zones are selected, show all
        preset.zones.some((zone) =>
          tempSelectedZones.some(
            (selectedZone) =>
              selectedZone.name.toLowerCase() === zone.name.toLowerCase()
          )
        );

      const matchesDateRange =
        (!tempDateRange?.from ||
          new Date(preset.updatedAt) >= tempDateRange.from) &&
        (!tempDateRange?.to || new Date(preset.updatedAt) <= tempDateRange.to);

      return matchesZones && matchesDateRange;
    });

    // Apply search term filter
    const filteredWithSearch = filterBySearchTerm(filtered);
    setFilteredPresets(filteredWithSearch); // Update the filtered state
  };

  const resetFilters = async () => {
    const data = await fetchData("get", `/presets`, true);
    setTempSelectedZones([]);
    setTempDateRange({});
    setSelectedZones([]);
    setSelectedDateRange({});
  };

  const [sortedPresets, setSortedPresets] = useState<IPresetWithExtras[]>([]);

  useEffect(() => {
    const sorted = [...filteredPreset].sort((a, b) => {
      let comparison = 0;

      if (sortOption === "Name") {
        comparison = a.title.localeCompare(b.title);
      } else if (sortOption === "ModifiedDate") {
        comparison =
          new Date(a.updatedAt).getTime() - new Date(b.updatedAt).getTime();
      }

      return sortOrder === "asc" ? comparison : -comparison;
    });

    setSortedPresets(sorted); // Update sorted checklists state
  }, [filteredPreset, sortOption, sortOrder]); // Dependencies to trigger the effect

  const handleEdit = (id: number) => {
    // Find the preset by id from the sortedPresets array
    const preset = sortedPresets.find((p) => p.id === id);

    // Redirect to the error page if preset or zones are missing
    if (!preset?.zones?.length || !preset.zones.every((zone) => zone.userId)) {
      router.push(
        `/${locale}/admin/settings/patrol-preset/error/unassinged-zone`
      );
    } else {
      // If all zones have a userId, navigate to the patrol preset page
      router.push(`/${locale}/admin/settings/patrol-preset/${id}`);
    }
  };

  return (
    <div className="flex flex-col gap-4 ">
      <div className="flex flex-row justify-between pt-2">
        <div className="text-2xl font-bold">{t("SettingsPatrolPreset")}</div>
        <Button
          variant="primary"
          className="flex flex-row gap-2"
          onClick={handleCreatePreset}
        >
          <span className="material-symbols-outlined text-2xl">add</span>
          <div className="text-lg">{t("CreatePreset")}</div>
        </Button>
      </div>
      <div className="flex items-center gap-2">
        <Textfield
          iconName="search"
          showIcon={true}
          placeholder={t("Search")}
          onChange={(e) => setSearchTerm(e.target.value)}
          value={searchTerm}
        />
        <DropdownMenu>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium `}
          >
            <span className="material-symbols-outlined">swap_vert</span>
            <div className="text-lg">{t("Sort")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="p-2 gap-2">
            {/* Sort By */}
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("SortBy")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sortOption}
              onValueChange={(value) =>
                setSortOption(value as "Name" | "ModifiedDate")
              }
            >
              <DropdownMenuRadioItem
                value="Name"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Name")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="ModifiedDate"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("DateModified")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>

            {/* Sort Order */}
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("Order")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sortOrder}
              onValueChange={(value) => setSortOrder(value as "desc" | "asc")}
            >
              <DropdownMenuRadioItem
                value="asc"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Ascending")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="desc"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Descending")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium`}
          >
            <span className="material-symbols-outlined">page_info</span>
            <div className="text-lg">{t("Filter")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="flex flex-col justify-center gap-2 p-2 z-50"
            align="end"
          >
            <div>
              <div>
                <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                  {t("Date")}
                </DropdownMenuLabel>
                <DatePickerWithRange
                  className="my-date-picker"
                  startDate={tempDateRange?.from || selectedDateRange?.from}
                  endDate={tempDateRange?.to || selectedDateRange?.to}
                  onSelect={(range) => {
                    if (range?.from || range?.to) {
                      setTempDateRange(range);
                    }
                  }}
                />
              </div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Zone")}
              </DropdownMenuLabel>
              <AlertDialog>
                <AlertDialogTrigger
                  asChild
                  className="flex items-center gap-2 rounded cursor-pointer"
                >
                  <Button
                    variant={"secondary"}
                    className="w-full h-[36px] rounded-md bg-secondary justify-start text-left gap-[13px] font-normal text-base pl-2 flex"
                  >
                    <span className="material-symbols-outlined ml-1 shrink-0">
                      location_on
                    </span>
                    <p className="truncate w-[200px]">
                      {selectedZones.length > 0
                        ? selectedZones.map((zone) => z(zone.name)).join(", ")
                        : t("SelectZones")}
                    </p>
                  </Button>
                </AlertDialogTrigger>

                <AlertDialogContent className="w-full sm:w-[40%] md:w-[50%] lg:w-[100%] max-w-[1200px] rounded-md">
                  <AlertDialogHeader>
                    <AlertDialogTitle className="text-2xl">
                      {t("FilterByZoneTitle")}
                    </AlertDialogTitle>
                    <AlertDialogDescription className="text-base">
                      {t("FilterByZoneDescription")}
                    </AlertDialogDescription>
                  </AlertDialogHeader>

                  <AlertDialogContent className="w-full sm:w-[50%] md:w-[70%] lg:w-[100%] max-w-[1200px] rounded-md px-6 py-4">
                    <div className="flex flex-col gap-1">
                      <div className="text-muted-foreground flex items-center gap-1">
                        <span className="material-symbols-outlined">
                          location_on
                        </span>
                        <p>{t("Zone")}</p>
                      </div>
                      <div className=" flex justify-center bg-secondary rounded-md py-4">
                        <Map
                          disable={false}
                          initialSelectedZones={selectedZones.map(
                            (zone) => zone.id
                          )}
                          onZoneSelect={(zones: IZone[]) =>
                            setTempSelectedZones(zones)
                          } //  tempSelectedZones
                        />
                      </div>
                    </div>

                    <AlertDialogFooter>
                      <AlertDialogCancel>{t("Cancel")}</AlertDialogCancel>
                      <AlertDialogAction
                        className="bg-primary"
                        onClick={() => {
                          //  Done  tempSelectedZones
                          setSelectedZones(tempSelectedZones);
                        }}
                      >
                        {t("Done")}
                      </AlertDialogAction>
                    </AlertDialogFooter>
                  </AlertDialogContent>
                </AlertDialogContent>
              </AlertDialog>
            </div>

            <div className="flex w-full justify-end mt-4 gap-2">
              <Button size="sm" variant="secondary" onClick={resetFilters}>
                {t("Reset")}
              </Button>
              <Button variant="primary" size="sm" onClick={applyFilters}>
                {t("Apply")}
              </Button>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
      <ScrollArea className="h-full overflow-hidden rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-290px)]">
        <div className="space-y-4">
          {(() => {
            return sortedPresets.length > 0 ? (
              sortedPresets.map((preset) => (
                <div
                  onClick={() => {
                    handleEdit(preset.id);
                  }}
                  key={preset.id}
                  className="bg-card rounded-lg custom-shadow p-4 cursor-pointer h-[219px]"
                >
                  {/* Title and Details */}
                  <div className="flex justify-between overflow-hidden text-ellipsis">
                    <div className="flex flex-col gap-4 min-w-0">
                      <div className="flex flex-col gap-1 justify-start items-start">
                        {/* Version with Tooltip */}
                        <VersionTooltip object={preset}>
                          <div className="flex justify-center">
                            <span className="material-symbols-outlined mr-1">
                              history
                            </span>
                            {t("Version")} {preset.version}
                          </div>
                        </VersionTooltip>
                        {/* Title */}
                        <h2 className="text-xl font-semibold truncate">
                          {preset.title}
                        </h2>
                      </div>
                      {/* Zones */}
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <span className="material-symbols-outlined">
                          location_on
                        </span>
                        <ZoneTooltip zones={preset.zones}>
                          <div className="text-base text-muted-foreground overflow-hidden text-ellipsis whitespace-nowrap w-[600px] truncate min-w-0">
                            {preset.zones && preset.zones.length > 0
                              ? preset.zones.map((zone, index) => (
                                <span
                                  key={index}
                                  className={
                                    zone.userId ? "" : "  text-destructive"
                                  }
                                >
                                  {z(zone.name)}
                                  {index < preset.zones.length - 1 && ", "}
                                </span>
                              ))
                              : z("NoZonesAvailable")}
                          </div>
                        </ZoneTooltip>
                      </div>
                      {/* Description */}
                      <div className="flex items-center gap-2 text-muted-foreground">
                        <span className="material-symbols-outlined">
                          data_info_alert
                        </span>
                        <TextTooltip object={preset.description}>

                          <div className="text-sm text-muted-foreground truncate w-[700px]">
                            {preset.description}
                          </div>
                        </TextTooltip>

                      </div>
                    </div>

                    {/* Action Buttons */}
                  </div>
                  <div className="flex flex-row justify-end items-end ">
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild
                        className={
                          buttonVariants({
                          variant: "ghost",
                          size: "icon",
                        })}
                      >
                        <span className="material-symbols-outlined text-input">
                          more_vert
                        </span>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent
                        align="end"
                        onClick={(e) => {
                          e.stopPropagation();
                        }}
                        className="w-[80px] px-4 py-2"
                        side="bottom"
                      >
                        <div
                          onClick={() => {
                            handleEdit(preset.id);
                          }}
                          key={preset.id}
                          className="text-lg cursor-pointer "
                        >
                          {t("Edit")}
                        </div>

                        <div
                          onClick={(e) => {
                            e.stopPropagation();
                            handleRemovePreset(preset.id);
                          }}
                          className="text-destructive text-lg cursor-pointer hover:text-transparent-50"
                        >
                          {t("Delete")}
                        </div>

                        {/*  AlertCustom  dialogType === "delete" */}
                        {isDialogOpen && dialogType === "delete" && (
                          <AlertCustom
                            title={a("PresetRemoveConfirmTitle")}
                            description={a("PresetRemoveConfirmDescription")}
                            primaryButtonText={t("Confirm")}
                            primaryIcon="check"
                            secondaryButtonText={t("Cancel")}
                            backResult={(result) => handleDialogResult(result)}
                          />
                        )}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-span-full min-h-[261px]">
                <NotFound
                  icon="quick_reference_all"
                  title="NoFoundPresetsAvailable"
                  description="NotFoundPresetsDescription"
                />
              </div>
            );
          })()}
        </div>
      </ScrollArea>
    </div>
  );
}

/**
 * :
 *  
 * Input:
 * - 
 * Output:
 * - 
 * -  
 * - 
 **/

"use client";
import { IFilterComment, IComment, itemType } from "@/app/type";
import BadgeCustom from "@/components/badge-custom";
import { DatePickerWithRange } from "@/components/date-picker";
import Loading from "@/components/loading";
import Textfield from "@/components/textfield";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectLabel,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  fetchData,
  getInitials,
  getItemTypeVariant,
  sortData,
} from "@/lib/utils";
import { useLocale, useTranslations } from "next-intl";
import React, { useEffect, useState } from "react";
import { DateRange } from "react-day-picker";
import { formatTime } from "@/lib/utils";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { buttonVariants } from "@/components/ui/button";
import { AlertCustom } from "@/components/alert-custom";
import { toast } from "@/hooks/use-toast";
import NotFound from "@/components/not-found";
import { ScrollArea } from "@/components/ui/scroll-area";
import { UserTooltip } from "@/components/user-tooltip";
import { TextTooltip } from "@/components/text-tooltip";

export default function Page() {
  const t = useTranslations("General");
  const s = useTranslations("Status");
  const z = useTranslations("Zone");
  const a = useTranslations("Alert");

  const [loading, setLoading] = useState<boolean>(true);
  const [isSortOpen, setIsSortOpen] = useState(false);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [allComments, setAllComments] = useState<IComment[]>([]);
  const locale = useLocale();

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
    }
  };

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const getAllComments = async () => {
    try {
      const queryString = buildQueryString(filter, searchTerm);
      const data = await fetchData("get", `/comments?${queryString}`, true);
      setAllComments(data);
    } catch (error) {
      console.error("Failed to fetch patrol data:", error);
    }
  };

  const handleConfirmComment = (id) => {
    setPendingAction(() => () => confirmComment(id));
    handleOpenDialog();
  };

  const confirmComment = async (id) => {
    try {
      await fetchData("put", `/comment/${id}`, true);
      setAllComments((prev) =>
        prev.map((comment) =>
          comment.id === id ? { ...comment, status: true } : comment
        )
      );
      toast({
        variant: "success",
        title: a("CommentConfirmSuccessTitle"),
        description: a("CommentConfirmSuccessDescription"),
      });
    } catch (error) {
      console.error("Error deleting patrol:", error);
    }
  };

  const handleSortChange = (type: string, value: string) => {
    setSort((prevSort) => ({
      ...prevSort,
      [type]: value,
    }));
  };

  const commentStatus = ["completed", "pending"];

  const initialFilter = {
    commentStatus: "All",
    dateRange: { start: undefined, end: undefined },
  };

  const getStoredFilter = () => {
    if (typeof window !== "undefined") {
      const storedFilter = localStorage.getItem("commentFilter");
      if (storedFilter) {
        return JSON.parse(storedFilter);
      }
    }
    return initialFilter;
  };

  const [filter, setFilter] = useState<IFilterComment | null>(
    getStoredFilter()
  );

  const [sort, setSort] = useState<{ by: string; order: string }>({
    by: "CommentDate",
    order: "Descending",
  });

  const applyFilter = () => {
    getAllComments();
  };

  const resetFilter = () => {
    setFilter(initialFilter);
  };

  const handleDateSelect = (dateRange: DateRange) => {
    const startDate = dateRange.from ?? null;
    const endDate = dateRange.to
      ? new Date(new Date(dateRange.to).setHours(23, 59, 59, 999))
      : null;
    setFilter({
      commentStatus: filter?.commentStatus || null,
      dateRange: {
        start: startDate || undefined,
        end: endDate || undefined,
      },
    });
  };

  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (event: any) => {
    setSearchTerm(event.target.value);
  };

  const buildQueryString = (
    filter: IFilterComment | null,
    searchTerm: string
  ) => {
    const params: Record<string, string | undefined> = {};

    //  search term 
    if (searchTerm) params.search = searchTerm;

    //  status filter  "All"
    if (filter?.commentStatus && filter.commentStatus !== "All") {
      params.status = filter.commentStatus === "completed" ? "true" : "false";
    }

    //  startDate
    if (filter?.dateRange?.start) {
      params.startDate = filter?.dateRange.start.toISOString();
    }

    //  endDate
    if (filter?.dateRange?.end) {
      params.endDate = filter?.dateRange?.end.toISOString();
    }

    return new URLSearchParams(params).toString();
  };

  useEffect(() => {
    getAllComments();
    setLoading(false);
  }, []);

  useEffect(() => {
    getAllComments();
  }, [searchTerm]);

  useEffect(() => {
    localStorage.setItem("commentFilter", JSON.stringify(filter));
  }, [filter]);

  useEffect(() => {
    const sortedData = sortData(allComments, sort);
    if (JSON.stringify(sortedData) !== JSON.stringify(allComments)) {
      setAllComments(sortedData);
    }
  }, [sort, allComments]);

  if (loading) {
    return <Loading />;
  }

  return (
    <div className="flex flex-col gap-4">
      <div className="flex items-center gap-2">
        <Textfield
          iconName="search"
          showIcon={true}
          placeholder={t("Search")}
          onChange={handleSearch}
        />

        <DropdownMenu onOpenChange={(open) => setIsSortOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
          ${isSortOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">swap_vert</span>
            <div className="text-lg">{t("Sort")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="p-2 gap-2">
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("SortBy")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.by}
              onValueChange={(value) => handleSortChange("by", value)}
            >
              <DropdownMenuRadioItem
                value="CommentDate"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Date")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="Status"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Status")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>

            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("Order")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.order}
              onValueChange={(value) => handleSortChange("order", value)}
            >
              <DropdownMenuRadioItem
                value="Ascending"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Ascending")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="Descending"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Descending")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu onOpenChange={(open) => setIsFilterOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
          ${isFilterOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">page_info</span>
            <div className="text-lg">{t("Filter")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="flex flex-col justify-center gap-2 p-2 z-50"
            align="end"
          >
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Date")}
              </DropdownMenuLabel>
              <DatePickerWithRange
                startDate={filter?.dateRange.start}
                endDate={filter?.dateRange.end}
                onSelect={handleDateSelect}
                className="my-date-picker"
              />
            </div>
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Status")}
              </DropdownMenuLabel>
              <Select
                value={filter?.commentStatus || "All"}
                onValueChange={(value) =>
                  setFilter({
                    commentStatus: value,
                    dateRange: {
                      start: filter?.dateRange.start,
                      end: filter?.dateRange.end,
                    },
                  })
                }
              >
                <SelectTrigger className="">
                  <SelectValue
                    placeholder={
                      filter?.commentStatus === "All"
                        ? t("All")
                        : filter?.commentStatus
                    }
                  />
                </SelectTrigger>
                <SelectContent>
                  <SelectGroup>
                    <SelectLabel>{t("Status")}</SelectLabel>
                    <SelectItem value="All">{t("All")}</SelectItem>
                    {commentStatus.map((status) => (
                      <SelectItem value={status} key={status}>
                        {s(status)}
                      </SelectItem>
                    ))}
                  </SelectGroup>
                </SelectContent>
              </Select>
            </div>
            <div className="flex w-full justify-end mt-4 gap-2">
              <Button size="sm" variant="secondary" onClick={resetFilter}>
                {t("Reset")}
              </Button>
              <Button size="sm" variant="primary" onClick={applyFilter}>
                {t("Apply")}
              </Button>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
      <Table>
        <TableHeader>
          <TableRow className="grid grid-cols-12 w-full">
            <TableHead className="sm:col-span-3 lg:col-span-5">
              {t("Message")}
            </TableHead>
            <TableHead className="sm:col-span-2 lg:col-span-2">
              {t("Date")}
            </TableHead>
            <TableHead className="sm:col-span-3 lg:col-span-2">
              {t("Status")}
            </TableHead>
            <TableHead className="sm:col-span-3 lg:col-span-2">
              {t("inspector")}
            </TableHead>
            <TableHead className="sm:col-span-1 lg:col-span-1"></TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          <ScrollArea className="rounded-md w-full [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-160px)]">
            {allComments?.length === 0 ? (
              <tr className="flex w-full h-full">
                <td colSpan={5} className="w-full text-center py-6">
                  <NotFound
                    icon="chat"
                    title="NoCommentsFound"
                    description="NoCommentsDescription"
                  />
                </td>
              </tr>
            ) : (
              allComments.map((comment, index) => (
                <TableRow key={index} className="grid grid-cols-12">
                  <TableCell className="font-medium sm:col-span-3 lg:col-span-5">
                    <TextTooltip object={comment.message}>
                      <div className=" truncate max-w-[400px]">{comment.message}</div>
                    </TextTooltip>
                  </TableCell>
                  <TableCell className="font-medium sm:col-span-2 lg:col-span-2">
                    {formatTime(comment.timestamp, locale)}
                  </TableCell>
                  <TableCell className="font-medium sm:col-span-3 lg:col-span-2">
                    <BadgeCustom
                      variant={comment.status === false ? "blue" : "green"}
                      iconName={
                        comment.status === false ? "hourglass_top" : "check"
                      }
                      showIcon={true}
                    >
                      {s(comment.status === false ? "pending" : "completed")}
                    </BadgeCustom>
                  </TableCell>
                  <TableCell className="font-medium sm:col-span-3 lg:col-span-2 flex flex-row gap-2 items-center">
                    {comment.user.profile.name ? (
                      <UserTooltip user={comment.user}>
                        <Avatar>
                          <AvatarImage
                            src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${comment.user.profile.image?.path}`}
                          />
                          <AvatarFallback id={comment.user.id.toString()}>
                            {getInitials(comment.user.profile.name)}
                          </AvatarFallback>
                        </Avatar>
                      </UserTooltip>
                    ) : (
                      <Skeleton className="h-12 w-12 rounded-full bg-input" />
                    )}
                    <div>
                      {comment.user.profile.name ? (
                        comment.user.profile.name
                      ) : (
                        <div className="text-destructive">
                          {comment.user.username}
                          <div className="text-[14px]">
                            No profile is provided
                          </div>
                        </div>
                      )}
                    </div>
                  </TableCell>
                  <TableCell>
                    <DropdownMenu>
                      <DropdownMenuTrigger onClick={(e) => e.stopPropagation()}>
                        <Button variant="ghost" className="w-[45px] h-[45px]">
                          <span className="material-symbols-outlined items-center text-input">
                            more_horiz
                          </span>
                        </Button>
                      </DropdownMenuTrigger>

                      <DropdownMenuContent align="end" className="p-0">
                        <DropdownMenuItem className="p-0">
                          <AlertDialog>
                            <AlertDialogTrigger
                              asChild
                              className="pl-2 py-2"
                              onClick={(e) => e.stopPropagation()}
                            >
                              <div
                                className="cursor-pointer w-full h-full flex"
                                onClick={(e) => {
                                  e.stopPropagation();
                                }}
                              >
                                {t("Detail")}
                              </div>
                            </AlertDialogTrigger>
                            <AlertDialogContent className="px-6 py-4 gap-4">
                              <div className="flex flex-col gap-2">
                                <p className="text-lg font-semibold text-muted-foreground">
                                  {formatTime(comment.timestamp, locale)}
                                </p>
                                <p className="text-2xl font-bold text-card-foreground">
                                  {
                                    comment.patrolResult.itemZone.item.checklist
                                      .title
                                  }
                                </p>
                              </div>
                              <div className="flex flex-col gap-2">
                                <p className="text-xl text-muted-foreground">
                                  {comment.patrolResult.itemZone.item.name}
                                </p>
                                <div className="flex items-center gap-1 text-base text-muted-foreground">
                                  <span className="material-symbols-outlined">
                                    location_on
                                  </span>
                                  <p>
                                    {z(comment.patrolResult.itemZone.zone.name)}
                                  </p>
                                </div>
                              </div>
                              <BadgeCustom
                                variant={
                                  getItemTypeVariant(
                                    comment.patrolResult.itemZone.item
                                      .type as itemType
                                  ).variant
                                }
                                shape="square"
                                showIcon={true}
                                iconName={
                                  getItemTypeVariant(
                                    comment.patrolResult.itemZone.item
                                      .type as itemType
                                  ).iconName
                                }
                              >
                                {s(comment.patrolResult.itemZone.item.type)}
                              </BadgeCustom>

                              <AlertDialogFooter>
                                <AlertDialogCancel
                                  onClick={(e) => e.stopPropagation()}
                                >
                                  {t("Close")}
                                </AlertDialogCancel>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </DropdownMenuItem>
                        {isDialogOpen && (
                          <AlertCustom
                            title={a("ConfirmCommentTitle")}
                            description={a("ConfirmCommentDescription")}
                            primaryButtonText={t("Confirm")}
                            primaryIcon="check"
                            primaryVariant="primary"
                            secondaryButtonText={t("Cancel")}
                            backResult={handleDialogResult}
                          ></AlertCustom>
                        )}
                        <DropdownMenuItem
                          className="cursor-pointer w-full h-full flex"
                          disabled={comment.status === true}
                          onClick={(e) => {
                            e.preventDefault();
                            handleConfirmComment(comment.id);
                            e.stopPropagation();
                          }}
                        >
                          <p className="text-primary">{t("Resolve")}</p>
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))
            )}
          </ScrollArea>
        </TableBody>
      </Table>
    </div>
  );
}

'use client'

import React, { useEffect, useState } from 'react'
import loginCover3 from "@/public/assets/img/login_cover_3.png"
import Image from 'next/image'
import Textfield from '@/components/textfield'
import { toast } from '@/hooks/use-toast'
import { useLocale, useTranslations } from 'next-intl'
import { fetchData } from '@/lib/utils'
import ModeToggle from '@/components/mode-toggle'
import { useTheme } from 'next-themes'
import lightLogo from "@/public/assets/img/system_logo_light.png"
import darkLogo from "@/public/assets/img/system_logo_dark.png"
import { Button } from '@/components/ui/button'
import Loading from '@/components/loading'
import { useRouter, useSearchParams } from 'next/navigation'
import { AlertCustom } from '@/components/alert-custom'
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

export default function page() {
    const t = useTranslations('General')
    const a = useTranslations("Alert");
    const [newPassword, setNewPassword] = useState<string | null>(null)
    const [confirmPassword, setConfirmPassword] = useState<string | null>(null)
    const [mounted, setMounted] = useState(false);
    const searchParams = useSearchParams();
    const [token, setToken] = useState<string | null>(null);
    const router = useRouter()
    const locale = useLocale();
    const { resolvedTheme } = useTheme()

    const [newPassError, setNewPassError] = useState<string | null>(null);
    const [confirmPasswordError, setConfirmPasswordError] = useState<string | null>(null);
    const [isValidToken, setIsValidToken] = useState<boolean | null>(null)

    const [isAlertOpen, setIsAlertOpen] = useState(false)
    const handleOpenAlert = () => setIsAlertOpen(true);
    const handleCloseAlert = () => setIsAlertOpen(false)

    const handleInputNewPassword = (
        e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
    ) => {
        const { value } = e.target;
        setNewPassword(value)
    };

    const handleInputConfirmPassword = (
        e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
    ) => {
        const { value } = e.target;
        setConfirmPassword(value)
    };

    const handleUpdateUserPassword = async () => {
        let showErrorToast = false;
        setNewPassError(null);
        setConfirmPasswordError(null);

        // 
        if (!newPassword) {
            setNewPassError(a("ProfileNewPassRequire"));
            showErrorToast = true;
        }

        // 
        if (!confirmPassword) {
            setConfirmPasswordError(a("ProfileConfirmPassRequire"));
            showErrorToast = true;
        }

        //  ( 8 )
        if (newPassword && newPassword.length < 8) {
            setNewPassError(a("ProfileNewPassMinLength"));
            showErrorToast = true;
        }

        if (confirmPassword && confirmPassword.length < 8) {
            setConfirmPasswordError(a("ProfileNewPassMinLength"));
            showErrorToast = true;
        }

        // 
        if (newPassword && confirmPassword && newPassword !== confirmPassword) {
            setConfirmPasswordError(a("ProfileConfirmPassInvalid"));
            showErrorToast = true;
        }

        //  toast 
        if (showErrorToast) {
            toast({
                variant: "error",
                title: a("ProfileUpdateErrorTitle"),
                description: a("ProfileUpdateErrorDescription"),
            });
            return;
        }

        // 
        const userForm = new FormData();
        userForm.append("newPassword", newPassword);
        userForm.append("token", token)

        try {
            const response = await fetchData(
                "put",
                "/reset-password",
                true,
                userForm
            );
            if (response.status === 200) {
                setNewPassError(null);
                setConfirmPasswordError(null);

                toast({
                    variant: "success",
                    title: a("PasswordResetCompletedTitle"),
                    description: a("PasswordResetCompletedDescription"),
                });
                router.push(`/${locale}/login`);
            }

        } catch (error) {
            console.error("Error updating password:", error);
            toast({
                variant: "error",
                title: a("ProfileUpdateErrorTitle"),
                description: a("ProfileUpdateErrorDescription"),
            });
        }
    };

    const changeLanguage = (locale: string) => {
        if (mounted) {
            const urlParams = new URLSearchParams(window.location.search);
            const token = urlParams.get('token');

            let newPath = `/${locale}/login/forgot-password`;
            if (token) {
                newPath += `?token=${token}`;
            }

            router.replace(newPath);
            router.refresh();
        }
    };

    const verifyToken = async (token: string) => {
        try {
            const response = await fetchData("get", `/verify-token?token=${token}`, true);

            if (response.status === "200" || response.status === 200) {
                setIsValidToken(true);
            } else {
                setIsValidToken(false);
                toast({
                    variant: "error",
                    title: (a("InvalidTokenTitle")),
                    description: (a("InvalidTokenDescription"))
                });
                router.push(`/${locale}/login`);
            }
        } catch (error) {
            console.error('Error verifying token:', error);
            setIsValidToken(false);
            router.push(`/${locale}/login`);
        }
    };

    useEffect(() => {
        const tokenFromURL = searchParams.get('token');
        if (tokenFromURL) {
            setToken(tokenFromURL);
            verifyToken(tokenFromURL);
        } else {
            router.push(`/${locale}/login`);
        }
    }, []);

    useEffect(() => {
        changeLanguage(locale)
        setMounted(true)
    }, [])

    if (!mounted) {
        return <Loading />;
    }

    return (
        <div className="flex flex-col min-h-screen justify-center items-center w-full">
            {/* header */}
            <div className=" flex flex-col w-full justify-center items-center p-4">
                <div className="flex flex-row w-full">
                    <div onClick={() => (router.push(`/${locale}/login`))}>
                        <Image
                            className="flex items-center"
                            src={resolvedTheme === 'dark' ? darkLogo : lightLogo}
                            alt="Logo"
                            width={250}
                            height={250}
                            priority
                        />
                    </div>
                    <div className="flex w-full justify-end gap-4">
                        <ModeToggle />

                        <DropdownMenu>
                            <DropdownMenuTrigger>
                                <Button variant='ghost' className="w-[45px] h-[45px] text-input">
                                    <span className="material-symbols-outlined">language</span>
                                </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent align="end" className='p-0'>
                                <DropdownMenuItem onClick={() => changeLanguage('en')}>
                                    {t('English')}
                                </DropdownMenuItem>
                                <DropdownMenuItem onClick={() => changeLanguage('th')}>
                                    {t('Thai')}
                                </DropdownMenuItem>
                            </DropdownMenuContent>
                        </DropdownMenu>
                    </div>
                </div>
            </div>

            {/* second block */}
            <div className="flex flex-col justify-center items-center gap-4 flex-grow w-full h-full">
                <Image
                    src={loginCover3}
                    alt="First Image"
                    width={100}
                    height={100}
                    className="rounded-md object-cover w-[250px] h-[250px]"
                    unoptimized
                />
                <div className="text-2xl font-bold">
                    <p>{t("ResetPassword")}</p>
                </div>
                <div className="flex flex-col gap-4 lg:w-[30%] sm:w-[45%]">
                    <div className="flex flex-col gap-1 w-full">
                        <p>{t("NewPassword")}</p>
                        <Textfield
                            name="newPassword"
                            className="text-xl font-normal bg-secondary"
                            type="password"
                            showIcon={true}
                            iconName="key"
                            placeholder=""
                            onChange={handleInputNewPassword}
                        />
                        {newPassError && (
                            <p className="text-sm font-light text-destructive italic mt-1">
                                {newPassError}
                            </p>
                        )}
                    </div>
                    <div className="flex flex-col gap-1 w-full">
                        <p>{t("ConfirmPassword")}</p>
                        <Textfield
                            name="confirmNewPassword"
                            className="text-xl font-normal bg-secondary"
                            type="password"
                            showIcon={true}
                            iconName="key"
                            placeholder=""
                            onChange={handleInputConfirmPassword}
                        />
                        {confirmPasswordError && (
                            <p className="text-sm font-light text-destructive italic mt-1">
                                {confirmPasswordError}
                            </p>
                        )}
                    </div>
                    <Button className='w-full' variant="default" size="lg" onClick={() => handleOpenAlert()}>{t("Reset")}</Button>
                </div>
            </div>

            {isAlertOpen && (
                <AlertCustom
                    title={a("ResetPasswordConfirmTitle")}
                    description={a("ResetPasswordConfirmDescription")}
                    primaryButtonText={t("Confirm")}
                    primaryIcon="check"
                    secondaryButtonText={t("Cancel")}
                    backResult={(backResult) => {
                        if (backResult) {
                            handleUpdateUserPassword()
                        }
                        handleCloseAlert()
                    }}
                />
            )}
        </div>
    );
}

/**
 * :
 *   Login   username, password  checkbox 
 * Input: 
 * - 
 * Output:
 * -  Login  username, password  checkbox 
 * -  Alert 
 * -  Alert 
 **/

'use client'
import { useTransition, useEffect, useState } from 'react';
import lightLogo from "@/public/assets/img/system_logo_light.png"
import darkLogo from "@/public/assets/img/system_logo_dark.png"
import loginCover1 from "@/public/assets/img/login_cover_1.png"
import loginCover2 from "@/public/assets/img/login_cover_2.png"
import loginCover3 from "@/public/assets/img/login_cover_3.png"
import Image from 'next/image'
import LanguageSelect from '@/components/language-select';
import ModeToggle from '@/components/mode-toggle';
import Textfield from '@/components/textfield';
import { Button, buttonVariants } from '@/components/ui/button';
import { useLocale, useTranslations } from "next-intl";
import { Checkbox } from '@/components/ui/checkbox';
import { z } from "zod"
import { zodResolver } from "@hookform/resolvers/zod"
import { useForm } from "react-hook-form"
import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
} from "@/components/ui/alert-dialog";

import {
    Form,
    FormControl,
    FormField,
    FormItem,
    FormMessage,
} from "@/components/ui/form"

import { fetchData, login } from '@/lib/utils';
import { Carousel, CarouselContent, CarouselItem } from '@/components/ui/carousel';
import EmblaAutoplay from 'embla-carousel-autoplay'
import { useTheme } from 'next-themes';
import { LoginSchema } from '@/app/type';
import { toast } from '@/hooks/use-toast';
import { useRouter } from 'next/navigation';
import { Input } from '@/components/ui/input';
import { AlertCustom } from '@/components/alert-custom';

export default function LoginPage() {
    const { resolvedTheme } = useTheme()
    const [mounted, setMounted] = useState(false)
    const t = useTranslations('General')
    const a = useTranslations("Alert");
    const [isPending, startTransition] = useTransition()
    const autoplayOptions = EmblaAutoplay({ delay: 3000, stopOnInteraction: false });
    const router = useRouter();
    const locale = useLocale()
    const [email, setEmail] = useState<string>(null)
    const [isAlertOpen, setIsAlertOpen] = useState(false)
    const [isConfirmAlertOpen, setIsConfirmAlertOpen] = useState(false)
    const [emailError, setEmailError] = useState<string | null>(null);

    const form = useForm<z.infer<typeof LoginSchema>>({
        resolver: zodResolver(LoginSchema),
        defaultValues: {
            username: "",
            password: "",
            rememberMe: false
        },
    })

    const handleInputEmail = (
        e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
    ) => {
        const { value } = e.target;
        setEmail(value)
    };

    const handleOpenAlert = () => setIsAlertOpen(true);
    const handleCloseAlert = () => {
        setIsAlertOpen(false)
        setEmailError(null)
        setEmail(null)
    };
    const handleOpenConfirmAlert = () => setIsConfirmAlertOpen(true)
    const handleCloseConfirmAlert = () => setIsConfirmAlertOpen(false)

    useEffect(() => {
        setMounted(true)
    }, [])

    function onSubmit(values: z.infer<typeof LoginSchema>) {
        startTransition(async () => {
            const result = await login(values)
            if (result.accessToken && result.refreshToken) {
                toast({
                    variant: "success",
                    title: a("LoginSuccessTitle"),
                    description: a("LoginSuccessDescription"),
                });
                router.push(`/${locale}`);
            } else if (result.error === "Too many login attempts, please try again later") {
                toast({
                    variant: "error",
                    title: a("LoginTooManyTitle"),
                    description: a("LoginTooManyDescription"),
                });
            } else {

                //  Toast  Error
                toast({
                    variant: "error",
                    title: a("LoginErrorTitle"),
                    description: a("LoginErrorDescription"),
                });
            }
        });
    }

    const sendForgotPasswordEmail = async (email: string) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        if (!emailRegex.test(email)) {
            setEmailError(a("InvalidEmailInlineValidate"))
            toast({
                variant: "error",
                title: (a("InvalidEmailTitle")),
                description: (a("InvalidEmailDescription")),
            });
            return;
        }

        try {
            const response = await fetchData("post", `/send-email-reset-password`, true, { email });
            toast({
                variant: "success",
                title: (a("ResetLinkCompletedTitle")),
                description: (a("ResetLinkCompletedDescription")),
            });
            setEmailError(null)
            setEmail(null)
            handleCloseAlert()
            return response
        } catch (error) {
            console.error(error)
        }
    }

    if (!mounted) {
        return null
    }

    return (
        <section className="bg-card flex lg:grid lg:grid-cols-2 h-screen p-2">
            {/*  sm  */}
            <div className="bg-background hidden lg:inline w-full p-4 rounded-md">
                <Image
                    className="flex items-center "
                    src={resolvedTheme === 'dark' ? darkLogo : lightLogo}
                    alt="Logo"
                    width={250}
                    height={250}
                    priority
                />
                <div className="flex justify-center items-center h-full">
                    <Carousel plugins={[autoplayOptions]} opts={{ loop: true }}>
                        <CarouselContent>
                            <CarouselItem>
                                <Image
                                    className="flex items-center"
                                    src={loginCover1}
                                    alt="Cover"
                                    width={1000}
                                    height={1000}
                                    priority
                                />
                            </CarouselItem>
                            <CarouselItem>
                                <Image
                                    className="flex items-center"
                                    src={loginCover2}
                                    alt="Cover"
                                    width={1000}
                                    height={1000}
                                    priority
                                />
                            </CarouselItem>
                            <CarouselItem>
                                <Image
                                    className="flex items-center"
                                    src={loginCover3}
                                    alt="Cover"
                                    width={1000}
                                    height={1000}
                                    priority
                                />
                            </CarouselItem>
                        </CarouselContent>
                    </Carousel>
                </div>
            </div>

            {/*  username, password  */}
            <div className="flex flex-col w-full justify-center items-center p-4">
                <div className='flex w-full'>
                    <div className='lg:hidden'>
                        <Image
                            className="absolute"
                            src={resolvedTheme === 'dark' ? darkLogo : lightLogo}
                            alt="Logo"
                            width={250}
                            height={250}
                            priority
                        />
                    </div>
                    <div className='flex w-full justify-end gap-4'>
                        <ModeToggle />
                        <LanguageSelect />
                    </div>
                </div>
                <div className='gap-2 flex flex-col justify-center items-center w-full lg:w-[450px] h-full'>
                    <div className='flex flex-col gap-4 w-[440px]'>
                        <div className='flex flex-col gap-1 items-start'>
                            <h1 className='text-[40px] font-semibold'>{t('Login')}</h1>
                            <p className='text-base'>{t('EnterCredentials')}</p>
                        </div>
                        <Form {...form}>
                            <form onSubmit={form.handleSubmit(onSubmit)} className='w-full'>
                                <FormField
                                    control={form.control}
                                    name="username"
                                    render={({ field }) => (
                                        <FormItem className='mt-2'>
                                            <div className='flex flex-col gap-1 items-start'>
                                                <label className='text-sm font-semibold text-muted-foreground'>{t('Username')}</label>
                                                <FormControl>
                                                    <Textfield className='bg-secondary' showIcon={true} iconName='person' placeholder='johnDoe' {...field} />
                                                </FormControl>
                                                <FormMessage />
                                            </div>
                                        </FormItem>
                                    )}
                                />
                                <FormField
                                    control={form.control}
                                    name="password"
                                    render={({ field }) => (
                                        <FormItem className='mt-6'>
                                            <div className='flex flex-col gap-1 items-start'>
                                                <label className='text-sm font-semibold text-muted-foreground'>{t('Password')}</label>
                                                <FormControl>
                                                    <Textfield className='bg-secondary' type='password' showIcon={true} iconName='lock' placeholder='verySecure' {...field} />
                                                </FormControl>
                                                <FormMessage />
                                            </div>
                                        </FormItem>
                                    )}
                                />
                                <div className='flex flex-col gap-6 mt-6 items-center'>
                                    <div className="flex justify-between items-center w-full">
                                        <FormField
                                            control={form.control}
                                            name="rememberMe"
                                            render={({ field }) => (
                                                <FormItem>
                                                    <div className="w-full h-full flex gap-2 items-center">
                                                        <FormControl>
                                                            <Checkbox id="terms1" checked={field.value} onCheckedChange={field.onChange} />
                                                        </FormControl>
                                                        <label
                                                            htmlFor="terms1"
                                                            className="text-sm font-medium text-muted-foreground leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 hover:cursor-pointer"
                                                        >
                                                            {t('RememberMe')}
                                                        </label>
                                                    </div>
                                                </FormItem>
                                            )}
                                        />
                                        <Button type="button" className='text-sm font-medium' variant='link' onClick={() => handleOpenAlert()}>{t('ForgotPassword')}</Button>
                                    </div>
                                    <Button className='w-full' size="lg" disabled={isPending}>
                                        {t('Login')}
                                        <span className="material-symbols-outlined">
                                            login
                                        </span>
                                    </Button>
                                </div>
                            </form>
                        </Form>
                    </div>
                </div>
            </div>

            <AlertDialog open={isAlertOpen}>
                <AlertDialogContent className="sm:w-[90%] xl:w-[60%] h-fit px-6 py-4">
                    <AlertDialogHeader>
                        <div className="flex flex-col justify-center items-center gap-4">
                            <AlertDialogTitle className="flex flex-col justify-center items-center text-2xl font-bold text-card-foreground">
                                <Image
                                    src={loginCover3} //  path  state
                                    alt="First Image"
                                    width={100}
                                    height={100}
                                    className="rounded-md object-fit cursor-pointer w-[250px] h-[250px]"
                                    unoptimized
                                />
                                <p>{a("ForgotPasswordTitle")}</p>
                            </AlertDialogTitle>
                            <AlertDialogDescription className="text-base text-input">
                                <p>{a("ForgotPasswordDescription")}</p>
                            </AlertDialogDescription>
                        </div>
                    </AlertDialogHeader>

                    <div className="flex flex-col">
                        <Textfield
                            name="confirmNewPassword"
                            className="text-base bg-secondary"
                            type="email"
                            showIcon={true}
                            iconName="mail"
                            placeholder="supervisor@gmail.com"
                            onChange={handleInputEmail}
                        />

                        {emailError && (
                            <p className="text-sm font-light text-destructive italic mt-1">
                                {emailError}
                            </p>
                        )}
                    </div>

                    <AlertDialogFooter>
                        <div className="flex items-end justify-end gap-2">
                            <AlertDialogCancel onClick={() => {
                                handleCloseAlert()
                            }}>
                                {t('Cancel')}
                            </AlertDialogCancel>
                            <AlertDialogAction
                                className={buttonVariants({ variant: 'primary', size: 'lg' })}
                                onClick={() => handleOpenConfirmAlert()}
                            >
                                <span className="material-symbols-outlined text-card w-[22px] h-[22px] items-center">
                                    send
                                </span>
                                {t('Send')}
                            </AlertDialogAction>
                        </div>
                    </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>

            {isConfirmAlertOpen && (
                <AlertCustom
                    title={a("ForgotPasswordConfirmTitle")}
                    description={a("ForgotPasswordConfirmDescription")}
                    primaryButtonText={t("Confirm")}
                    primaryIcon="check"
                    secondaryButtonText={t("Cancel")}
                    backResult={(backResult) => {
                        if (backResult) {
                            sendForgotPasswordEmail(email)
                        }
                        handleCloseConfirmAlert()
                    }}
                />
            )}

        </section>
    );
}

/**
 * :
 *   Patrol  Patrol 
 *
 * Input:
 * - 
 * Output:
 * -  Patrol  Patrol 
 * -  Progress Bar   "Finish"  "Start"  Patrol
 * -  Patrol
 * -  Alert  Patrol
 **/

"use client";
import { IDefect, IPatrolChecklist, patrolStatus } from "@/app/type";
import BadgeCustom from "@/components/badge-custom";
import PatrolChecklist from "@/components/patrol-checklist";
import PatrolTimer from "@/components/patrol-timer";
import TabMenu from "@/components/tab-menu";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { usePatrol } from "@/context/patrol-context";
import { exportData, getInitials, getPatrolStatusVariant } from "@/lib/utils";
import { useLocale, useTranslations } from "next-intl";
import { useParams, useRouter } from "next/navigation";
import { AlertCustom } from "@/components/alert-custom";
import { toast } from "@/hooks/use-toast";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  HoverCard,
  HoverCardContent,
  HoverCardTrigger,
} from "@/components/ui/hover-card";
import { CardFooter } from "@/components/ui/card";
import {
  AlertDialog,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogCancel,
  AlertDialogAction,
  AlertDialogFooter,
} from "@/components/ui/alert-dialog";
import React, { useRef, useState } from "react";
import { ScrollArea } from "@/components/ui/scroll-area";
import { UserTooltip } from "@/components/user-tooltip";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";

export default function Page() {
  const {
    patrol,
    patrolResults,
    user,
    lock,
    isAlertOpen,
    canFinish,
    countDefects,
    countFails,
    countItems,
    patrolUser,
    isHovered,
    formatDate,
    formatId,
    formatTimeDate,
    formatZone,
    handleMouseEnter,
    handleMouseLeave,
    itemCounts,
    toggleLock,
    calculateProgress,
    handleResult,
    fetchRealtimeData,
    handleStartPatrol,
    handleFinishPatrol,
    handleOpenDialog,
    handleCloseDialog,
  } = usePatrol();
  const locale = useLocale();
  const params = useParams();
  const id = params.id;
  const router = useRouter();
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const s = useTranslations("Status");
  const [open, setOpen] = useState(false);
  const openType = useRef<"hover" | "click" | null>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const triggerContainerRef = useRef<HTMLDivElement>(null);

  itemCounts(patrol, patrolResults);
  const inspectors = patrolUser;
  const handleClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    //  timeout  hover 
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
      timeoutRef.current = null;
    }
    openType.current = "click";
    setOpen(true); //  Popover 
  };

  const handleClose = () => {
    setOpen(false);
    openType.current = null;
  };

  const TooltipContent = () => {
    return (
      <div >
        <div className="flex items-center justify-center gap-1">
          <span className="material-symbols-outlined">person_search</span>
          <p className="text-lg font-semibold">{t("InspectorList")}</p>
        </div>
        {inspectors.map((inspector, idx) => (
          <div
            key={idx}
            className="flex items-center w-full py-2 gap-1 border-b-2 border-secondary"
          >
            <UserTooltip user={inspector}>
              <Avatar className="custom-shadow ms-[-10px] me-2.5">
                <AvatarImage
                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                />
                <AvatarFallback id={inspector.id.toString()}>
                  {getInitials(inspector.profile.name)}
                </AvatarFallback>
              </Avatar>
            </UserTooltip>
            <p className="text-lg truncate">{inspector.profile.name}</p>
          </div>
        ))}
        <div className="flex items-center justify-between w-full text-muted-foreground">
          <p className="text-lg font-semibold">{t("Total")}</p>
          <p className="text-lg font-semibold">{inspectors.length}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="flex flex-col gap-4 overflow-hidden">
      {/* TabList  Title */}
      <div className="flex flex-col gap-4 bg-background">
        <div className="flex sm:flex-col lg:flex-row lg:items-center justify-between sm:items-start">
          <div className="flex items-center p-0 justify-center text-center gap-2">
            <AlertDialog>
              <AlertDialogTrigger asChild>
                <Button variant="ghost" className="flex hover:bg-secondary p-2">
                  <span className="material-symbols-outlined text-card-foreground w-[22px] h-[22px]">
                    error
                  </span>
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent className="w-[400px] h-fit px-6 py-4 ">
                <AlertDialogHeader className="flex flex-col gap-2">
                  <div className="flex flex-col gap-1">
                    <p className="text-lg font-semibold text-muted-foreground">
                      {formatDate(patrol.date)}
                    </p>
                    <AlertDialogTitle>
                      <p className="text-2xl font-semibold">
                        {patrol.preset.title}
                      </p>
                    </AlertDialogTitle>
                  </div>

                  <AlertDialogDescription className="flex flex-col gap-2">
                    <div className="flex flex-row gap-1">
                      <span className="material-symbols-outlined">
                        description
                      </span>
                      <p className="text-muted-foreground text-xl font-semibold">
                        {formatId(patrol.id)}
                      </p>
                    </div>

                    <div className="flex flex-row gap-1">
                      {patrol.status === "completed" && (
                        <span className="material-symbols-outlined">
                          event_available
                        </span>
                      )}

                      {patrol.status === "completed" ? (
                        <div className="text-muted-foreground font-extrabold text-lg">
                          <p className="text-xl font-semibold text-muted-foreground">
                            {formatDate(patrol.endTime) +
                              " " +
                              formatTimeDate(patrol.startTime) +
                              " - " +
                              formatTimeDate(patrol.endTime)}
                          </p>
                        </div>
                      ) : (
                        ""
                      )}
                    </div>

                    <div className="flex flex-row gap-1 items-start">
                      <span className="material-symbols-outlined text-2xl text-muted-foreground p-0">
                        location_on
                      </span>
                      <p className="flex items-center text-base text-muted-foreground">
                        {formatZone(patrol) || "No zones available"}
                      </p>
                    </div>

                    <div className="flex flex-row gap-1 ">
                      <span className="material-symbols-outlined text-2xl text-muted-foreground ">
                        data_info_alert
                      </span>
                      <p className="gap-1 text-base text-muted-foreground ">
                        {patrol.preset.description}
                      </p>
                    </div>
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel>Close</AlertDialogCancel>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>

            <div className="flex flex-col h-full justify-start w-full">
              <p className="text-2xl font-bold mb-1 text-start">{patrol.preset.title}</p>
              <div className="w-[300px]">
                <Progress value={calculateProgress()} />
              </div>
            </div>
          </div>
          <div className="flex flex-row sm:w-full sm:justify-between lg:w-fit items-center gap-2">
            {patrol && (
              <PatrolTimer
                launchDate={patrol.startTime}
                patrolStatus={patrol.status}
                patrolDuration={patrol.duration}
              />
            )}

            <BadgeCustom
              iconName={getPatrolStatusVariant(patrol.status).iconName}
              showIcon={true}
              showTime={false}
              variant={getPatrolStatusVariant(patrol.status).variant}
            >
              {s(patrol.status)}
            </BadgeCustom>
          </div>
        </div>
        <div className="flex w-full justify-between items-center">
          <TabMenu id={id.toString()} />
          <div className="flex items-center gap-2">
            <Button
              variant={"secondary"}
              onClick={() => router.push(`/${locale}`)}
            >
              {t("Back")}
            </Button>
            {(() => {
              let iconName: string;
              let text: string;
              let variant:
                | "link"
                | "default"
                | "secondary"
                | "destructive"
                | "success"
                | "fail"
                | "outline"
                | "ghost"
                | "primary"
                | null
                | undefined;
              let disabled: boolean;
              let handleFunction: any;
              switch (patrol.status as patrolStatus) {
                case "completed":
                  variant = "outline";
                  iconName = "ios_share";
                  text = "Export";
                  disabled = false;
                  handleFunction = () => { };
                  break;
                case "on_going":
                  variant = "primary";
                  iconName = "Check";
                  text = "Finish";
                  disabled = false;
                  handleFunction = () => {
                    handleFinishPatrol();
                  };
                  break;
                case "scheduled":
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = false;
                  handleFunction = () => {
                    handleStartPatrol();
                  };
                  break;
                case "pending":
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = true;
                  handleFunction = () => { };
                  break;
                default:
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = true;
                  handleFunction = () => { };
                  break;
              }
              return (
                <div>
                  {patrol.status === "completed" && isAlertOpen ? (
                    <AlertDialog open={isAlertOpen}>
                      <AlertDialogTrigger asChild>
                        <Button
                          variant={variant}
                          onClick={handleOpenDialog}
                          disabled={disabled}
                          className="flex flex-row items-center gap-2"
                        >
                          <span className="material-symbols-outlined ">
                            {iconName}
                          </span>
                          {t(text)}
                        </Button>
                      </AlertDialogTrigger>

                      <AlertDialogContent className="w-[400px] h-fit px-6 py-4 ">
                        <AlertDialogHeader className="flex flex-col gap-2">
                          <div className="flex flex-col gap-1">
                            <p className="text-lg font-semibold text-muted-foreground">
                              {formatDate(patrol.date)}
                            </p>
                            <AlertDialogTitle>
                              <p className="text-2xl font-semibold">
                                {patrol.preset.title}
                              </p>
                            </AlertDialogTitle>
                          </div>

                          <AlertDialogDescription className="flex flex-col gap-2">
                            <div className="flex flex-row gap-1">
                              <span className="material-symbols-outlined">
                                description
                              </span>
                              <p className="text-muted-foreground text-xl font-semibold">
                                {formatId(patrol.id)}
                              </p>
                            </div>

                            <Popover
                              open={open}
                              onOpenChange={(isOpen) => {
                                if (!isOpen) handleClose()
                              }}
                            >
                              <HoverCard
                                open={open && openType.current === 'hover'}
                                onOpenChange={(isHoverOpen) => {
                                  if (!isHoverOpen && openType.current === 'hover') handleClose();
                                }}
                              >
                                <div
                                  ref={triggerContainerRef}
                                  onMouseEnter={handleMouseEnter}
                                  onMouseLeave={handleMouseLeave}
                                >
                                  <HoverCardTrigger asChild>
                                    <PopoverTrigger asChild>
                                      <div className="cursor-pointer" onClick={handleClick}>
                                        <div className="flex text-muted-foreground items-center">
                                          <span className="material-symbols-outlined me-1">
                                            person_search
                                          </span>
                                          {inspectors.length > 0 && (
                                            <div className="flex items-center me-1 truncate max-w-[190px]">
                                              <p className="text-xl me-2.5 truncate">
                                                {inspectors[0].profile.name}
                                              </p>
                                            </div>
                                          )}
                                          {inspectors.map((inspector, idx) => {
                                            return (
                                              <Avatar key={idx} className="custom-shadow ms-[-10px]">
                                                <AvatarImage
                                                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                                                />
                                                <AvatarFallback id={inspector.id.toString()}>
                                                  {getInitials(inspector.profile.name)}
                                                </AvatarFallback>
                                              </Avatar>
                                            );
                                          })}

                                          {inspectors.length > 5 && (
                                            <Avatar className="custom-shadow flex items-center justify-center ms-[-10px]">
                                              <AvatarImage src="" />
                                              <span className="absolute text-card-foreground text-base font-semibold">
                                                +{inspectors.length - 5}
                                              </span>
                                              <AvatarFallback id={"0"}></AvatarFallback>
                                            </Avatar>
                                          )}
                                        </div>
                                      </div>
                                    </PopoverTrigger>
                                  </HoverCardTrigger>

                                  <HoverCardContent
                                    className="w-[300px] px-6 py-4"
                                    zIndex={0}
                                    side="bottom"
                                    align="start"
                                    onClick={(e) => e.stopPropagation()}
                                  >
                                    <TooltipContent />
                                  </HoverCardContent>
                                  <PopoverContent
                                    className="w-[300px] z-[100] px-6 py-4"
                                    side="bottom"
                                    align="start"
                                    onInteractOutside={(e) => {
                                      const target = e.target as HTMLElement;
                                      const isUserTooltipContent = target?.closest?.('[data-radix-tooltip-content]');
                                      const isUserTooltipTrigger = target?.closest?.('.user-tooltip');

                                      if (isUserTooltipContent || isUserTooltipTrigger) {
                                        e.preventDefault();
                                      }
                                    }}
                                  >
                                    <TooltipContent />
                                  </PopoverContent>
                                </div>
                              </HoverCard>
                            </Popover>

                            <CardFooter className="p-0 gap-0">
                              <div className="flex gap-2 items-center w-full">
                                <div className="flex gap-1 text-primary items-center">
                                  <span className="material-symbols-outlined">
                                    checklist
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countItems}
                                  </p>
                                </div>
                                <div className="flex gap-1 text-orange items-center">
                                  <span className="material-symbols-outlined">
                                    close
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countFails}
                                  </p>
                                </div>
                                <div className="flex gap-1 text-destructive items-center">
                                  <span className="material-symbols-outlined">
                                    error
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countDefects}
                                  </p>
                                </div>
                              </div>
                            </CardFooter>
                          </AlertDialogDescription>
                        </AlertDialogHeader>
                        <AlertDialogFooter>
                          <AlertDialogCancel
                            onClick={() => handleCloseDialog()}
                          >
                            Cancel
                          </AlertDialogCancel>
                          <AlertDialogAction
                            className="bg-primary text-primary-foreground shadow hover:bg-primary/90 h-10 rounded-md px-4 text-lg font-bold"
                            onClick={() => {
                              exportData(patrol, patrolResults);
                              handleCloseDialog();
                              toast({
                                variant: "success",
                                title: a("ExportReportPatrolTitle"),
                                description: a("ExportReportPatrolDescription"),
                              });
                            }}
                          >
                            <span className="material-symbols-outlined text-2xl me-2">
                              ios_share
                            </span>
                            Export
                          </AlertDialogAction>
                        </AlertDialogFooter>
                      </AlertDialogContent>
                    </AlertDialog>
                  ) : patrol.status === "on_going" ? (
                    canFinish ? (
                      <Button
                        variant={variant}
                        onClick={handleOpenDialog}
                        disabled={disabled}
                      >
                        <span className="material-symbols-outlined">
                          {iconName}
                        </span>
                        {t(text)}
                      </Button>
                    ) : (
                      <Button
                        variant={lock ? "secondary" : variant}
                        disabled={disabled}
                        onClick={toggleLock}
                      >
                        <span className="material-symbols-outlined">
                          {lock ? "lock_open" : "lock"}
                        </span>
                        {lock ? t("Unlock") : t("Lock")}
                      </Button>
                    )
                  ) : (
                    <Button
                      variant={variant}
                      onClick={() => handleOpenDialog()}
                      disabled={disabled}
                      className="flex flex-row items-center gap-2"
                    >
                      <span className="material-symbols-outlined">
                        {iconName}
                      </span>
                      {t(text)}
                    </Button>
                  )}
                  {patrol.status === "scheduled" && isAlertOpen ? (
                    <AlertCustom
                      title={a("PatrolStartConfirmTitle")}
                      description={a("PatrolStartConfirmDescription")}
                      primaryButtonText={t("Confirm")}
                      primaryIcon="check"
                      secondaryButtonText={t("Cancel")}
                      backResult={(result) => {
                        if (result) {
                          handleStartPatrol();
                        }
                        handleCloseDialog();
                      }}
                    />
                  ) : patrol.status === "on_going" && isAlertOpen ? (
                    <AlertCustom
                      title={a("PatrolFinishConfirmTitle")}
                      description={a("PatrolFinishConfirmDescription")}
                      primaryButtonText={t("Confirm")}
                      primaryIcon="check"
                      secondaryButtonText={t("Cancel")}
                      backResult={(result) => {
                        if (result) {
                          handleFinishPatrol();
                        }
                        handleCloseDialog();
                      }}
                    />
                  ) : null}
                </div>
              );
            })()}
          </div>
        </div>
      </div>
      <ScrollArea className="h-full w-full rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-224px)]">
        {" "}
        {patrol.patrolChecklists.map((pc: IPatrolChecklist) => (
          <div className="rounded-md mb-4">
            {user?.profile.name === pc.inspector.profile.name ? (
              <PatrolChecklist
                handleResult={handleResult}
                patrolStatus={patrol.status}
                patrolChecklist={pc}
                disabled={patrol.status === "on_going" && !lock ? false : true}
                patrolResults={patrolResults}
                response={(defect: IDefect) => {
                  fetchRealtimeData(defect, "create");
                }}
              />
            ) : (
              <div></div>
            )}
          </div>
        ))}
      </ScrollArea>
    </div>
  );
}

'use client'

import { PatrolProvider, usePatrol } from '@/context/patrol-context';

export default function PatrolLayout({
    children,
}: Readonly<{
    children: React.ReactNode;
}>) {

    return (
        <PatrolProvider>
            <div className="flex flex-col gap-4 px-6 py-4">
                {children}
            </div>
        </PatrolProvider>
    );
}

/**
 * :
 *   Defect  Patrol 
 *
 * Input:
 * - 
 * Output:
 * -  Defect  Patrol 
 * -  Patrol  Progress  Patrol
 * -  Start  Finish  Patrol  Patrol
 * -  Defect  Defect 
 **/

"use client";
import { IDefect, patrolStatus } from "@/app/type";
import BadgeCustom from "@/components/badge-custom";
import Loading from "@/components/loading";
import PatrolTimer from "@/components/patrol-timer";
import ReportDefect from "@/components/report-defect";
import TabMenu from "@/components/tab-menu";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { usePatrol } from "@/context/patrol-context";
import { exportData, getInitials, getPatrolStatusVariant } from "@/lib/utils";
import { useLocale, useTranslations } from "next-intl";
import { useParams, useRouter } from "next/navigation";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { CardFooter } from "@/components/ui/card";

import React, { useRef, useState } from "react";
import {
  AlertDialog,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogCancel,
  AlertDialogAction,
  AlertDialogFooter,
} from "@/components/ui/alert-dialog";
import {
  HoverCard,
  HoverCardContent,
  HoverCardTrigger,
} from "@/components/ui/hover-card";
import { toast } from "@/hooks/use-toast";
import NotFound from "@/components/not-found";
import { ScrollArea } from "@/components/ui/scroll-area";
import { AlertCustom } from "@/components/alert-custom";
import { UserTooltip } from "@/components/user-tooltip";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";

export default function Page() {
  const {
    patrol,
    patrolResults,
    lock,
    mounted,
    defects,
    canFinish,
    countDefects,
    countFails,
    countItems,
    patrolUser,
    isAlertOpen,
    isHovered,
    formatDate,
    formatId,
    formatTimeDate,
    formatZone,
    handleMouseEnter,
    handleMouseLeave,
    handleOpenDialog,
    handleCloseDialog,
    toggleLock,
    calculateProgress,
    handleStartPatrol,
    fetchRealtimeData,
    handleFinishPatrol,
  } = usePatrol();
  const locale = useLocale();
  const params = useParams();
  const id = params.id;
  const router = useRouter();
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const s = useTranslations("Status");
  const [open, setOpen] = useState(false);
  const openType = useRef<"hover" | "click" | null>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const triggerContainerRef = useRef<HTMLDivElement>(null);

  const inspectors = patrolUser;
  const handleClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    //  timeout  hover 
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
      timeoutRef.current = null;
    }
    openType.current = "click";
    setOpen(true); //  Popover 
  };

  const handleClose = () => {
    setOpen(false);
    openType.current = null;
  };

  const TooltipContent = () => {
    return (
      <div >
        <div className="flex items-center justify-center gap-1">
          <span className="material-symbols-outlined">person_search</span>
          <p className="text-lg font-semibold">{t("InspectorList")}</p>
        </div>
        {inspectors.map((inspector, idx) => (
          <div
            key={idx}
            className="flex items-center w-full py-2 gap-1 border-b-2 border-secondary"
          >
            <UserTooltip user={inspector}>
              <Avatar className="custom-shadow ms-[-10px] me-2.5">
                <AvatarImage
                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                />
                <AvatarFallback id={inspector.id.toString()}>
                  {getInitials(inspector.profile.name)}
                </AvatarFallback>
              </Avatar>
            </UserTooltip>
            <p className="text-lg truncate">{inspector.profile.name}</p>
          </div>
        ))}
        <div className="flex items-center justify-between w-full text-muted-foreground">
          <p className="text-lg font-semibold">{t("Total")}</p>
          <p className="text-lg font-semibold">{inspectors.length}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="flex flex-col gap-4">
      {/* TabList  Title */}
      <div className="flex flex-col gap-4 bg-background">
        <div className="flex sm:flex-col lg:flex-row lg:items-center justify-between sm:items-start">
          <div className="flex items-center p-0 justify-center text-center gap-2">
            <AlertDialog>
              <AlertDialogTrigger asChild>
                <Button variant="ghost" className="flex hover:bg-secondary p-2">
                  <span className="material-symbols-outlined text-card-foreground w-[22px] h-[22px]">
                    error
                  </span>
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent className="w-[400px] h-fit px-6 py-4 ">
                <AlertDialogHeader className="flex flex-col gap-2">
                  <div className="flex flex-col gap-1">
                    <p className="text-lg font-semibold text-muted-foreground">
                      {formatDate(patrol.date)}
                    </p>
                    <AlertDialogTitle>
                      <p className="text-2xl font-semibold">
                        {patrol.preset.title}
                      </p>
                    </AlertDialogTitle>
                  </div>

                  <AlertDialogDescription className="flex flex-col gap-2">
                    <div className="flex flex-row gap-1">
                      <span className="material-symbols-outlined">
                        description
                      </span>
                      <p className="text-muted-foreground text-xl font-semibold">
                        {formatId(patrol.id)}
                      </p>
                    </div>

                    <div className="flex flex-row gap-1">
                      {patrol.status === "completed" && (
                        <span className="material-symbols-outlined">
                          event_available
                        </span>
                      )}

                      {patrol.status === "completed" ? (
                        <div className="text-muted-foreground font-extrabold text-lg">
                          <p className="text-xl font-semibold text-muted-foreground">
                            {formatDate(patrol.endTime) +
                              " " +
                              formatTimeDate(patrol.startTime) +
                              " - " +
                              formatTimeDate(patrol.endTime)}
                          </p>
                        </div>
                      ) : (
                        ""
                      )}
                    </div>

                    <div className="flex flex-row gap-1 items-start">
                      <span className="material-symbols-outlined text-2xl text-muted-foreground p-0">
                        location_on
                      </span>
                      <p className="flex items-center text-base text-muted-foreground">
                        {formatZone(patrol) || "No zones available"}
                      </p>
                    </div>

                    <div className="flex flex-row gap-1 ">
                      <span className="material-symbols-outlined text-2xl text-muted-foreground ">
                        data_info_alert
                      </span>
                      <p className="gap-1 text-base text-muted-foreground ">
                        {patrol.preset.description}
                      </p>
                    </div>
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel>Close</AlertDialogCancel>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>

            <div className="flex flex-col h-full justify-start w-full">
              <p className="text-2xl font-bold mb-1 text-start">{patrol.preset.title}</p>
              <div className="w-[300px]">
                <Progress value={calculateProgress()} />
              </div>
            </div>
          </div>
          <div className="flex flex-row sm:w-full sm:justify-between lg:w-fit items-center gap-2">
            {patrol && (
              <PatrolTimer
                launchDate={patrol.startTime}
                patrolStatus={patrol.status}
                patrolDuration={patrol.duration}
              />
            )}

            <BadgeCustom
              iconName={getPatrolStatusVariant(patrol.status).iconName}
              showIcon={true}
              showTime={false}
              variant={getPatrolStatusVariant(patrol.status).variant}
            >
              {s(patrol.status)}
            </BadgeCustom>
          </div>
        </div>
        <div className="flex w-full justify-between items-center">
          <TabMenu id={id.toString()} />
          <div className="flex items-center gap-2">
            <Button
              variant={"secondary"}
              onClick={() => router.push(`/${locale}`)}
            >
              {t("Back")}
            </Button>
            {(() => {
              let iconName: string;
              let text: string;
              let variant:
                | "link"
                | "default"
                | "secondary"
                | "destructive"
                | "success"
                | "fail"
                | "outline"
                | "ghost"
                | "primary"
                | null
                | undefined;
              let disabled: boolean;
              let handleFunction: any;
              switch (patrol.status as patrolStatus) {
                case "completed":
                  variant = "outline";
                  iconName = "ios_share";
                  text = "Export";
                  disabled = false;
                  handleFunction = () => { };
                  break;
                case "on_going":
                  variant = "primary";
                  iconName = "Check";
                  text = "Finish";
                  disabled = false;
                  handleFunction = () => {
                    handleFinishPatrol();
                  };
                  break;
                case "scheduled":
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = false;
                  handleFunction = () => {
                    handleStartPatrol();
                  };
                  break;
                case "pending":
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = true;
                  handleFunction = () => { };
                  break;
                default:
                  variant = "primary";
                  iconName = "cached";
                  text = "Start";
                  disabled = true;
                  handleFunction = () => { };
                  break;
              }
              return (
                <div>
                  {patrol.status === "completed" && isAlertOpen ? (
                    <AlertDialog open={isAlertOpen}>
                      <AlertDialogTrigger asChild>
                        <Button
                          variant={variant}
                          onClick={handleOpenDialog}
                          disabled={disabled}
                          className="flex flex-row items-center gap-2"
                        >
                          <span className="material-symbols-outlined ">
                            {iconName}
                          </span>
                          {t(text)}
                        </Button>
                      </AlertDialogTrigger>

                      <AlertDialogContent className="w-[400px] h-fit px-6 py-4 ">
                        <AlertDialogHeader className="flex flex-col gap-2">
                          <div className="flex flex-col gap-1">
                            <p className="text-lg font-semibold text-muted-foreground">
                              {formatDate(patrol.date)}
                            </p>
                            <AlertDialogTitle>
                              <p className="text-2xl font-semibold">
                                {patrol.preset.title}
                              </p>
                            </AlertDialogTitle>
                          </div>

                          <AlertDialogDescription className="flex flex-col gap-2">
                            <div className="flex flex-row gap-1">
                              <span className="material-symbols-outlined">
                                description
                              </span>
                              <p className="text-muted-foreground text-xl font-semibold">
                                {formatId(patrol.id)}
                              </p>
                            </div>

                            <Popover
                              open={open}
                              onOpenChange={(isOpen) => {
                                if (!isOpen) handleClose()
                              }}
                            >
                              <HoverCard
                                open={open && openType.current === 'hover'}
                                onOpenChange={(isHoverOpen) => {
                                  if (!isHoverOpen && openType.current === 'hover') handleClose();
                                }}
                              >
                                <div
                                  ref={triggerContainerRef}
                                  onMouseEnter={handleMouseEnter}
                                  onMouseLeave={handleMouseLeave}
                                >
                                  <HoverCardTrigger asChild>
                                    <PopoverTrigger asChild>
                                      <div className="cursor-pointer" onClick={handleClick}>
                                        <div className="flex text-muted-foreground items-center">
                                          <span className="material-symbols-outlined me-1">
                                            person_search
                                          </span>
                                          {inspectors.length > 0 && (
                                            <div className="flex items-center me-1 truncate max-w-[190px]">
                                              <p className="text-xl me-2.5 truncate">
                                                {inspectors[0].profile.name}
                                              </p>
                                            </div>
                                          )}
                                          {inspectors.map((inspector, idx) => {
                                            return (
                                              <Avatar key={idx} className="custom-shadow ms-[-10px]">
                                                <AvatarImage
                                                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${inspector?.profile?.image?.path}`}
                                                />
                                                <AvatarFallback id={inspector.id.toString()}>
                                                  {getInitials(inspector.profile.name)}
                                                </AvatarFallback>
                                              </Avatar>
                                            );
                                          })}

                                          {inspectors.length > 5 && (
                                            <Avatar className="custom-shadow flex items-center justify-center ms-[-10px]">
                                              <AvatarImage src="" />
                                              <span className="absolute text-card-foreground text-base font-semibold">
                                                +{inspectors.length - 5}
                                              </span>
                                              <AvatarFallback id={"0"}></AvatarFallback>
                                            </Avatar>
                                          )}
                                        </div>
                                      </div>
                                    </PopoverTrigger>
                                  </HoverCardTrigger>

                                  <HoverCardContent
                                    className="w-[300px] px-6 py-4"
                                    zIndex={0}
                                    side="bottom"
                                    align="start"
                                    onClick={(e) => e.stopPropagation()}
                                  >
                                    <TooltipContent />
                                  </HoverCardContent>
                                  <PopoverContent
                                    className="w-[300px] z-[100] px-6 py-4"
                                    side="bottom"
                                    align="start"
                                    onInteractOutside={(e) => {
                                      const target = e.target as HTMLElement;
                                      const isUserTooltipContent = target?.closest?.('[data-radix-tooltip-content]');
                                      const isUserTooltipTrigger = target?.closest?.('.user-tooltip');

                                      if (isUserTooltipContent || isUserTooltipTrigger) {
                                        e.preventDefault();
                                      }
                                    }}
                                  >
                                    <TooltipContent />
                                  </PopoverContent>
                                </div>
                              </HoverCard>
                            </Popover>

                            <CardFooter className="p-0 gap-0">
                              <div className="flex gap-2 items-center w-full">
                                <div className="flex gap-1 text-primary items-center">
                                  <span className="material-symbols-outlined">
                                    checklist
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countItems}
                                  </p>
                                </div>
                                <div className="flex gap-1 text-orange items-center">
                                  <span className="material-symbols-outlined">
                                    close
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countFails}
                                  </p>
                                </div>
                                <div className="flex gap-1 text-destructive items-center">
                                  <span className="material-symbols-outlined">
                                    error
                                  </span>
                                  <p className="text-xl font-semibold">
                                    {countDefects}
                                  </p>
                                </div>
                              </div>
                            </CardFooter>
                          </AlertDialogDescription>
                        </AlertDialogHeader>
                        <AlertDialogFooter>
                          <AlertDialogCancel
                            onClick={() => handleCloseDialog()}
                          >
                            Cancel
                          </AlertDialogCancel>
                          <AlertDialogAction
                            className="bg-primary text-primary-foreground shadow hover:bg-primary/90 h-10 rounded-md px-4 text-lg font-bold"
                            onClick={() => {
                              exportData(patrol, patrolResults);
                              handleCloseDialog();
                              toast({
                                variant: "success",
                                title: a("ExportReportPatrolTitle"),
                                description: a("ExportReportPatrolDescription"),
                              });
                            }}
                          >
                            <span className="material-symbols-outlined text-2xl me-2">
                              ios_share
                            </span>
                            Export
                          </AlertDialogAction>
                        </AlertDialogFooter>
                      </AlertDialogContent>
                    </AlertDialog>
                  ) : patrol.status === "on_going" ? (
                    canFinish ? (
                      <Button
                        variant={variant}
                        onClick={handleOpenDialog}
                        disabled={disabled}
                      >
                        <span className="material-symbols-outlined">
                          {iconName}
                        </span>
                        {t(text)}
                      </Button>
                    ) : (
                      <Button
                        variant={lock ? "secondary" : variant}
                        disabled={disabled}
                        onClick={toggleLock}
                      >
                        <span className="material-symbols-outlined">
                          {lock ? "lock_open" : "lock"}
                        </span>
                        {lock ? t("Unlock") : t("Lock")}
                      </Button>
                    )
                  ) : (
                    <Button
                      variant={variant}
                      onClick={() => handleOpenDialog()}
                      disabled={disabled}
                      className="flex flex-row items-center gap-2"
                    >
                      <span className="material-symbols-outlined">
                        {iconName}
                      </span>
                      {t(text)}
                    </Button>
                  )}
                  {patrol.status === "scheduled" && isAlertOpen ? (
                    <AlertCustom
                      title={a("PatrolStartConfirmTitle")}
                      description={a("PatrolStartConfirmDescription")}
                      primaryButtonText={t("Confirm")}
                      primaryIcon="check"
                      secondaryButtonText={t("Cancel")}
                      backResult={(result) => {
                        if (result) {
                          handleStartPatrol();
                        }
                        handleCloseDialog();
                      }}
                    />
                  ) : patrol.status === "on_going" && isAlertOpen ? (
                    <AlertCustom
                      title={a("PatrolFinishConfirmTitle")}
                      description={a("PatrolFinishConfirmDescription")}
                      primaryButtonText={t("Confirm")}
                      primaryIcon="check"
                      secondaryButtonText={t("Cancel")}
                      backResult={(result) => {
                        if (result) {
                          handleFinishPatrol();
                        }
                        handleCloseDialog();
                      }}
                    />
                  ) : null}
                </div>
              );
            })()}
          </div>
        </div>
      </div>
      <ScrollArea className="h-full w-full rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-220px)]">
        {defects.length === 0 ? (
          <NotFound
            icon="campaign"
            title="NoDefectsPatrol"
            description="NoDefectsPatrolDescription"
          />
        ) : (
          defects
            .slice() //  array 
            .reverse() // 
            .map((defect: IDefect) => {
              return (
                <div className="mb-4" key={defect.id}>
                  <ReportDefect
                    defect={defect}
                    page={"patrol-view-report"}
                    response={(defect: IDefect) => {
                      fetchRealtimeData(defect, "edit");
                    }}
                  />
                </div>
              );
            })
        )}
      </ScrollArea>
    </div>
  );
}

/**
 * :
 *   Patrol   Patrol 
 *     Patrol 
 *
 * Input:
 * - 
 * Output:
 * -  Patrol  Patrol   Patrol 
 * -    Patrol 
 * -  Preset  Patrol 
 * -  Inspector  Checklist  Preset 
 * -  Patrol 
 * -  Patrol 
 * -  Alert  Patrol 
 * -  Alert  Patrol
 * -  Alert  Patrol 
 * -  Alert  Patrol 
 * -  Alert  Patrol
 * -  Alert  Patrol
 **/

"use client";

import { useEffect, useMemo, useRef, useState } from "react";
import { CreatePatrolCard, PatrolCard } from "@/components/patrol-card";
import Textfield from "@/components/textfield";
import { Button, buttonVariants } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { useTranslations } from "next-intl";
import { fetchData } from "@/lib/utils";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Textarea } from "@/components/ui/textarea";
import { ChecklistDropdown } from "@/components/checklist-dropdown";
import { DatePicker, DatePickerWithRange } from "@/components/date-picker";
import BadgeCustom from "@/components/badge-custom";
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectLabel,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  IPatrol,
  IPatrolChecklist,
  patrolStatus,
  IPreset,
  IPresetChecklist,
  IPatrolResult,
} from "@/app/type";
import { IUser, IFilterPatrol } from "@/app/type";
import { sortData } from "@/lib/utils";
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { DateRange, DateRange as DayPickerDateRange } from "react-day-picker";
import Loading from "@/components/loading";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";
import NotFound from "@/components/not-found";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { ZoneTooltip } from "@/components/zone-tooltip";
import { TextTooltip } from "@/components/text-tooltip";
import { useSocket } from "@/components/socket-provider";

export default function Page() {
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const z = useTranslations("Zone");
  const [loading, setLoading] = useState<boolean>(true);
  const [socketData, setSocketData] = useState<IPatrolResult[]>([]);
  const [allPatrols, setAllPatrols] = useState<IPatrol[]>([]);
  const [allPresets, setAllPresets] = useState<IPreset[]>();
  const [secondDialog, setSecondDialog] = useState(false);
  const { socket, isConnected } = useSocket();

  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);

  const [selectedPreset, setSelectedPreset] = useState<IPreset>();
  const [selectedDate, setSelectedDate] = useState<string>();
  const [patrolChecklist, setPatrolChecklist] = useState<IPatrolChecklist[]>(
    []
  );

  const [isSortOpen, setIsSortOpen] = useState(false);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [dateError, setDateError] = useState<string | null>(null);
  const [user, setUser] = useState<IUser | null>(null);

  const isNextButtonDisabled = !selectedPreset;

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const handleDialogResult = (result: boolean) => {
    setIsDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
    }
  };

  const handleCreatePatrol = () => {
    setPendingAction(() => () => createPatrol());
    handleOpenDialog();
  };

  const createPatrol = async () => {
    if (!selectedDate) {
      setDateError("PatrolUnselectDate");
      toast({
        variant: "error",
        title: a("PatrolMissingDateTitle"),
        description: a("PatrolMissingDateDescription"),
      });
      return;
    } else {
      setDateError(null);
    }

    // 
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const selected = new Date(selectedDate);
    selected.setHours(0, 0, 0, 0);

    if (selected < today) {
      setDateError("PatrolInvalidDate");
      toast({
        variant: "error",
        title: a("PatrolCreateInvalidDateTitle"),
        description: a("PatrolCreateInvalidDateDescription"),
      });
      return;
    }

    if (
      patrolChecklist.length !== selectedPreset.presetChecklists.length ||
      !patrolChecklist.every((item) => item.userId !== null)
    ) {
      toast({
        variant: "error",
        title: a("PatrolCreateErrorMissingInspectorTitle"),
        description: a("PatrolCreateErrorMissingInspectorDescription"),
      });
      return;
    }

    const data = {
      date: selectedDate,
      presetId: selectedPreset.id,
      checklists: patrolChecklist,
    };

    try {
      const response = await fetchData("post", "/patrol", true, data);
      setSecondDialog(false);
      toast({
        variant: "success",
        title: a("PatrolCreateTitle"),
        description: a("PatrolCreateDescription"),
      });
      setPatrolChecklist([]);
      setSelectedDate(null);
      setSelectedPreset(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleRemoveSuccess = (id: number) => {
    setAllPatrols((prevPatrols) =>
      prevPatrols.filter((patrol) => patrol.id !== id)
    );
    toast({
      variant: "success",
      title: a("PatrolRemoveSuccessTitle"),
      description: a("PatrolRemoveSuccessDescription"),
    });
  };

  const handleSelectUser = (checklistId: number, userId: number) => {
    setPatrolChecklist((prev: IPatrolChecklist[]) => {
      const existingIndex = prev.findIndex(
        (item) => item.checklistId === checklistId
      );

      if (existingIndex !== -1) {
        // If the checklist already exists, update the inspectorId
        const updatedChecklist = [...prev];
        updatedChecklist[existingIndex].userId = userId;
        return updatedChecklist;
      } else {
        // Add new checklist
        return [...prev, { checklistId, userId }];
      }
    });
  };

  const handleSortChange = (type: string, value: string) => {
    setSort((prevSort) => ({
      ...prevSort,
      [type]: value,
    }));
  };

  const initialFilter = {
    presetTitle: "All",
    patrolStatuses: ["pending", "on_going", "scheduled"],
    dateRange: { start: undefined, end: undefined },
  };

  const getStoredFilter = () => {
    if (typeof window !== "undefined") {
      const storedFilter = localStorage.getItem("filter");
      if (storedFilter) {
        return JSON.parse(storedFilter);
      }
    }
    return initialFilter;
  };

  const [filter, setFilter] = useState<IFilterPatrol | null>(getStoredFilter());

  const [sort, setSort] = useState<{ by: string; order: string }>({
    by: "Doc No.",
    order: "Ascending",
  });

  const toggleStatusFilter = (status: string, checked: boolean) => {
    if (checked) {
      setFilter((prev) => {
        if (prev) {
          return {
            ...prev,
            patrolStatuses: [...prev.patrolStatuses, status],
          };
        } else {
          return {
            presetTitle: "All",
            patrolStatuses: [],
            dateRange: { start: undefined, end: undefined },
          };
        }
      });
    } else {
      setFilter((prev) => {
        if (prev) {
          return {
            ...prev,
            patrolStatuses: prev.patrolStatuses.filter((s) => s !== status),
          };
        }
        return prev;
      });
    }
  };

  const applyFilter = () => {
    getPatrolData();
  };

  const resetFilter = () => {
    setFilter(initialFilter);
  };

  const handleDateSelect = (dateRange: DateRange) => {
    const startDate = dateRange.from ?? null;
    const endDate = dateRange.to
      ? new Date(new Date(dateRange.to).setHours(23, 59, 59, 999))
      : null;
    setFilter({
      presetTitle: filter?.presetTitle || null,
      patrolStatuses: filter?.patrolStatuses || [],
      dateRange: {
        start: startDate || undefined,
        end: endDate || undefined,
      },
    });
  };

  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (event: any) => {
    setSearchTerm(event.target.value);
  };

  const buildQueryString = (
    filter: IFilterPatrol | null,
    searchTerm: string
  ) => {
    const params: Record<string, string | undefined> = {};

    //  search term 
    if (searchTerm) params.search = searchTerm;

    //  preset filter  "All"
    if (filter?.presetTitle && filter.presetTitle !== "All") {
      params.preset = filter.presetTitle;
    }

    //  status filter
    if (filter?.patrolStatuses?.length) {
      params.status = filter.patrolStatuses.join(",");
    }

    //  startDate
    if (filter?.dateRange?.start) {
      params.startDate = filter?.dateRange.start.toISOString();
    }

    //  endDate
    if (filter?.dateRange?.end) {
      params.endDate = filter?.dateRange?.end.toISOString();
    }

    return new URLSearchParams(params).toString();
  };

  const getUserData = async () => {
    try {
      const userfetch = await fetchData("get", "/user?profile=true&image=true", true);
      setUser(userfetch);
    } catch (error) {
      console.error("Failed to fetch profile data:", error);
    }
  };

  const getPatrolData = async () => {
    try {
      const queryString = buildQueryString(filter, searchTerm);
      const patrols = await fetchData("get", `/patrols?${queryString}`, true);
      setAllPatrols(patrols);
    } catch (error) {
      console.error("Failed to fetch patrol data:", error);
    }
  };

  const getPresetData = async () => {
    try {
      const preset = await fetchData("get", "/presets?latest=true", true);
      setAllPresets(preset);
    } catch (error) {
      console.error("Failed to fetch patrol data:", error);
    }
  };

  const mergedPatrols = useMemo(() => {
    if (!socketData || socketData.length === 0) {
      return allPatrols;
    }

    return allPatrols.map((patrol) => {
      if (!patrol.results || patrol.results.length === 0) {
        return patrol;
      }

      const updatedResults = patrol.results
        .map((existingResult) => {
          const matchingSocketResult = socketData.find((result) => result.id === existingResult.id);
          return matchingSocketResult ? { ...existingResult, ...matchingSocketResult } : existingResult;
        })
        .filter((result) => result.id); //  id 

      return { ...patrol, results: [...updatedResults] };
    });
  }, [allPatrols, socketData]);

  //  useEffect  allPatrols  mergedPatrols 
  useEffect(() => {
    if (JSON.stringify(allPatrols) !== JSON.stringify(mergedPatrols)) {
      setAllPatrols([...mergedPatrols]);
    }
  }, [mergedPatrols]);

  const joinedRoomsRef = useRef(new Set());

  useEffect(() => {
    getUserData();
    getPatrolData();
    getPresetData();
  }, []);

  useEffect(() => {
    const initializeSocketListeners = () => {
      if (user?.id) {
        socket.emit('join_room', user.id);
      }

      //  socket
      const handleInitialData = (initialResults: IPatrolResult[]) => {
        if (initialResults.length <= 0) {
          return;
        }
        //  socketData  socket
        setSocketData(prevData => {
          // 
          const updatedResults = initialResults.map((incomingResult) => {
            //  incomingResult.id  prevData 
            const existingIndex = prevData.findIndex(result => result.id === incomingResult.id);

            if (existingIndex !== -1) {
              //  id ,  existing result
              return {
                ...prevData[existingIndex],  // 
                ...incomingResult,            //  initialResults
              };
            }

            //  id , 
            return incomingResult;
          });

          //  prevData
          const newResults = initialResults.filter(result =>
            !prevData.some(existingResult => existingResult.id === result.id)
          );

          // 
          return [...updatedResults, ...newResults];
        });
      };

      // 
      const handleResultUpdate = (incomingResult: IPatrolResult) => {
        setSocketData(prevData => {
          //  incomingResult.id  prevData 
          const updatedResults = prevData.map(existingResult => {
            if (existingResult.id === incomingResult.id) {
              return {
                ...existingResult,   // 
                ...incomingResult,   //  incomingResult
              };
            }
            return existingResult;
          });

          //  incomingResult  prevData, 
          if (!prevData.some(existingResult => existingResult.id === incomingResult.id)) {
            return [...updatedResults, incomingResult];
          }

          return updatedResults;
        });
      };

      //  patrol 
      const handlePatrolStarted = async (data: { patrolId: string; patrolData: IPatrol }) => {
        if (!joinedRoomsRef.current.has(data.patrolId)) {
          socket.emit("join_patrol", data.patrolId);
          joinedRoomsRef.current.add(data.patrolId);
        }
        await getPatrolData();
      };

      //  patrol 
      const handlePatrolFinished = async (data: { patrolId: string; patrolData: IPatrol }) => {
        await getPatrolData();
      };

      //  Patrol 
      const handleNewPatrol = async (newPatrol) => {
        if (!joinedRoomsRef.current.has(newPatrol.id)) {
          socket.emit("join_patrol", newPatrol.id);
          joinedRoomsRef.current.add(newPatrol.id);
        }
        await getPatrolData();
      };

      //  Patrol  
      const handlePatrolDeleted = (patrolId) => {
        setAllPatrols((prevPatrols) => prevPatrols.filter((patrol) => patrol.id !== patrolId));
        if (!joinedRoomsRef.current.has(patrolId)) {
          socket.emit("join_patrol", patrolId);
          joinedRoomsRef.current.add(patrolId);
        }
      };

      socket.on("initial_patrol_data", handleInitialData);
      socket.on("patrol_result_update", handleResultUpdate);
      socket.on("patrol_started", handlePatrolStarted);
      socket.on("patrol_finished", handlePatrolFinished);
      socket.on("patrol_created", handleNewPatrol);
      socket.on("patrol_deleted", handlePatrolDeleted);
      setLoading(false);
      return () => {
        socket.off("initial_patrol_data", handleInitialData);
        socket.off("patrol_result_update", handleResultUpdate);
        socket.off("patrol_started", handlePatrolStarted);
        socket.off("patrol_finished", handlePatrolFinished);
        socket.off("patrol_created", handleNewPatrol);
        socket.off("patrol_deleted", handlePatrolDeleted);
      };
    };

    initializeSocketListeners();
  }, [socket, isConnected, user?.id]);

  useEffect(() => {
    allPatrols.forEach((patrol) => {
      if (!joinedRoomsRef.current.has(patrol.id) && patrol.status === 'on_going' || patrol.status === 'scheduled') {
        socket.emit("join_patrol", patrol.id);
        joinedRoomsRef.current.add(patrol.id);
      }
    });
  }, [allPatrols])

  useEffect(() => {
    getPatrolData();
  }, [searchTerm]);

  useEffect(() => {
    localStorage.setItem("filter", JSON.stringify(filter));
  }, [filter]);

  useEffect(() => {
    const sortedData = sortData(allPatrols, sort);
    if (JSON.stringify(sortedData) !== JSON.stringify(allPatrols)) {
      setAllPatrols(sortedData);
    }
  }, [sort, allPatrols]);

  useEffect(() => {
    if (selectedDate !== null || selectedDate !== undefined) {
      setDateError(null);
    }
  }, [selectedDate]);
  if (loading) {
    return <Loading />;
  }

  return (
    <div className="flex flex-col px-6 py-4 gap-4">
      <div className="flex items-center gap-2">
        <Textfield
          iconName="search"
          showIcon={true}
          placeholder={t("Search")}
          onChange={handleSearch}
        />

        <DropdownMenu onOpenChange={(open) => setIsSortOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
    ${isSortOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">swap_vert</span>
            <div className="text-lg">{t("Sort")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="p-2 gap-2">
            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("SortBy")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.by}
              onValueChange={(value) => handleSortChange("by", value)}
            >
              <DropdownMenuRadioItem
                value="Doc No."
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("DocNo")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="Date"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Date")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>

            <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
              {t("Order")}
            </DropdownMenuLabel>
            <DropdownMenuRadioGroup
              value={sort.order}
              onValueChange={(value) => handleSortChange("order", value)}
            >
              <DropdownMenuRadioItem
                value="Ascending"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Ascending")}
              </DropdownMenuRadioItem>
              <DropdownMenuRadioItem
                value="Descending"
                className="text-base"
                onSelect={(e) => e.preventDefault()}
              >
                {t("Descending")}
              </DropdownMenuRadioItem>
            </DropdownMenuRadioGroup>
          </DropdownMenuContent>
        </DropdownMenu>

        <DropdownMenu onOpenChange={(open) => setIsFilterOpen(open)}>
          <DropdownMenuTrigger
            className={`custom-shadow px-[10px] bg-card w-auto h-[40px] gap-[10px] inline-flex items-center justify-center rounded-md text-sm font-medium 
    ${isFilterOpen ? "border border-destructive" : "border-none"}`}
          >
            <span className="material-symbols-outlined">page_info</span>
            <div className="text-lg">{t("Filter")}</div>
          </DropdownMenuTrigger>
          <DropdownMenuContent
            className="flex flex-col justify-center gap-2 p-2 z-50"
            align="end"
          >
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Date")}
              </DropdownMenuLabel>
              <DatePickerWithRange
                startDate={filter?.dateRange.start}
                endDate={filter?.dateRange.end}
                onSelect={handleDateSelect}
                className="my-date-picker"
              />
            </div>
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Status")}
              </DropdownMenuLabel>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("pending")}
                onCheckedChange={(checked) =>
                  toggleStatusFilter("pending", checked)
                }
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="blue"
                  showIcon={true}
                  iconName="hourglass_top"
                  children="Pending"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("scheduled")}
                onCheckedChange={(checked) =>
                  toggleStatusFilter("scheduled", checked)
                }
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="yellow"
                  showIcon={true}
                  iconName="event_available"
                  children="Scheduled"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("on_going")}
                onCheckedChange={(checked) =>
                  toggleStatusFilter("on_going", checked)
                }
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="purple"
                  showIcon={true}
                  iconName="cached"
                  children="On Going"
                />
              </DropdownMenuCheckboxItem>
              <DropdownMenuCheckboxItem
                checked={filter?.patrolStatuses.includes("completed")}
                onCheckedChange={(checked) =>
                  toggleStatusFilter("completed", checked)
                }
                onSelect={(e) => e.preventDefault()}
              >
                <BadgeCustom
                  width="w-full"
                  variant="green"
                  showIcon={true}
                  iconName="check"
                  children="Complete"
                />
              </DropdownMenuCheckboxItem>
            </div>
            <div>
              <DropdownMenuLabel className="p-0 text-sm font-semibold text-muted-foreground">
                {t("Preset")}
              </DropdownMenuLabel>
              <Select
                value={filter?.presetTitle || "All"}
                onValueChange={(value) =>
                  setFilter({
                    presetTitle: value,
                    patrolStatuses: filter?.patrolStatuses || [],
                    dateRange: {
                      start: filter?.dateRange.start,
                      end: filter?.dateRange.end,
                    },
                  })
                }
              >
                <SelectTrigger className="">
                  <SelectValue
                    placeholder={
                      filter?.presetTitle === "All"
                        ? t("All")
                        : filter?.presetTitle
                    }
                  />
                </SelectTrigger>
                <SelectContent>
                  <SelectGroup>
                    <SelectLabel>{t("Preset")}</SelectLabel>
                    <SelectItem value="All">{t("All")}</SelectItem>
                    {allPresets &&
                      allPresets.map((preset) => (
                        <SelectItem value={preset.title} key={preset.id}>
                          {preset.title}
                        </SelectItem>
                      ))}
                  </SelectGroup>
                </SelectContent>
              </Select>
            </div>
            <div className="flex w-full justify-end mt-4 gap-2">
              <Button size="sm" variant="secondary" onClick={resetFilter}>
                {t("Reset")}
              </Button>
              <Button variant="primary" size="sm" onClick={applyFilter}>
                {t("Apply")}
              </Button>
            </div>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
      <ScrollArea className="h-full w-full rounded-md flex-1 [&>[data-radix-scroll-area-viewport]]:max-h-[calc(100vh-160px)]">
        <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-4 gap-4">
          {/* Create Patrol Card with AlertDialog */}
          <AlertDialog>
            <AlertDialogTrigger className="w-full">
              <CreatePatrolCard />
            </AlertDialogTrigger>
            <AlertDialogContent className="sm:w-[90%] xl:w-[60%] h-[670px] px-6 py-4">
              <AlertDialogHeader className="flex">
                <div className="flex flex-col gap-1">
                  <AlertDialogTitle className="text-2xl font-bold text-card-foreground">
                    {t("PatrolPreset")}
                  </AlertDialogTitle>
                  <AlertDialogDescription className="text-base text-input">
                    {t("PleaseSelectAPresetForThePatrol")}
                  </AlertDialogDescription>
                </div>
                <div className="flex items-center justify-center pt-2">
                  <ScrollArea className="h-[500px] w-full rounded-md border-none pr-4 overflow-y-auto">
                    <div className="grid sm:grid-cols-1 xl:grid-cols-2 gap-4">
                      {(() => {
                        const availablePresets = allPresets
                          ? allPresets.filter((preset) => !preset.disabled)
                          : [];
                        return availablePresets.length > 0 ? (
                          availablePresets.map((preset, index) => (
                            <Button
                              key={index}
                              variant={"outline"}
                              className={`custom-shadow bg-secondary grid grid-cols-1 sm:grid-cols-1 h-60 ${selectedPreset === preset
                                ? "border-destructive"
                                : "border-transparent"
                                } flex flex-col py-4 px-6 gap-4 justify-start items-start`}
                              onClick={() => setSelectedPreset(preset)}
                            >
                              {/* Title */}
                              <p className="font-bold text-2xl text-card-foreground">
                                {preset.title}
                              </p>
                              {/* Zone */}

                              <ZoneTooltip zonesName={preset.zones}>
                                <div className="flex flex-row justify-start items-center w-auto gap-1 lg:max-w-[400px] sm:max-w-[620px] truncate">
                                  {/* Positioned Icon */}
                                  <span className="material-symbols-outlined text-2xl text-muted-foreground">
                                    location_on
                                  </span>
                                  <p className="text-muted-foreground min-w-0 text-start truncate overflow-hidden text-base font-semibold whitespace-nowrap text-ellipsis">
                                    {preset.zones
                                      .map((zone) => z(zone))
                                      .join(", ")}
                                  </p>
                                </div>
                              </ZoneTooltip>
                              {/* Description */}
                              <div className="flex flex-row w-full h-full gap-1">
                                {/* Positioned Icon */}
                                <span className="material-symbols-outlined text-2xl text-muted-foreground">
                                  data_info_alert
                                </span>
                                {/* Positioned Textarea */}
                                <div className="w-full h-full ">
                                  <TextTooltip object={preset.description}>
                                    <Textarea
                                      disabled
                                      className="p-0 pointer-events-none border-none shadow-none overflow-hidden text-left resize-none  max-h-full h-20 w-full text-base font-normal line-clamp-3"
                                      value={preset.description}
                                    />
                                  </TextTooltip>
                                </div>

                              </div>
                            </Button>
                          ))
                        ) : (
                          <div className="col-span-full min-h-[261px]">
                            <NotFound
                              icon="deployed_code"
                              title="NoPresetsFound"
                              description="NoPresetsDescription"
                            />
                          </div>
                        );
                      })()}
                    </div>
                  </ScrollArea>
                </div>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <div className="flex items-end justify-end gap-2">
                  <AlertDialogCancel>{t("Cancel")}</AlertDialogCancel>
                  <AlertDialogAction
                    className={buttonVariants({
                      variant: "primary",
                      size: "lg",
                    })}
                    onClick={() => setSecondDialog(true)}
                    disabled={isNextButtonDisabled}
                  >
                    {t("Next")}
                    <span className="material-symbols-outlined text-2xl">
                      chevron_right
                    </span>
                  </AlertDialogAction>
                </div>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>

          {/* Second AlertDialog */}
          <AlertDialog open={secondDialog}>
            <AlertDialogContent className="sm:w-[90%] xl:w-[60%] h-fit px-6 py-4">
              <AlertDialogHeader>
                <div className="flex flex-col gap-1">
                  <AlertDialogTitle className="text-2xl font-bold text-card-foreground">
                    {t("PatrolPreset")}
                  </AlertDialogTitle>
                  <AlertDialogDescription className="text-base text-input">
                    {t("PleaseSelectAPresetForThePatrol")}
                  </AlertDialogDescription>
                </div>
                <div className="flex flex-col gap-1 pt-2">
                  <p className="text-sm font-semibold text-muted-foreground">
                    {" "}
                    {t("Date")}
                  </p>
                  <DatePicker
                    handleSelectedTime={(time: string) => setSelectedDate(time)}
                  />
                  {dateError && (
                    <p className="text-sm font-light italic text-destructive mt-1">
                      {a(dateError)}
                    </p>
                  )}
                </div>
              </AlertDialogHeader>
              <div className="flex flex-col gap-1">
                <p className="text-sm font-semibold text-muted-foreground">
                  {t("Checklist")}
                </p>
                <div className="grid grid-cols-1">
                  <ScrollArea className="pr-2 h-96 w-full rounded-md overflow-visible overflow-y-clip">
                    <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-1 xl:grid-cols-1 gap-4">
                      {selectedPreset?.presetChecklists.flatMap(
                        (presetChecklist: IPresetChecklist) => (
                          <ChecklistDropdown
                            key={presetChecklist.checklist.id}
                            checklist={presetChecklist.checklist}
                            handleselectUser={(selectedUser: IUser) => {
                              handleSelectUser(
                                presetChecklist.checklist.id,
                                selectedUser.id
                              );
                            }}
                          />
                        )
                      )}
                    </div>
                  </ScrollArea>
                </div>
              </div>

              <AlertDialogFooter>
                <div className="flex items-end justify-end gap-2">
                  <AlertDialogCancel
                    onClick={() => {
                      setSecondDialog(false);
                      setDateError(null);
                    }}
                  >
                    {t("Cancel")}
                  </AlertDialogCancel>
                  <AlertDialogAction
                    className={`${buttonVariants({
                      variant: "primary",
                      size: "lg",
                    })} gap-2`}
                    onClick={handleCreatePatrol}
                  >
                    <span className="material-symbols-outlined text-2xl">
                      note_add
                    </span>
                    {t("NewPatrol")}
                  </AlertDialogAction>
                </div>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>

          {isDialogOpen && (
            <AlertCustom
              title={a("PatrolCreateConfirmTitle")}
              description={a("PatrolCreateConfirmDescription")}
              primaryButtonText={t("Confirm")}
              primaryIcon="check"
              secondaryButtonText={t("Cancel")}
              backResult={handleDialogResult}
            ></AlertCustom>
          )}

          {(() => {
            //  preset  disabled
            const availablePatrols = allPatrols
              ? allPatrols.filter((patrol) => !patrol.disabled)
              : [];

            return availablePatrols.length > 0 ? (
              availablePatrols.map((patrol) => (
                <PatrolCard
                  key={patrol.id}
                  status={patrol.status as patrolStatus}
                  date={patrol.date}
                  preset={patrol.preset}
                  itemCounts={patrol.itemCounts}
                  id={patrol.id}
                  results={[...(patrol.results ?? [])]}
                  inspectors={patrol.inspectors}
                  onRemoveSuccess={handleRemoveSuccess}
                />
              ))
            ) : (
              <div className="col-span-full min-h-[261px]">
                <NotFound
                  icon="task"
                  title="NoPatrolsAvailable"
                  description="NoPatrolsDescription"
                />
              </div>
            );
          })()}
        </div>
      </ScrollArea>
    </div>
  );
}

/**
 * :
 *    Profile  
 *
 * Input:
 * - 
 * Output:
 * -  Profile  
 * -  
 **/

"use client";
import bcrypt from "bcryptjs";
import React, { useEffect, useRef, useState } from "react";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Button, buttonVariants } from "@/components/ui/button";
import BadgeCustom from "@/components/badge-custom";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { IUser } from "@/app/type";
import { fetchData, getInitials } from "@/lib/utils";
import Loading from "@/components/loading";
import { useRouter } from "next/navigation";
import { useLocale, useTranslations } from "next-intl";
import { Skeleton } from "@/components/ui/skeleton";
import {
  AlertDialog,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogCancel,
  AlertDialogAction,
  AlertDialogFooter,
} from "@/components/ui/alert-dialog";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";
import Map from "@/components/map";
import Textfield from "@/components/textfield";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";

interface IFormProfile {
  name?: string;
  email?: string;
  age?: number;
  tel?: string;
  address?: string;
  image?: string;
  username?: string;
  password?: string;
  currentPassword?: string;
  newPassword?: string;
  confirmPassword?: string;
}

export default function page() {
  const [nameError, setNameError] = useState<string | null>(null);
  const [emailError, setEmailError] = useState<string | null>(null);
  const [ageError, setAgeError] = useState<string | null>(null);
  const [telError, setTelError] = useState<string | null>(null);
  const [addressError, setAddressError] = useState<string | null>(null);
  const [currentPassError, setCurrentPassError] = useState<string | null>(null);
  const [newPassError, setNewPassError] = useState<string | null>(null);
  const [confirmPassError, setConfirmPassError] = useState<string | null>(null);
  const [isProfileDialogOpen, setIsProfileDialogOpen] = useState(false);
  const [isPasswordDialogOpen, setIsPasswordDialogOpen] = useState(false);
  const [isProfileImageDialogOpen, setIsProfileImageDialogOpen] =
    useState(false);
  const [mounted, setMounted] = useState(false);
  const [pendingAction, setPendingAction] = useState<(() => void) | null>(null);
  const [imageProfile, setImageProfile] = useState<File | null>(null);
  const [userData, setUserData] = useState<IUser>(null);
  const [formData, setFormData] = useState<IFormProfile>();
  const router = useRouter();
  const locale = useLocale();

  useEffect(() => {
    getUserData();
    setMounted(true);
  }, []);

  const z = useTranslations("Zone");
  const a = useTranslations("Alert");
  const t = useTranslations("General");

  const getUserData = async () => {
    try {
      const data = await fetchData(
        "get",
        "/user?profile=true&image=true&password=true",
        true
      );
      setUserData(data);
    } catch (error) {
      console.error("Failed to fetch user data: ", error);
    }
  };

  if (!mounted || !userData) {
    return <Loading />;
  }

  const handleOpenDialog = (dialogType: String) => {
    if (dialogType === "SaveProfileDialog") {
      setIsProfileDialogOpen(true);
    } else if (dialogType === "SaveProfileImageDialog") {
      setIsProfileImageDialogOpen(true);
    } else if (dialogType === "SavePasswordDialog") {
      setIsPasswordDialogOpen(true);
    }
  };

  const handleDialogResult = (result: boolean) => {
    setIsProfileDialogOpen(false);
    setIsProfileImageDialogOpen(false);
    setIsPasswordDialogOpen(false);
    if (result && pendingAction) {
      pendingAction(); // Execute the pending action
      setPendingAction(null); // Clear the pending action
    }
  };

  const handleSaveProfile = () => {
    setPendingAction(() => () => handleUpdateUserData());
    handleOpenDialog("SaveProfileDialog");
  };

  const handleChangePassword = () => {
    setPendingAction(() => () => handleUpdateUserPassword());
    handleOpenDialog("SavePasswordDialog");
  };

  const handleSaveProfileImage = () => {
    setPendingAction(() => () => handleUpdateImageProfile());
    handleOpenDialog("SaveProfileImageDialog");
    setImageProfile(null);
  };

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => {
      const updatedFormData = { ...prev };
      updatedFormData[name] = value;
      return updatedFormData;
    });
  };

  const handleUpdateUserData = async () => {
    let update = false;
    let showErrorToast = false;
    setCurrentPassError(null);
    setNewPassError(null);
    setConfirmPassError(null);
    setNameError(null);
    setEmailError(null);
    setAgeError(null);
    setTelError(null);
    setAddressError(null);

    const userForm = new FormData();

    // name
    if (formData?.name === null || formData?.name === "") {
      setNameError(a("ProfileNameRequire"));
      showErrorToast = true;
    } else if (formData?.name && formData?.name !== userData.profile.name) {
      userForm.append("name", formData.name);
      update = true;
    }

    //  email
    if (formData?.email === null || formData?.email === "") {
      setEmailError(a("ProfileEmailRequire"));
      showErrorToast = true;
    } else if (formData?.email && formData?.email !== userData.email) {
      if (
        formData?.email &&
        formData.email.trim() &&
        /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.email)
      ) {
        userForm.append("email", formData.email);
        update = true;
      } else {
        setEmailError(a("ProfileEmailInvalid"));
        showErrorToast = true;
      }
    }

    //  age
    if (formData?.age !== undefined && formData?.age.toString() === "") {
      setAgeError(a("ProfileAgeRequire"));
      showErrorToast = true;
    } else if (
      formData?.age &&
      formData?.age.toString() !== userData.profile?.age?.toString()
    ) {
      if (
        formData?.age &&
        !isNaN(Number(formData.age)) &&
        Number(formData.age) <= 120 &&
        Number(formData.age) > 0
      ) {
        userForm.append("age", formData.age.toString());
        update = true;
      } else {
        setAgeError(a("ProfileAgeInvalid"));
        showErrorToast = true;
      }
    }

    //  tel
    if (formData?.tel === null || formData?.tel === "") {
      setTelError(a("ProfileTelRequire"));
      showErrorToast = true;
    } else if (formData?.tel && formData?.tel !== userData.profile.tel) {
      if (formData?.tel?.trim() && /^[0-9]{10}$/.test(formData.tel.trim())) {
        userForm.append("tel", formData.tel);
        update = true;
      } else {
        setTelError(a("ProfileTelInvalid"));
        showErrorToast = true;
      }
    }

    //  address
    if (formData?.address === null || formData?.address === "") {
      setAddressError(a("ProfileAddressRequire"));
      showErrorToast = true;
    } else if (
      formData?.address &&
      formData?.address !== userData.profile.address
    ) {
      userForm.append("address", formData.address);
      update = true;
    }

    if (showErrorToast) {
      toast({
        variant: "error",
        title: a("ProfileUpdateErrorTitle"),
        description: a("ProfileUpdateErrorDescription"),
      });
      return;
    }

    if (update) {
      try {
        const response = await fetchData(
          "put",
          `/user/${userData.profile.userId}`,
          true,
          userForm
        );
        if (response) {
          setUserData(response);
          toast({
            variant: "success",
            title: a("ProfileUpdateSuccessTitle"),
            description: a("ProfileUpdateSuccessDescription"),
          });
        }
      } catch (error) {
        console.error("Error updating profile:", error);
        toast({
          variant: "error",
          title: a("ProfileUpdateErrorTitle"),
          description: a("ProfileUpdateErrorDescription"),
        });
      }
    } else {
      toast({
        variant: "default",
        title: a("ProfileNoChangeTitle"),
        description: a("ProfileNoChangeDescription"),
      });
      return;
    }
  };

  const handleUpdateUserPassword = async () => {
    let showErrorToast = false;
    setCurrentPassError(null);
    setNewPassError(null);
    setConfirmPassError(null);
    setNameError(null);
    setEmailError(null);
    setAgeError(null);
    setTelError(null);
    setAddressError(null);

    // Check if all fields are filled
    if (!formData?.currentPassword) {
      setCurrentPassError(a("ProfileCurrentPassRequire"));
      showErrorToast = true;
    }
    if (!formData?.newPassword) {
      setNewPassError(a("ProfileNewPassRequire"));
      showErrorToast = true;
    }
    if (!formData?.confirmPassword) {
      setConfirmPassError(a("ProfileConfirmPassRequire"));
      showErrorToast = true;
    }
    if (formData?.newPassword && formData?.confirmPassword) {
      if (formData?.newPassword.length < 8) {
        setNewPassError("Password must be at least 8 characters long.");
      }
      if (formData?.confirmPassword.length < 8) {
        setConfirmPassError("Password must be at least 8 characters long.");
        return;
      }
    }

    // If there's any error, show toast and stop
    if (showErrorToast) {
      toast({
        variant: "error",
        title: a("ProfileUpdateErrorTitle"),
        description: a("ProfileUpdateErrorDescription"),
      });
      return;
    }

    const passwordMatch = await bcrypt.compare(
      formData?.currentPassword,
      userData?.password
    );

    const isPasswordTheSame = await bcrypt.compare(
      formData?.newPassword,
      userData?.password
    );

    if (formData?.newPassword === formData?.confirmPassword) {
      if (!isPasswordTheSame) {
        if (passwordMatch) {
          const userForm = new FormData();
          userForm.append("password", formData.newPassword);

          try {
            const response = await fetchData(
              "put",
              `/user/${userData.profile.userId}`,
              true,
              userForm
            );
            if (response) {
              setUserData(response);
              toast({
                variant: "success",
                title: a("ProfileUpdateSuccessTitle"),
                description: a("ProfileUpdateSuccessDescription"),
              });
              try {
                await fetchData("post", `/logout`, true);
                window.location.reload();
              } catch (error) {}
            }
          } catch (error) {
            console.error("Error updating password:", error);
            showErrorToast = true;
          }
          return;
        } else {
          setCurrentPassError(a("ProfileCurrentPassInvalid"));
          toast({
            variant: "error",
            title: a("ProfileUpdateErrorTitle"),
            description: a("ProfileUpdateErrorDescription"),
          });
          return;
        }
      } else {
        toast({
          variant: "default",
          title: a("ProfileNoChangeTitle"),
          description: a("ProfileNoChangeDescription"),
        });
      }
    } else {
      setConfirmPassError(a("ProfileConfirmPassInvalid"));
      toast({
        variant: "error",
        title: a("ProfileUpdateErrorTitle"),
        description: a("ProfileUpdateErrorDescription"),
      });
      return;
    }
  };

  const handleUpdateImageProfile = async () => {
    const imageForm = new FormData();
    imageForm.append("imageProfile", imageProfile);
    try {
      if (imageProfile) {
        await fetchData("put", "/profile", true, imageForm, true);
      }
      toast({
        variant: "success",
        title: a("ProfileImageUpdateSuccessTitle"),
        description: a("ProfileImageUpdateSuccessDescription"),
      });
      getUserData();
    } catch (error) {
      console.error("Error updating image profile:", error);
      toast({
        variant: "error",
        title: a("ProfileImageUpdateErrorTitle"),
        description: a("ProfileImageUpdateErrorDescription"),
      });
    }
  };

  const handleButtonClick = (event: React.FormEvent) => {
    event.preventDefault();
    document.getElementById("file-input")?.click();
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setImageProfile(event.target.files[0]); // Store the new image file in state
    } else {
      setImageProfile(null); // Reset the image if no file is selected
    }
  };

  const handCancelImageProfile = () => {
    setImageProfile(null); // Reset the image if no file is selected
  };

  return (
    <div className="flex flex-col py-4 px-6 gap-4">
      <div className="text-2xl font-bold">{t("ViewProfile")}</div>

      {/* first block */}
      <div className="flex justify-between px-6 py-4 bg-card rounded-md custom-shadow">
        <div className="flex flex-row">
          {userData.profile ? (
              <Avatar className="h-32 w-32 mr-6">
                <AvatarImage
                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${userData.profile.image?.path}`}
                />
                <AvatarFallback id={userData.id?.toString()}>
                  <p className="text-4xl">
                    {getInitials(userData.profile.name)}
                  </p>
                </AvatarFallback>
              </Avatar>
          ) : (
            <Skeleton className="h-12 w-12 rounded-full" />
          )}

          <div className="flex flex-col justify-center items-start">
            <div className="text-2xl font-bold py-4">
              {t("WelcomeBack")}, {userData.profile.name}
            </div>

            <AlertDialog>
              <AlertDialogTrigger>
                <Button
                  className="custom-shadow"
                  variant="outline"
                  size="default"
                >
                  {t("UploadNewPhoto")}
                </Button>
              </AlertDialogTrigger>

              <AlertDialogContent className="flex flex-col px-6 py-4 w-[400px] h-fit">
                <AlertDialogHeader>
                  <AlertDialogTitle className="text-2xl font-semibold">
                    {t("ProfileImage")}
                  </AlertDialogTitle>
                  <AlertDialogDescription className="flex items-start justify-start text-base text-input">
                    {t("ProfileImageDescription")}
                  </AlertDialogDescription>
                </AlertDialogHeader>

                <div className="flex gap-2">
                  <div>
                    <input
                      name="image"
                      id="file-input"
                      placeholder={null}
                      type="file"
                      className="hidden"
                      onChange={handleFileChange}
                    />
                    <Button
                      className="flex justify-center items-center h-[40px] w-[40px]"
                      variant="primary"
                      onClick={handleButtonClick}
                    >
                      <span className="material-symbols-outlined text-2xl">
                        upload
                      </span>
                    </Button>
                  </div>
                </div>

                <div className="flex justify-center items-center mb-2">
                  {imageProfile ? (
                    <Avatar className="h-80 w-80">
                      <AvatarImage src={URL.createObjectURL(imageProfile)} />
                      <AvatarFallback id={userData.id.toString()}>
                        <p className="text-6xl">
                          {getInitials(userData.profile.name)}
                        </p>
                      </AvatarFallback>
                    </Avatar>
                  ) : (
                    <Avatar className="h-80 w-80">
                      <AvatarImage
                        src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${userData.profile.image?.path}`}
                      />
                      <AvatarFallback id={userData.id.toString()}>
                        <p className="text-6xl">
                          {getInitials(userData.profile.name)}
                        </p>
                      </AvatarFallback>
                    </Avatar>
                  )}
                </div>

                <AlertDialogFooter>
                  <div className="flex items-end justify-end gap-2">
                    <AlertDialogCancel onClick={handCancelImageProfile}>
                      {t("Cancel")}
                    </AlertDialogCancel>
                    <AlertDialogAction
                      className={`bg-primary hover:bg-primary/70 
                ${!imageProfile ? "opacity-50 cursor-not-allowed" : ""}`}
                      onClick={handleSaveProfileImage}
                      disabled={!imageProfile ? true : false}
                    >
                      <span className="material-symbols-outlined text-2xl">
                        save
                      </span>
                      {t("Save")}
                    </AlertDialogAction>
                  </div>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </div>
        </div>

        <div className="flex flex-col items-end justify-between">
          <BadgeCustom
            variant={
              userData.role === "supervisor"
                ? "yellow"
                : userData.role === "inspector"
                ? "red"
                : "blue"
            }
            showIcon={true}
            shape="square"
            iconName={
              userData.role === "supervisor"
                ? "engineering"
                : userData.role === "inspector"
                ? "person_search"
                : "manage_accounts"
            }
          >
            {t(userData.role)}
          </BadgeCustom>

          {userData.zone ? (
            <AlertDialog>
              <AlertDialogTrigger className="flex justify-center items-center min-w-72 px-6 py-4 bg-secondary gap-2 rounded-md">
                <div className="flex flex-row items-center gap-1 text-muted-foreground">
                  <span className="material-symbols-outlined text-[22px] pr-1">
                    location_on
                  </span>
                  <p className="text-base font-semibold">{t("ZoneText")}</p>
                </div>
                <p>{z(userData.zone.name)}</p>
              </AlertDialogTrigger>

              <AlertDialogContent className="w-full sm:w-[50%] md:w-[70%] lg:w-[100%] max-w-[1200px] rounded-md px-6 py-4">
                <div className="flex flex-col gap-1">
                  <div className="text-muted-foreground flex items-center gap-1">
                    <span className="material-symbols-outlined">
                      location_on
                    </span>
                    <p>{t("Zone")}</p>
                  </div>
                  <div className=" flex justify-center bg-secondary rounded-md py-4">
                    <Map
                      disable={true}
                      initialSelectedZones={[userData.zone.id]}
                    />
                  </div>
                </div>

                <AlertDialogFooter>
                  <AlertDialogAction
                    className={buttonVariants({
                      variant: "secondary",
                      size: "lg",
                    })}
                  >
                    {t("Close")}
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          ) : null}
        </div>
      </div>

      <Tabs defaultValue="profile" className="w-full">
        <TabsContent className="w-full" value="profile">
          <div className="flex flex-col bg-card w-full py-4 px-6 rounded-md custom-shadow">
            <div className="flex flex-col gap-4">
              <TabsList className="flex gap-1 bg-secondary w-[233px] h-10">
                <TabsTrigger value="profile" className="flex gap-2 w-[98px]">
                  <span className="material-symbols-outlined">
                    account_circle
                  </span>
                  <div className=" font-bold">Profile</div>
                </TabsTrigger>
                <TabsTrigger value="password" className="flex gap-2 w-[119px]">
                  <span className="material-symbols-outlined">lock</span>
                  <div className=" font-bold">Password</div>
                </TabsTrigger>
              </TabsList>
              <div className="flex flex-row justify-between">
                {/* edit profile */}
                <div className="flex flex-col w-full">
                  <div className="flex flex-col gap-2 mb-4">
                    <div className="text-2xl font-bold ">
                      {t("EditProfile")}
                    </div>
                    <div>{t("EditProfileDescription")}</div>
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Name")}
                    </p>
                    <Input
                      defaultValue={
                        userData.profile.name ? userData.profile.name : "-"
                      }
                      name="name"
                      onChange={handleInputChange}
                      placeholder={
                        userData.profile.name ? userData.profile.name : "-"
                      }
                      className="text-xl font-normal bg-secondary"
                    ></Input>
                    {nameError && (
                      <p className="ttext-sm font-light text-destructive italic mt-1">
                        {nameError}
                      </p>
                    )}
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Email")}
                    </p>
                    <Input
                      defaultValue={userData.email ? userData.email : "-"}
                      name="email"
                      onChange={handleInputChange}
                      placeholder={userData.email ? userData.email : "-"}
                      className="text-xl font-normal bg-secondary"
                    ></Input>
                    {emailError && (
                      <p className="text-sm font-light text-destructive italic mt-1">
                        {emailError}
                      </p>
                    )}
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Age")}
                    </p>
                    <Input
                      defaultValue={
                        userData.profile.age ? userData.profile.age : "-"
                      }
                      name="age"
                      onChange={handleInputChange}
                      placeholder={
                        String(userData.profile.age)
                          ? String(userData.profile.age)
                          : "-"
                      }
                      className="text-xl font-normal bg-secondary"
                    ></Input>
                    {ageError && (
                      <p className="ttext-sm font-light text-destructive italic mt-1">
                        {ageError}
                      </p>
                    )}
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Tel")}
                    </p>
                    <Input
                      defaultValue={
                        userData.profile.tel ? userData.profile.tel : "-"
                      }
                      name="tel"
                      onChange={handleInputChange}
                      placeholder={
                        userData.profile.tel ? userData.profile.tel : "-"
                      }
                      className="text-xl font-normal bg-secondary"
                    ></Input>
                    {telError && (
                      <p className="ttext-sm font-light text-destructive italic mt-1">
                        {telError}
                      </p>
                    )}
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Address")}
                    </p>
                    <Textarea
                      defaultValue={
                        userData.profile.address
                          ? userData.profile.address
                          : "-"
                      }
                      name="address"
                      onChange={handleInputChange}
                      placeholder={
                        userData.profile.address
                          ? userData.profile.address
                          : "-"
                      }
                      className="text-xl font-normal bg-secondary custom-shadow h-56"
                    ></Textarea>
                    {addressError && (
                      <p className="ttext-sm font-light text-destructive italic mt-1">
                        {addressError}
                      </p>
                    )}
                  </div>
                </div>
              </div>
            </div>
            {/* Button Back And Save    */}
            <div className="flex justify-end items-end gap-2">
              <Button
                variant="secondary"
                size="lg"
                onClick={() => router.push(`/${locale}/`)}
              >
                {t("Back")}
              </Button>
              <Button variant="primary" size="lg" onClick={handleSaveProfile}>
                <span className="material-symbols-outlined text-2xl">save</span>
                {t("Save")}
              </Button>
            </div>
          </div>
        </TabsContent>
        <TabsContent className="w-full" value="password">
          <div className="flex  flex-col bg-card py-4 px-6 rounded-md custom-shadow">
            <div className="flex flex-col gap-4">
              <TabsList className="flex gap-1 bg-secondary w-[233px] h-10">
                <TabsTrigger value="profile" className="flex gap-2 w-[98px]">
                  <span className="material-symbols-outlined">
                    account_circle
                  </span>
                  <div className=" font-bold">Profile</div>
                </TabsTrigger>
                <TabsTrigger value="password" className="flex gap-2 w-[119px]">
                  <span className="material-symbols-outlined">lock</span>
                  <div className=" font-bold">Password</div>
                </TabsTrigger>
              </TabsList>
              <div className="flex flex-row justify-between">
                {/* manage account */}
                <div className="flex flex-col w-full">
                  <div className="flex flex-col gap-2 mb-4">
                    <div className="text-2xl font-bold ">
                      {t("ManageAccount")}
                    </div>
                    <div>{t("EditPasswordDescription")}</div>
                  </div>

                  <div className="w-[415px] mb-4">
                    <p className="text-base font-semibold text-muted-foreground mb-1">
                      {t("Username")}
                    </p>
                    <Input
                      name="username"
                      onChange={handleInputChange}
                      placeholder={userData.username ? userData.username : "-"}
                      className="text-xl font-normal bg-secondary"
                      readOnly
                    ></Input>
                  </div>

                  <div className="flex flex-col w-[415px] mb-4 gap-1">
                    <p className="flex gap-1 text-base font-semibold text-muted-foreground">
                      {t("CurrentPassword")}
                      <p className="text-destructive">*</p>
                    </p>
                    <Textfield
                      name="currentPassword"
                      className="text-xl font-normal bg-secondary"
                      type="password"
                      showIcon={true}
                      iconName="lock"
                      placeholder=""
                      onChange={handleInputChange}
                    />
                    {currentPassError && (
                      <p className="text-sm font-light text-destructive italic mt-1">
                        {currentPassError}
                      </p>
                    )}
                  </div>

                  <div className="flex flex-col w-[415px] mb-4 gap-1">
                    <p className="flex gap-1 text-base font-semibold text-muted-foreground">
                      {t("NewPassword")}
                      <p className="text-destructive">*</p>
                    </p>
                    <Textfield
                      name="newPassword"
                      className="text-xl font-normal bg-secondary"
                      type="password"
                      showIcon={true}
                      iconName="key"
                      placeholder=""
                      onChange={handleInputChange}
                    />
                    {newPassError && (
                      <p className="text-sm font-light text-destructive italic mt-1">
                        {newPassError}
                      </p>
                    )}
                  </div>

                  <div className="flex flex-col w-[415px] mb-4 gap-1">
                    <p className="flex gap-1 text-base font-semibold text-muted-foreground">
                      {t("ConfirmPassword")}
                      <p className="text-destructive">*</p>
                    </p>
                    <Textfield
                      name="confirmPassword"
                      className="text-xl font-normal bg-secondary"
                      type="password"
                      showIcon={true}
                      iconName="check_circle"
                      placeholder=""
                      onChange={handleInputChange}
                    />
                    {confirmPassError && (
                      <p className="text-sm font-light text-destructive italic mt-1">
                        {confirmPassError}
                      </p>
                    )}
                  </div>
                </div>
              </div>
            </div>
            {/* Button Back And Save    */}
            <div className="flex justify-end items-end gap-2">
              <Button
                variant="secondary"
                size="lg"
                onClick={() => router.push(`/${locale}/`)}
              >
                {t("Back")}
              </Button>
              <Button
                variant="primary"
                size="lg"
                onClick={handleChangePassword}
              >
                <span className="material-symbols-outlined text-2xl">save</span>
                {t("Save")}
              </Button>
            </div>
          </div>
        </TabsContent>
      </Tabs>

      {isProfileDialogOpen && (
        <AlertCustom
          title={a("ProfileUpdateConfirmTitle")}
          description={a("ProfileUpdateConfirmDescription")}
          primaryButtonText={t("Confirm")}
          primaryIcon="check"
          secondaryButtonText={t("Cancel")}
          backResult={handleDialogResult}
        ></AlertCustom>
      )}
      {isPasswordDialogOpen && (
        <AlertCustom
          title={a("ConfirmPasswordChange")}
          description={a("ConfirmPasswordChangeDescription")}
          primaryButtonText={t("Confirm")}
          primaryIcon="check"
          secondaryButtonText={t("Cancel")}
          backResult={handleDialogResult}
        ></AlertCustom>
      )}
      {isProfileImageDialogOpen && (
        <AlertCustom
          title={a("ProfileImageUpdateConfirmTitle")}
          description={a("ProfileImageUpdateConfirmDescription")}
          primaryButtonText={t("Confirm")}
          primaryIcon="check"
          secondaryButtonText={t("Cancel")}
          backResult={handleDialogResult}
        ></AlertCustom>
      )}
    </div>
  );
}

"use client";

import { useEffect } from "react";
import { fetchData } from "@/lib/utils";
import { useRouter } from "next/navigation";
import Loading from "@/components/loading";
import { useLocale } from "next-intl";

export default function RefreshPage() {
    const router = useRouter();
    const locale = useLocale()
    useEffect(() => {
        async function refresh() {
            try {
                const result = await fetchData("post",'/refresh-token',true)
                if (result.status !== 200) {
                    router.push(`/${locale}/login`);
                }
                router.push(`/`);
            } catch (error) {
                router.push(`/${locale}/login`);
            }

        }

        refresh();
    }, []);

    return <Loading />;
}

import { z } from "zod"
import { timeStamp } from 'console';
import { useTranslations } from "next-intl";
import { badgeVariants } from "@/components/badge-custom";

export type patrolStatus = "pending" | "scheduled" | "on_going" | "completed"
export type role = "admin" | "inspector" | "supervisor"
export type defectStatus = "reported" | "completed" | "pending_inspection" | "in_progress" | "resolved";
export type itemType = "safety" | "environment" | 'maintenance';
export type notificationType = "information" | "request" | 'system';

export interface IUser {
    id: number;
    username?: string;
    email?: string;
    password?: any;
    role: role;
    department?: string | null;
    createdAt?: string;
    active?: boolean;
    resetToken?: string;

    presets?: IPreset[];
    checklists?: IChecklist[]
    comments?: IComment[]
    profile: IProfile;
    notifications?: INotification[]
    defects?: IDefect[]
    zone?: IZone;
    patrolChecklists?: IPatrolChecklist[]
    images?: IImage[]
}

export interface IDefect {
    title: string;
    id: number;
    name: string;
    description: string;
    type: itemType;
    status: defectStatus;
    startTime: string;
    endTime: string;
    userId?: number;
    supervisorId?: number;
    patrolResultId?: number;
    timeStamp: Date;

    user?: IUser;
    zone?: IZone;
    supervisor?: IUser;
    patrolResult: IPatrolResult;
    images: IDefectImage[];
}

export interface INotification {
    id: number
    message: string
    read: boolean,
    timestamp: string
    type: notificationType
    url?: string
    userId: number

    user?: IUser
}

export interface IProfile {
    id: number;
    name: string;
    age?: number;
    tel?: string;
    address?: string;
    userId: number
    imageId: number | null

    image?: IImage | null;
    user?: IUser;
}


export interface IPatrol {
    id: number;
    date: string;
    startTime: string | null;
    endTime: string | null;
    duration: string | null;
    status: patrolStatus;
    presetId: number;

    patrolChecklists: IPatrolChecklist[];
    preset: IPreset;
    results: IPatrolResult[];
    itemCounts: number;
    inspectors?: IUser[]
    disabled?: boolean;
}

export interface IPatrolChecklist {
    id?: number;
    patrolId?: number;
    checklistId: number;
    userId: number;

    patrol?: IPatrol;
    checklist?: IChecklist;
    inspector?: IUser;
}

export interface IPreset {
    id: number;
    title: string;
    description: string;
    version: number;
    latest: boolean;
    updatedAt: string;
    updatedBy: number;

    user?: IUser;
    presetChecklists?: IPresetChecklist[];
    patrols?: IPatrol[];
    zones?: IZone[];
    versionCount?: number;
    disabled?: boolean;
}

export interface IPresetChecklist {
    presetId: number;
    checklistId: number;

    checklist: IChecklist;
    preset: IPreset;
}

export interface IChecklist {
    id: number;
    title: string;
    version: number;
    latest: boolean;
    updatedAt: string;
    updatedBy: number;

    patrols?: IPatrolChecklist[];
    user: IUser;
    presetChecklists?: IPresetChecklist[];
    items: IItem[];
}

export interface IPatrolResult {
    inspectorId: number
    id: number;
    status: boolean;
    itemId: number;
    zoneId: number;
    patrolId?: number;
    supervisorId?: number;

    comments?: IComment[];
    defects?: IDefect[]
    itemZone?: IItemZone;
    patrol?: IPatrol;
    supervisor?: IUser;
}

export interface IItem {
    id: number;
    name: string;
    type: itemType;
    checklistId: number;

    itemZones: IItemZone[];
    checklist?: IChecklist
}

export interface IZone {
    id: number;
    name: string;
    locationId?: number;
    userId?: number;
    pathData?: string;
    text?: {
        x: number;
        y: number;
        fontSize: number;
        rotation: number;
    } | null
    supervisor?: IUser;
    defects?: number;
    dashboard?: {
        defectReported: IDashboardCard;
        defectCompleted: IDashboardCard;
        defectPending: IDashboardCard;
        chartData: IZoneChartDataItem[];
        defectTrend: number;
        defects?: IDefect[];
    }
}

interface IZoneChartDataItem {
    month: string;
    defect: number;
}

export interface IItemZone {
    itemId: number;
    zoneId: number;

    results?: IPatrolResult;
    item: IItem;
    zone: IZone;
}

export interface ILocation {
    id: number
    name: string

    zones: IZone[]
}

export interface IComment {
    id: number;
    message: string;
    timestamp: string;
    status: boolean;
    userId: number;
    supervisorId: number;
    patrolResultId: number;

    user: IUser;
    supervisor: IUser;
    patrolResult: IPatrolResult;
}


export interface IImage {
    id: number;
    path: string;
    timestamp?: string;
    updateBy?: number;

    user?: IUser;
    profiles: IProfile[];
    defects: IDefectImage[];
}

export interface IDefectImage {
    defectId?: number
    imageId?: number
    image: IImage
}

















// filter 

export interface IFilterPatrol {
    presetTitle: string | null;
    patrolStatuses: string[];
    dateRange: { start: Date | undefined; end: Date | undefined };
}

export interface IFilterDefect {
    defectStatus: string | null;
    defectTypes: string[];
    dateRange: { start: Date | undefined; end: Date | undefined };
}
export interface IFilterComment {
    commentStatus: string | null;
    dateRange: { start: Date | undefined; end: Date | undefined };
}


export const LoginSchema = z.object({
    username: z.string().min(1, { message: "LoginUsernameRequire" }),
    password: z.string().min(1, { message: "LoginPasswordRequire" }),
    rememberMe: z.boolean().optional()
})

export interface IToast {
    variant: "default" | "error" | "success";
    title: string;
    description: string;
}


export interface IHeatmapZone {
    id: number;
    name: string;
    defects: number;
}

export interface IHeatMap {
    data: IHeatmapZone[];
}

export interface IDefectCategory {
    chartData: IDefectCategoryItem[];
    trend: number;
}
export interface IDefectCategoryItem {
    type: string;
    amounts: number;
    fill: string;
}

export interface ICommonDefectItem {
    name: string;
    amounts: number;
    fill: string;
}
export interface IPatrolCompletionRate {
    chartData: IPatrolCompletionRateItem[];
    trend: number;
    percent: number;
}
export interface IPatrolCompletionRateItem {
    noDefect: number;
    withDefect: number;
}
export interface IDashboardCard {
    title: string;
    value: number;
    trend?: number;
    icon: string;
    variant: keyof typeof badgeVariants;
    positive?: boolean;
}

export interface IPatrolDuration {
    duration: number;
}

/**
 * :
 *    AlertDefect  Alert ,   
 * Input:
 * - defect: 
 * - item: 
 * - type:  Alert 
 * - patrolResults: 
 * - result: 
 * - response:  IDefect 
 * Output:
 * - JSX  AlertDefect , , 2  Action 
 **/

"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { defectStatus, IDefect } from "@/app/type";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import {
  AlertDialog,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogCancel,
  AlertDialogAction,
  AlertDialogFooter,
} from "@/components/ui/alert-dialog";
import { IItem, IItemZone, IPatrolResult } from "@/app/type";
import React, { useEffect, useState } from "react";
import { fetchData, getInitials } from "@/lib/utils";
import { useTranslations } from "next-intl";
import { ScrollArea } from "@/components/ui/scroll-area";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "./ui/tooltip";
import { Skeleton } from "./ui/skeleton";
import { toast } from "@/hooks/use-toast";
import { AlertCustom } from "@/components/alert-custom";
import { AspectRatio } from "@/components/ui/aspect-ratio";
import { UserTooltip } from "./user-tooltip";
interface IAlertDefect {
  userId?: number;
  defect?: IDefect;
  item?: IItem;
  type: "edit" | "report" | "resolve" | "edit-resolve";
  patrolResults?: IPatrolResult[];
  result?: {
    id: number;
    inspectorId: number;
    itemId: number;
    zoneId: number;
    status: boolean;
    supervisorId?: number;
  };
  response: (defect: IDefect) => void;
}

export default function AlertDefect({
  userId,
  defect,
  item,
  type,
  patrolResults,
  result,
  response,
}: IAlertDefect) {
  const disabled = false;
  const a = useTranslations("Alert");
  const t = useTranslations("General");
  const z = useTranslations("Zone");

  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [defectDescription, setDefectDescription] = useState<string>("");

  const [isAlertOpen, setIsAlertOpen] = useState(false);
  const [isAlertDefectOpen, setAlertDefectOpen] = useState(true);

  const [detailError, setDetailError] = useState<string | null>(null);
  const [fileError, setFileError] = useState<string | null>(null);

  const closeAlertDefect = () => {
    setAlertDefectOpen(false);
  };

  const openAlertDefect = () => {
    setAlertDefectOpen(true);
  };

  const handleOpenDialog = () => {
    setIsAlertOpen(true);
  };

  const handleCloseDialog = () => {
    setIsAlertOpen(false);
  };

  const handleDefectDescription = (
    event: React.ChangeEvent<HTMLTextAreaElement>
  ) => {
    const { value } = event.target;
    setDefectDescription(value);
  };

  // 
  const validImageTypes = ["image/jpeg", "image/png", "image/jpg", "image/gif"];

  const handleCreateDefect = async (
    name: string,
    description: string,
    type: string,
    userId: number,
    patrolResultId: number | null,
    supervisorId: number,
    files: File[]
  ) => {
    setDetailError(null);
    setFileError(null);
    if (!description || !files || files?.length === 0) {
      setDetailError(!description ? a("ReportDefectErrorMissingDetail") : null);
      setFileError(
        !files || files?.length === 0
          ? a("ReportDefectErrorMissingImage")
          : null
      );
      toast({
        variant: "error",
        title: a("ReportDefectMissingField"),
        description:
          (!description && !files) || files?.length === 0
            ? a("ReportDefectMissingDetailAndImage")
            : !description
            ? a("ReportDefectMissingDetail")
            : a("ReportDefectMissingImage"),
      });
      return;
    }
    const invalidFiles = files.filter(
      (file) => !validImageTypes.includes(file.type)
    );

    if (invalidFiles.length > 0) {
      setFileError(a("ReportDefectErrorInvalidImageMessage"));
      toast({
        variant: "error",
        title: a("ReportDefectErrorInvalidImage"),
        description: a("ReportDefectErrorInvalidImageMessage"),
      });
      return;
    }
    const defectData = {
      name,
      description,
      type,
      defectUserId: userId?.toString(),
      patrolResultId: patrolResultId.toString(),
      supervisorId: supervisorId.toString(),
    };

    try {
      const createDefect = await fetchData(
        "post",
        "/defect",
        true,
        defectData,
        false
      );
      const id = createDefect.id;

      //  request 
      const formData = new FormData();
      formData.append("status", "reported");
      files.forEach((file) => formData.append("imageFiles", file));

      const defectImage = await fetchData(
        "put",
        `/defect/${id}/upload`,
        true,
        formData,
        true //  FormData
      );
      if (defectImage) {
        toast({
          variant: "success",
          title: a("ReportDefectTitle"),
          description: a("ReportDefectDescription"),
        });
        closeAlertDefect();
      }

      createDefect.images = defectImage.images;
      createDefect.images = defectImage.images;
      response(createDefect);
      setDetailError(null);
      setFileError(null);
      setSelectedFiles(null);
      setDefectDescription(null);
    } catch (error) {
      console.error("Error creating defect:", error);
    }
  };

  const handleEditDefect = async (
    id: number,
    name: string,
    description: string,
    type: string,
    userId: number,
    patrolResultId: number | null,
    supervisorId: number,
    files: File[]
  ) => {
    setDetailError(null);
    setFileError(null);
    if (!description || !files || files?.length === 0) {
      setDetailError(!description ? a("ReportDefectErrorMissingDetail") : null);
      setFileError(
        !files || files?.length === 0
          ? a("ReportDefectErrorMissingImage")
          : null
      );
      toast({
        variant: "error",
        title: a("ReportDefectMissingField"),
        description:
          (!description && !files) || files?.length === 0
            ? a("ReportDefectMissingDetailAndImage")
            : !description
            ? a("ReportDefectMissingDetail")
            : a("ReportDefectMissingImage"),
      });
      return;
    }

    const invalidFiles = files.filter(
      (file) => !validImageTypes.includes(file.type)
    );

    if (invalidFiles.length > 0) {
      setFileError(a("ReportDefectErrorInvalidImageMessage"));
      toast({
        variant: "error",
        title: a("ReportDefectErrorInvalidImage"),
        description: a("ReportDefectErrorInvalidImageMessage"),
      });
      return;
    }

    const formData = new FormData();

    formData.append("name", name);
    formData.append("description", description);
    formData.append("type", type);
    formData.append("status", "reported");
    formData.append("defectUserId", userId.toString());
    formData.append("supervisorId", supervisorId.toString());
    formData.append("deleteExistingImages", "true");

    files?.forEach((file) => {
      formData.append("imageFiles", file);
    });
    try {
      const updateDefect = await fetchData(
        "put",
        `/defect/${id}`,
        true,
        formData,
        true
      );

      if (updateDefect) {
        toast({
          variant: "success",
          title: a("EditReportDefectTitle"),
          description: a("EditReportDefectDescription"),
        });
        closeAlertDefect();
      }
      setDetailError(null);
      setFileError(null);
      setSelectedFiles(null);
      setDefectDescription(null);
      response(updateDefect);
    } catch (error) {
      console.error("Error edit defect:", error);
    }
  };

  const handleResolveDefect = async (
    id: number,
    files: File[],
    type: string,
    deleteExistingImages: boolean
  ) => {
    setDetailError(null);
    setFileError(null);
    if (!files || files?.length === 0) {
      setFileError(
        !files || files?.length === 0
          ? a("ReportDefectErrorMissingImage")
          : null
      );
      toast({
        variant: "error",
        title: a("ReportDefectMissingField"),
        description: a("ReportDefectMissingImage"),
      });
      return;
    }

    const invalidFiles = files.filter(
      (file) => !validImageTypes.includes(file.type)
    );

    if (invalidFiles.length > 0) {
      setFileError(a("ReportDefectErrorInvalidImageMessage"));
      toast({
        variant: "error",
        title: a("ReportDefectErrorInvalidImage"),
        description: a("ReportDefectErrorInvalidImageMessage"),
      });
      return;
    }

    const formData = new FormData();

    formData.append("status", "resolved");
    formData.append("defectUserId", defect.userId.toString());
    formData.append("supervisorId", defect.supervisor.id.toString());
    formData.append("deleteExistingImages", "true");
    files?.forEach((file) => {
      formData.append("imageFiles", file);
    });

    try {
      const resolveDefect = await fetchData(
        "put",
        `/defect/${id}`,
        true,
        formData,
        true
      );
      if (resolveDefect) {
        if (type === "resolve") {
          toast({
            variant: "success",
            title: a("DefectResolveTitle"),
            description: a("DefectResolveDescription"),
          });
        }
        if (type === "edit") {
          toast({
            variant: "success",
            title: a("EditDefectResolveTitle"),
            description: a("EditDefectResolveDescription"),
          });
        }
        closeAlertDefect();
      }
      setDetailError(null);
      setFileError(null);
      setSelectedFiles(null);
      response(resolveDefect);
    } catch (error) {
      console.error("Error resolve defect:", error);
    }
  };

  const handleSendClick = (event: React.FormEvent) => {
    event.preventDefault();
    event.stopPropagation();
    handleOpenDialog();
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setSelectedFiles([
        ...(selectedFiles ?? []),
        ...Array.from(event.target.files),
      ]);
    }
  };

  const handleRemoveFile = (index: number) => {
    setSelectedFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  const handleDrop = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    const files = event.dataTransfer.files;
    if (files && files.length > 0) {
      setSelectedFiles([...selectedFiles, ...Array.from(files)]);
    }
  };

  const handleButtonClick = (event: React.FormEvent) => {
    event.preventDefault();
    document.getElementById("file-input")?.click();
  };

  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
  };

  if (!patrolResults && type === "report") {
    return <Skeleton />;
  }

  return (
    <div>
      <AlertDialog>
        <AlertDialogTrigger disabled={disabled}>
          <Button
            variant={
              type === "resolve" || type === "edit-resolve"
                ? "primary"
                : type === "edit"
                ? "primary"
                : "outline"
            }
            size={"lg"}
            onClick={() => openAlertDefect()}
          >
            <span className="material-symbols-outlined pr-2 ">
              {type === "edit" || type === "edit-resolve"
                ? "edit"
                : type === "resolve"
                ? "published_with_changes"
                : "campaign"}
            </span>
            {t(
              type === "edit" || type === "edit-resolve"
                ? "Edit"
                : type === "resolve"
                ? "Resolve"
                : "Report"
            )}
          </Button>
        </AlertDialogTrigger>
        {isAlertDefectOpen && (
          <AlertDialogContent className="sm:w-[90%] xl:w-[60%]">
            <AlertDialogHeader>
              <AlertDialogTitle className="text-2xl font-semibold">
                {t(
                  type === "edit"
                    ? "EditDefect"
                    : type === "resolve"
                    ? "ResolveDefect"
                    : type === "edit-resolve"
                    ? "EditResolveDefect"
                    : "ReportDefect"
                )}
              </AlertDialogTitle>
              <AlertDialogDescription className="flex items-start justify-start text-lg text-input">
                {t(
                  type === "edit"
                    ? "PleaseProvideDetailsForTheDefect"
                    : type === "resolve"
                    ? "PleaseProvideResolvedTheDefect"
                    : type === "edit-resolve"
                    ? "EditResolvedTheDefect"
                    : "PleaseProvideDetailsForTheDefect"
                )}
              </AlertDialogDescription>
              <div className="flex flex-col justify-start">
                <p className="font-semibold">
                  {type === "report" ? item.name : defect.name}
                </p>
                <div className="flex items-center">
                  <span className="material-symbols-outlined text-2xl me-2">
                    location_on
                  </span>
                  <p className="font-semibold me-2">{t("Zone")}</p>
                  <p>
                    <p>
                      {type === "report"
                        ? item.itemZones.map((itemZone: IItemZone) => {
                            return result.zoneId === itemZone.zone.id
                              ? z(itemZone.zone.name)
                              : null;
                          })
                        : z(defect.patrolResult.itemZone.zone.name)}
                    </p>
                  </p>
                </div>

                <div className="flex items-center">
                  <span className="material-symbols-outlined text-2xl me-2">
                    engineering
                  </span>

                  <p className="font-semibold me-2">{t("supervisor")}</p>
                  {type === "report" ? (
                    item.itemZones.map((itemZone: IItemZone) => {
                      if (result.zoneId === itemZone.zone.id) {
                        const patrolResult = patrolResults.find(
                          (patrolResult: IPatrolResult) =>
                            patrolResult.zoneId === itemZone.zone.id
                        );
                        const supervisor = patrolResult?.supervisor;
                        return (
                          <div className="flex items-center gap-1">
                            <UserTooltip user={supervisor}>
                              <Avatar className="custom-shadow h-[35px] w-[35px]">
                                <AvatarImage
                                  src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${supervisor.profile.image?.path}`}
                                />
                                <AvatarFallback id={supervisor.id.toString()}>
                                  {getInitials(supervisor.profile.name)}
                                </AvatarFallback>
                              </Avatar>
                            </UserTooltip>
                            <span className="text-card-foreground text-lg truncate">
                              {supervisor.profile.name}
                            </span>
                          </div>
                        );
                      }
                      return null;
                    })
                  ) : (
                    <div className="flex items-center gap-1">
                      <UserTooltip user={defect.supervisor}>
                        <Avatar className="custom-shadow h-[35px] w-[35px]">
                          <AvatarImage
                            src={`${process.env.NEXT_PUBLIC_UPLOAD_URL}/${defect.supervisor.profile.image?.path}`}
                          />
                          <AvatarFallback id={defect.supervisor.id.toString()}>
                            {getInitials(defect.supervisor.profile.name)}
                          </AvatarFallback>
                        </Avatar>
                      </UserTooltip>

                      <span className="text-card-foreground text-lg truncate">
                        {defect.supervisor.profile.name}
                      </span>
                    </div>
                  )}
                </div>
              </div>
            </AlertDialogHeader>

            {type === "resolve" || type === "edit-resolve" ? null : (
              <div className="flex flex-col gap-1">
                <div className="text-sm font-semibold">{t("Detail")}</div>
                <Textarea
                  onChange={handleDefectDescription}
                  className="h-[100px] bg-secondary border-none"
                  placeholder={`${t("Detail")}...`}
                />
                {detailError && (
                  <p className="text-sm font-light text-destructive italic mt-1">
                    {detailError}
                  </p>
                )}
              </div>
            )}

            <div className="flex flex-col gap-1 justify-between w-full">
              <div className="text-sm font-semibold">{t("Image")}</div>
              <div className="grid grid-cols-2 pr-2 gap-4">
                <div className="flex flex-col gap-1 flex-1 rounded-md">
                  <div
                    className="flex h-full w-full rounded-[10px] bg-secondary justify-center items-center"
                    onDragOver={handleDragOver}
                    onDrop={handleDrop}
                  >
                    <div className="flex p-8 flex-col items-center justify-center">
                      <span className="material-symbols-outlined text-[48px] font-normal text-primary">
                        upload
                      </span>
                      <div className="text-center mt-2">
                        {t("Drag&DropFile")}
                      </div>
                      <div className="text-center mt-1">{t("Or")}</div>
                      <div className="mt-2">
                        <input
                          type="file"
                          id="file-input"
                          style={{ display: "none" }}
                          multiple
                          onChange={handleFileChange}
                        />
                        <Button
                          variant={"outline"}
                          className="custom-shadow"
                          onClick={handleButtonClick}
                        >
                          <span className="material-symbols-outlined mr-1">
                            browser_updated
                          </span>
                          {t("Browse")}
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
                <ScrollArea className="sm:h-64 overflow-y-auto gap-5 rounded-md flex-1">
                  <div className="flex flex-col gap-2 w-full">
                    {selectedFiles?.map((file, index) => (
                      <div
                        key={index}
                        className=" flex flex-row justify-between px-4 py-2 w-full bg-secondary rounded-md"
                      >
                        <div className="flex flex-row gap-3">
                          <div className="flex flex-col justify-center items-center">
                            <span className="material-symbols-outlined text-card-foreground">
                              image
                            </span>
                          </div>
                          <div className="flex items-center gap-2 ">
                            <div className="flex flex-col">
                              <TooltipProvider>
                                <Tooltip>
                                  <TooltipTrigger asChild>
                                    <div className=" truncate sm:w-56 xl:w-72">
                                      {file.name}
                                    </div>
                                  </TooltipTrigger>
                                  <TooltipContent className="bg-foreground">
                                    <div className=" w-full ">{file.name}</div>
                                  </TooltipContent>
                                </Tooltip>
                              </TooltipProvider>

                              <p className="text-sm font-semibold text-muted-foreground">
                                {(file.size / 1024).toFixed(2)} KB
                              </p>
                            </div>
                          </div>
                        </div>
                        <Button
                          variant={"ghost"}
                          className="flex flex-col justify-center items-center w-[40px] h-[40px]"
                          onClick={() => handleRemoveFile(index)}
                        >
                          <span className="material-symbols-outlined text-destructive">
                            delete
                          </span>
                        </Button>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </div>
              {fileError && (
                <p className="text-sm font-light text-destructive italic mt-1">
                  {fileError}
                </p>
              )}
            </div>

            <AlertDialogFooter>
              <div className="flex items-end justify-end gap-[10px]">
                <AlertDialogCancel
                  onClick={() => {
                    setDetailError(null);
                    setFileError(null);
                    setSelectedFiles(null);
                    setDefectDescription(null);
                  }}
                >
                  {t("Cancel")}
                </AlertDialogCancel>
                <AlertDialogAction
                  onClick={(event) => {
                    handleSendClick(event);
                  }}
                  className={"bg-primary"}
                >
                  <span className="material-symbols-outlined text-2xl me-2">
                    send
                  </span>
                  {t("Send")}
                </AlertDialogAction>
              </div>
            </AlertDialogFooter>

            {isAlertOpen && (
              <AlertCustom
                title={
                  type === "report"
                    ? a("ReportDefectConfirmTitle")
                    : type === "edit"
                    ? a("ReportEditConfirmTitle")
                    : type === "edit-resolve"
                    ? a("ReportEditResolveConfirmTitle")
                    : a("ReportResolveConfirmTitle")
                }
                description={
                  type === "report"
                    ? a("ReportDefectConfirmDescription")
                    : type === "edit"
                    ? a("ReportEditConfirmDescription")
                    : type === "edit-resolve"
                    ? a("ReportEditResolveConfirmDescription")
                    : a("ReportResolveConfirmDescription")
                }
                primaryButtonText={t("Confirm")}
                primaryIcon="check"
                secondaryButtonText={t("Cancel")}
                backResult={(backResult) => {
                  if (backResult) {
                    switch (type) {
                      case "report":
                        handleCreateDefect(
                          item.name,
                          defectDescription,
                          item.type,
                          result.inspectorId || userId,
                          patrolResults.find(
                            (patrolResult: IPatrolResult) =>
                              result.id === patrolResult.id
                          )?.id || null,
                          patrolResults.find(
                            (patrolResult: IPatrolResult) =>
                              result.zoneId === patrolResult.zoneId
                          )?.supervisor.id || null,
                          selectedFiles
                        );
                        break;

                      case "resolve":
                        handleResolveDefect(
                          defect.id,
                          selectedFiles,
                          "resolve",
                          false
                        );
                        break;

                      case "edit":
                        handleEditDefect(
                          defect.id,
                          defect.name,
                          defectDescription,
                          defect.type,
                          defect.userId,
                          defect.patrolResultId,
                          defect.patrolResult.itemZone.zone.supervisor.id,
                          selectedFiles
                        );
                        break;

                      case "edit-resolve":
                        handleResolveDefect(
                          defect.id,
                          selectedFiles,
                          "edit",
                          true
                        );
                        break;

                      default:
                        console.error("Invalid type");
                    }
                  }
                  handleCloseDialog();
                }}
              />
            )}
          </AlertDialogContent>
        )}
      </AlertDialog>
    </div>
  );
}

/**
 * :
 *    Defect  Defect  API
