        >
          <PieChart width={300} height={350}>
            <ChartTooltip
              cursor={false}
              content={({ payload }) => {
                if (!payload || payload.length === 0) return null;

                return (
                  <div className="bg-card-foreground px-2.5 py-1.5 custom-shadow rounded-md">
