import { IDefect, IPatrol, IPatrolChecklist, IPatrolResult, IUser } from "@/app/type";
import Loading from "@/components/loading";
import { useSocket } from "@/components/socket-provider";
import { Skeleton } from "@/components/ui/skeleton";
import { fetchData } from "@/lib/utils";
import { notFound, useParams } from "next/navigation";
import React, { createContext, useContext, useState, ReactNode, useEffect, useRef, useDebugValue } from "react";
import { boolean, IpVersion } from "zod";
import { AlertCustom } from "@/components/alert-custom";
import { toast } from "@/hooks/use-toast";
import { useTranslations } from "next-intl";

interface IPatrolContext {
    patrol: IPatrol | null;
    patrolResults: IPatrolResult[];
    user: IUser | null;
    lock: boolean;
    isAlertOpen: boolean;
    handleResult: (patrolResult: IPatrolResult) => void;
    handleUpdateResult: (patrolResult: IPatrolResult[]) => void;
    toggleLock: () => void;
    handleStartPatrol: () => void;
    handleFinishPatrol: () => void;
    fetchRealtimeData: (defect: IDefect, actionType: string) => void;
    calculateProgress: () => number;
    handleOpenDialog: () => void
    handleCloseDialog: () => void
    itemCounts: (patrol: IPatrol, results: IPatrolResult[]) => void
    mounted: boolean;
    canFinish: boolean;
    defects: IDefect[]
    countItems: number
    countFails: number
    countDefects: number
    patrolUser: IUser[]
    isHovered: boolean
    handleMouseLeave: () => void
    handleMouseEnter: () => void
    formatZone: (patrol: IPatrol) => string
    formatTimeDate: (dateStr: string) => string
    formatId: (id: number) => string
    formatDate: (dateStr: string) => string
}

const PatrolContext = createContext<IPatrolContext | undefined>(undefined);

export const PatrolProvider: React.FC<{ children: React.ReactNode }> = ({
    children,
}) => {
    const [patrol, setPatrol] = useState<IPatrol | null>(null);
    const [defects, setDefects] = useState<IDefect[]>([]);
    const [patrolResults, setPatrolResults] = useState<IPatrolResult[]>([]);
    const [user, setUser] = useState<IUser | null>(null);
    const { socket, isConnected } = useSocket();
    const [lock, setLock] = useState(false);
    const [mounted, setMounted] = useState(false);
    const params = useParams();
    const a = useTranslations("Alert");

    const [patrolUser, setPatrolUser] = useState<IUser[] | null>([])
    const [isAlertOpen, setIsAlertOpen] = useState(false);
    const [isHovered, setIsHovered] = useState(false);

    const [countItems, setCountItems] = useState(0);
    const [countFails, setCountFails] = useState(0);
    const [countDefects, setCountDefects] = useState(0);

    const itemCounts = (patrol: IPatrol, results: IPatrolResult[]) => {
        let itemCounts = 0
        patrol?.patrolChecklists.forEach(patrolChecklist => {
            //  item 
            patrolChecklist.checklist.items.forEach(item => {
                item.itemZones.forEach(() => {
                    itemCounts++
                })
            });
        });

        let countItems = itemCounts;
        let countFails = 0;
        let countDefects = 0;

        if (patrol?.status !== "pending" && patrol?.status !== "scheduled") {
            if (results) {
                for (const patrolResult of results) {
                    if (patrolResult.status === false) {
                        countFails++;
                        if (patrolResult.defects && patrolResult.defects.length !== 0) {
                            countDefects += patrolResult.defects.length;
                        }
                    }
                }
            }
        }

        setCountItems(countItems)
        setCountFails(countFails)
        setCountDefects(countDefects)

        return { countItems, countFails, countDefects }
    }


    const formatDate = (dateStr: string): string => {
        const date = new Date(dateStr);
        return date.toLocaleDateString('en-GB');
    };

    const formatId = (id: number): string => {
        return `P${id.toString().padStart(4, '0')}`;
    };

    const formatTimeDate = (dateStr: string): string => {
        const date = new Date(dateStr);
        const hours = date.getHours().toString().padStart(2, '0');
        const minutes = date.getMinutes().toString().padStart(2, '0');
        return `${hours}.${minutes}`;
    };

    const formatZone = (patrol: IPatrol) => {
        const zones = new Set<string>();
        patrol.patrolChecklists.forEach((checklist: any) => {
            checklist.checklist.items.forEach((item: any) => {
                item.itemZones.forEach((zoneObj: any) => {
                    const zoneName = zoneObj.zone.name;
                    if (typeof zoneName === "string") {
                        zones.add(zoneName);
                    }
                });
            });
        });
        return Array.from(zones)
            .map(
                (zone) =>
                    zone
                        .split("_") //  `_`
                        .map(
                            (word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
                        ) // 
                        .join(" ") // 
            )
            .join(", "); // 
    };

    const handleMouseEnter = () => {
        setIsHovered(true);
    };

    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    const handleOpenDialog = () => {
        setIsAlertOpen(true);
    };

    const handleCloseDialog = () => {
        setIsAlertOpen(false)
    }

    const totalResults = patrolResults?.length || 0
    const checkedResults =
        patrolResults?.filter((res) => res.status !== null).length || 0;
    const canFinish = checkedResults === totalResults;

    const getPatrolData = async () => {
        if (params.id) {
            try {
                const data = await fetchData("get", `/patrol/${params.id}?preset=true&result=true`, true);
                setPatrol(data);
                //  localStorage  patrol.id 
                const savedResults = localStorage.getItem(`patrolResults_${patrol.id}`);
                let mergedResults = [...data.results]; //  server
                if (savedResults) {
                    const savedResultsParsed = JSON.parse(savedResults);

                    //  localStorage  server
                    savedResultsParsed.forEach((savedResult: IPatrolResult) => {
                        const existingIndex = mergedResults.findIndex((r) => r.id === savedResult.id);

                        if (existingIndex !== -1) {
                            //  server  id ,  ()
                            mergedResults[existingIndex] = {
                                ...mergedResults[existingIndex],
                                ...savedResult,
                            };
                        } else {
                            // ,  localStorage 
                            mergedResults.push(savedResult);
                        }
                    });
                }

                //  state 
                setPatrolResults(mergedResults);
            } catch (error) {
                console.error("Failed to fetch patrol data:", error);
            }
        }
    };

    const getDefectData = async () => {
        try {
            const defectFetch = await fetchData("get", `/patrol/${params.id}/defects`, true);
            setDefects(defectFetch);
        } catch (error) {
            console.error("Failed to fetch defects data:", error);
        }
    };

    const calculateProgress = () => {
        if (!patrol) return 0;
        const checkedResults = patrolResults.filter(
            (res) => res.status !== null && (res.status === false ? (res.comments?.length > 0 || res.defects?.length > 0) : true)
        ).length;
        if (totalResults === 0) return 0;
        return (checkedResults / totalResults) * 100;
    };

    const toggleLock = () => setLock((prev) => !prev);

    const handleResult = (patrolResult: IPatrolResult) => {
        setPatrolResults((prevResults) => {
            const existingIndex = prevResults.findIndex(
                (r) => r.id === patrolResult.id
            );

            let updatedResults;
            if (existingIndex !== -1) {
                updatedResults = [...prevResults];
                updatedResults[existingIndex] = {
                    ...updatedResults[existingIndex],
                    ...patrolResult,
                };
            } else {
                updatedResults = [...prevResults, patrolResult];
            }
            if (socket && patrol?.id) {
                socket.emit("update_patrol_result", {
                    patrolId: patrol.id,
                    result: patrolResult
                });
            }

            return updatedResults
        });
    };

    const handleUpdateResult =(patrolResult: IPatrolResult[]) => {
        setPatrolResults(patrolResult)
    };
    
    const handleStartPatrol = async () => {
        setIsAlertOpen(false)

        if (!patrol) return;
        const patrolId = patrol.id;
        const data = {
            status: patrol?.status,
            checklists: patrol?.patrolChecklists,
        };

        try {
            const startPatrol = await fetchData(
                "put",
                `/patrol/${patrolId}/start`,
                true,
                data
            );
            socket.emit("start_patrol", {
                patrolId: patrol.id,
                patrolData: startPatrol,
            });
            setPatrol(startPatrol)
            setPatrolResults(startPatrol.results)
            toast({
                variant: "default",
                title: a("PatrolStartTitle"),
                description: a("PatrolStartDescription"),
            });
        } catch (error) {
            console.error("Error starting patrol:", error);
        }
    };

    const handleFinishPatrol = async () => {
        if (!patrol) return;

        const data = {
            status: patrol.status,
            checklists: patrol.patrolChecklists,
            results: patrolResults,
            startTime: patrol.startTime
        };

        let resultCount = 0;
        for (const checklist of patrol.patrolChecklists) {
            for (const item of checklist.checklist.items) {
                // eslint-disable-next-line @typescript-eslint/no-unused-vars
                for (const zone of item.itemZones) {
                    resultCount++;
                }
            }
        }

        if (data.results.length === resultCount) {
            try {
                localStorage.removeItem(`patrolResults_${patrol.id}`);
                const finishPatrol = await fetchData(
                    "put",
                    `/patrol/${patrol.id}/finish`,
                    true,
                    data
                );
                socket.emit("finish_patrol", {
                    patrolId: patrol.id,
                    patrolData: finishPatrol
                });
                setPatrol(finishPatrol)

                toast({
                    variant: "default",
                    title: a("PatrolFinishTitle"),
                    description: a("PatrolFinishDescription"),
                });
            } catch (error) {
                console.error("Error finishing patrol:", error);
            }
        }
    };

    const getUserData = async () => {
        try {
            const userfetch = await fetchData("get", "/user?profile=true&image=true", true);
            setUser(userfetch);
        } catch (error) {
            console.error("Failed to fetch profile data:", error);
        }
    };

    const getPatrolUserData = async () => {
        try {
            const patrolUserFetch = await fetchData("get", `/patrol/${params.id}/user`, true);
            setPatrolUser(patrolUserFetch);
        } catch (error) {
            console.error("Failed to fetch profile data:", error);
        }
    };

    const fetchRealtimeData = (defect: IDefect, actionType: string) => {
        if (actionType === "create") {
            setDefects((prevDefects) => {
                //  defects 
                if (prevDefects && prevDefects.length > 0) {
                    return [...prevDefects, defect]; //  defect 
                } else {
                    return [defect]; //  defect   defect 
                }
            });
        } else {
            setDefects((prevDefects) =>
                prevDefects.map((d) => (d.id === defect.id ? defect : d))
            );
        }
        //  patrolResults
        setPatrolResults((prevResults) => {
            return prevResults.map((patrolResult) => {
                if (patrolResult.id === defect.patrolResultId) {
                    const updatedPatrolResult = {
                        ...patrolResult,
                        defects: [...patrolResult.defects, defect],
                    };

                    //  `patrolResult` 
                    if (socket && patrolResult.id) {
                        socket.emit("update_patrol_result", {
                            patrolId: patrolResult.patrolId,
                            result: updatedPatrolResult,
                        });
                    }

                    return updatedPatrolResult;
                }
                return patrolResult;
            });
        });
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                await getUserData();
                await getPatrolData();
                await getDefectData()
                await getPatrolUserData()
            } catch (error) {
                console.error("Error loading data: ", error);
            } finally {
                setMounted(true);
            }
        };
        fetchData();
    }, [patrol?.id])

    useEffect(() => {
        if (patrol && patrol.status !== 'on_going') {
            localStorage.removeItem(`patrolResults_${patrol.id}`);
        }
    }, [patrol])

    useEffect(() => {
        if (patrol?.id && patrolResults && patrol.status === 'on_going') {
            //  patrolResults  localStorage  key patrolResults_<patrol.id>
            localStorage.setItem(
                `patrolResults_${patrol.id}`,
                JSON.stringify(patrolResults)
            );
        }
    }, [patrolResults, patrol]);

    useEffect(() => {
        if (socket && isConnected && patrol?.id && user?.id) {
            socket.emit("join_patrol", patrol.id);

            // 
            const handleInitialData = (initialResults: IPatrolResult[]) => {
                setPatrolResults(prev => {
                    const merged = [...prev];
                    initialResults.forEach(result => {
                        const existingIndex = merged.findIndex(r => r.id === result.id);
                        if (existingIndex !== -1) {
                            merged[existingIndex] = {
                                ...merged[existingIndex],
                                ...result
                            };
                        } else {
                            merged.push(result);
                        }
                    });
                    return merged;
                });
            };

            //  real-time
            const handleResultUpdate = (incomingResult: IPatrolResult) => { // 
                setPatrolResults((prev) => {
                    const existingIndex = prev.findIndex(r => r.id === incomingResult.id);
                    if (existingIndex !== -1) {
                        const updated = [...prev];
                        updated[existingIndex] = { ...updated[existingIndex], ...incomingResult };
                        return updated;
                    }
                    return [...prev, incomingResult];
                });
            };

            const handlePatrolStarted = (data: { patrolId: string; patrolData: IPatrol }) => {
                setPatrol(data.patrolData);
                setPatrolResults(data.patrolData.results);
            };

            const handlePatrolFinished = (data: { patrolId: string; patrolData: IPatrol }) => {
                localStorage.removeItem(`patrolResults_${data.patrolId}`);
                const fetchData = async () => {
                    await getPatrolData()
                };
                fetchData();
            };

            socket.on("initial_patrol_data", handleInitialData);
            socket.on("patrol_started", handlePatrolStarted);
            socket.on("patrol_result_update", handleResultUpdate);
            socket.on("patrol_finished", handlePatrolFinished);

            return () => {
                socket.off("initial_patrol_data", handleInitialData);
                socket.off("patrol_started", handlePatrolStarted);
                socket.off("patrol_result_update", handleResultUpdate);
                socket.off("patrol_finished", handlePatrolFinished);
            };
        }
    }, [socket, isConnected, patrol?.id, user?.id]);

    if (!mounted) {
        return <Loading />;
    }

    if (mounted && !patrolResults) {
        return notFound();
    }

    return (
        <PatrolContext.Provider
            value={{
                patrol,
                patrolResults,
                // results,
                handleUpdateResult,
                user,
                lock,
                isAlertOpen,
                mounted,
                canFinish,
                defects,
                countItems,
                countFails,
                countDefects,
                patrolUser,
                isHovered,
                formatDate,
                formatId,
                formatTimeDate,
                formatZone,
                handleMouseEnter,
                handleMouseLeave,
                itemCounts,
                toggleLock,
                calculateProgress,
                handleResult,
                handleStartPatrol,
                fetchRealtimeData,
                handleFinishPatrol,
                handleOpenDialog,
                handleCloseDialog,
            }}
        >
            {children}
        </PatrolContext.Provider>
    );
}

export const usePatrol = () => {
    const context = useContext(PatrolContext);
    if (!context) {
        throw new Error('usePatrol must be used within a PatrolProvider');
    }
    return context;
};

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";
import { IPatrol, IPatrolResult, defectStatus, patrolStatus, itemType, IToast, role } from "@/app/type";
import { badgeVariants } from "@/components/badge-custom";
import { LoginSchema } from '@/app/type';
import axios, { AxiosRequestConfig } from "axios";
import { z } from "zod";


const ExcelJS = require("exceljs");

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export async function login(values: z.infer<typeof LoginSchema>) {
  const validatedFields = LoginSchema.safeParse(values)
  if (!validatedFields.success) {
    return { error: "Invalid field!" }
  }
  try {
    const csrfResponse = await axios.get(`${process.env.NEXT_PUBLIC_API_URL}/csrf-token`, { withCredentials: true });
    const csrfToken = csrfResponse.data.csrfToken;

    const response = await axios.post(`${process.env.NEXT_PUBLIC_API_URL}/login`,
      values,
      {
        headers: {
          "X-CSRF-Token": csrfToken,
        },
        withCredentials: true
      })
    return response.data
  } catch (error: any) {
    return { error: error.response?.data?.message || "Login failed" };
  }
}

export async function logout() {
  try {
    const csrfResponse = await axios.get(`${process.env.NEXT_PUBLIC_API_URL}/csrf-token`, { withCredentials: true });
    const csrfToken = csrfResponse.data.csrfToken;
    await axios.post(`${process.env.NEXT_PUBLIC_API_URL}/logout`, {}, {
      headers: {
        "X-CSRF-Token": csrfToken,
      },
      withCredentials: true
    })
  } catch (error: any) {
    throw new Error("Logout failed", error);
  }
}

export async function fetchData(
  type: "get" | "delete" | "post" | "put",
  endpoint: string,
  credential: boolean,
  value?: any,
  form?: boolean
) {
  try {
    const csrfResponse = await axios.get(`${process.env.NEXT_PUBLIC_API_URL}/csrf-token`, { withCredentials: true });
    const csrfToken = csrfResponse.data.csrfToken;
    const config: AxiosRequestConfig = {
      withCredentials: credential,
      headers: {
        "X-CSRF-Token": csrfToken,
        "Content-Type": form ? "multipart/form-data" : "application/json",
      },
    };

    let response;
    if (type === "get" || type === "delete") {
      response = await axios[type](`${process.env.NEXT_PUBLIC_API_URL}${endpoint}`, config);
    } else if (type === "post" || type === "put") {
      response = await axios[type](`${process.env.NEXT_PUBLIC_API_URL}${endpoint}`, value, config);
    }

    return response?.data; // Return the response body
  } catch (error: any) {
    console.error("Failed to fetch data:", error);

    // Return more detailed error information
    if (error.response) {
      return {
        error: true,
        status: error.response.status,
        data: error.response.data,
      };
    }

    // For unexpected errors
    return {
      error: true,
      message: error.message || "Unexpected error occurred",
    };
  }
}

export const exportData = async (patrol: IPatrol, result: IPatrolResult[]) => {
  try {
    const workbook = new ExcelJS.Workbook();
    const worksheet = workbook.addWorksheet("Patrol Report");

    const formatDateTime = (dateTime) => {
      const date = new Date(dateTime);
      // 
      const formattedDate = date.toLocaleDateString('en-GB');
      // 
      const formattedTime = date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false });
      return `${formattedDate} ${formattedTime}`;
    };

    // Funtion   quality_control_zone  Quality Control Zone
    const formatZoneName = (zoneName) => {
      return zoneName
        .split("_")
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(" ");
    }

    //  Patrol 
    const rows = [
      ["Date:", formatDateTime(patrol.date)],
      ["Start Time:", formatDateTime(patrol.startTime)],
      ["End Time:", formatDateTime(patrol.endTime)],
      ["Status:", patrol.status],
      ["Preset Title:", patrol.preset.title],
      ["Preset Version:", patrol.preset.version],
      ["Preset Description:", patrol.preset.description],
      [] // 
    ];

    //   alignment
    rows.forEach((data) => {
      const row = worksheet.addRow(data);
      row.font = { size: 12 }
      row.alignment = { horizontal: "left" }; // 
    });

    //  Column
    worksheet.columns = [
      { width: 30 },
      { width: 25 },
      { width: 25 },
      { width: 25 },
      { width: 25 },
    ];

    let totalFails = 0;
    let totalComments = 0;
    let totalDefects = 0;
    let totalPass = 0;

    //  patrolChecklist
    for (const patrolChecklist of patrol.patrolChecklists) {
      const inspectorName = patrolChecklist.inspector.profile.name;
      const checklist = patrolChecklist.checklist;

      //  Checklist
      const titleRow = worksheet.addRow([checklist.title + " By " + inspectorName]);
      // 
      const titleRowIdx = worksheet.rowCount;
      // Merge  (., ., ., .)
      worksheet.mergeCells(titleRowIdx, 0, titleRowIdx, 5);
      //   
      titleRow.getCell(1).font = { bold: true, color: { argb: 'FFFFFFFF' }, size: 14 };
      titleRow.getCell(1).alignment = { horizontal: "center", vertical: "middle" };
      titleRow.getCell(1).border = {
        top: { style: 'thin', color: { argb: 'FF000000' } },
        left: { style: 'thin', color: { argb: 'FF000000' } },
        bottom: { style: 'thin', color: { argb: 'FF000000' } },
        right: { style: 'thin', color: { argb: 'FF000000' } },
      };
      // 
      titleRow.getCell(1).fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: 'FFFF0000' } }; // 

      // 
      const headerRow = worksheet.addRow(["Item Name", "Zone Name", "Status", "Annotation", ""]);
      const headerRowIdx = worksheet.rowCount;
      worksheet.mergeCells(headerRowIdx , 4, headerRowIdx, 5);

      //   
      headerRow.eachCell((cell) => {
        cell.alignment = { horizontal: 'center', vertical: 'middle' };
        cell.font = { bold: true, size: 12 };
        cell.border = {
          top: { style: 'thin' },
          left: { style: 'thin' },
          bottom: { style: 'thin' },
          right: { style: 'thin' }
        };
        cell.fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: 'FFF1F1F1' } };  // 
      });

      // Loop Items  Checklist
      for (const item of checklist.items) {
        // Loop Zones  Item
        for (const itemZone of item.itemZones) {
          const zoneName = formatZoneName(itemZone.zone.name);
          const resultItem = result.find(
              (r) => r.itemId === item.id && r.zoneId === itemZone.zone.id
          );
  
          let statusText = "N/A";
          let annotationDetails = [];
  
          if (resultItem) {
            if (resultItem.status === true) {
                statusText = "Passed";
                totalPass++;
            } else if (resultItem.status === false) {
                statusText = "Failed";
                totalFails++;
            }

            if (resultItem.comments?.length > 0) {
              statusText = "Commented";
              totalComments += resultItem.comments.length;
              totalFails--;

              // Loop  Commends  Annotations
              for (const comment of resultItem.comments) {
                  annotationDetails.push([formatDateTime(comment.timestamp), comment.message]);
              }
            }

            if (resultItem.defects?.length > 0) {
              statusText = "Defected";
              totalDefects += resultItem.defects.length;
              totalFails--;

              // Loop  Defects  Annotations
              for (const defect of resultItem.defects) {
                annotationDetails.push([formatDateTime(defect.startTime), defect.description]);
              }
            }
          }
    
          //  Rows
          const startRow = worksheet.rowCount + 1;
          worksheet.addRow([item.name, zoneName, statusText, "-"]);
  
          //  Comments  Defects  Annotation rows
          if (annotationDetails.length > 0) {
            //  Subrows  Annotation  Commend
            if (statusText === "Commented") {
              worksheet.getCell(`D${startRow}`).value = "Comments DateTime"; 
              worksheet.getCell(`E${startRow}`).value = "Comments Detail";
              worksheet.getCell(`D${startRow}`).font = { bold: true };
              worksheet.getCell(`E${startRow}`).font = { bold: true };
            //  Subrows  Annotation  Defect
            } else if (statusText === "Defected") {
              worksheet.getCell(`D${startRow}`).value = "Defects DateTime"; 
              worksheet.getCell(`E${startRow}`).value = "Defects Detail";
              worksheet.getCell(`D${startRow}`).font = { bold: true };
              worksheet.getCell(`E${startRow}`).font = { bold: true };
            }
            // Loop  Annotations
            for (const [timestamp, detail] of annotationDetails) {
                const annotationRow = worksheet.addRow(["", "", "", timestamp, detail]);
                annotationRow.eachCell(cell => {
                    cell.alignment = { horizontal: 'left', vertical: 'middle', wrapText: true };
                    cell.border = { top: { style: 'thin' }, bottom: { style: 'thin' }, left: { style: 'thin' }, right: { style: 'thin' } };
                });
            }
          }else {
            // Merge columns D  E  Annotation
            const annotationRowIndex = worksheet.rowCount;
            worksheet.mergeCells(`D${annotationRowIndex}:E${annotationRowIndex}`);
        
            worksheet.getCell(`D${annotationRowIndex}`).alignment = { 
                horizontal: 'center', vertical: 'middle', wrapText: true 
            };
        
            worksheet.getCell(`D${annotationRowIndex}`).border = { 
                top: { style: 'thin' }, 
                bottom: { style: 'thin' }, 
                left: { style: 'thin' }, 
                right: { style: 'thin' } 
            };
          }

          // Merge Item Name, Zone Name,  Status  Annotations  Rows
          const endRow = worksheet.rowCount;
          if (endRow > startRow) {
            worksheet.mergeCells(`A${startRow}:A${endRow}`);
            worksheet.mergeCells(`B${startRow}:B${endRow}`);
            worksheet.mergeCells(`C${startRow}:C${endRow}`);
          }

          for (let row = startRow; row <= endRow; row++) {
            worksheet.getRow(row).eachCell(cell => {
                cell.alignment = { horizontal: 'center', vertical: 'middle', wrapText: true };
                cell.border = { top: { style: 'thin' }, bottom: { style: 'thin' }, left: { style: 'thin' }, right: { style: 'thin' } };
            });
          }
        }
      }
      const columnCount = headerRow.cellCount;
      const blankRow = worksheet.addRow(Array(columnCount).fill("")); // 
      blankRow.eachCell((cell) => {
        cell.border = { top: { style: 'thin' } };
      });
    }

    // 
    const sumRow = worksheet.addRow(["Summary"]);
    // 
    const sumRowIdx = worksheet.rowCount;
    // Merge  (., ., ., .)
    worksheet.mergeCells(sumRowIdx, 0, sumRowIdx, 4);
    //   
    sumRow.getCell(1).font = { bold: true, color: { argb: 'FFFFFFFF' }, size: 14 };
    sumRow.getCell(1).alignment = { horizontal: "center", vertical: "middle" };
    sumRow.getCell(1).border = {
      top: { style: 'thin' },
      left: { style: 'thin' },
      bottom: { style: 'thin' },
      right: { style: 'thin' },
    };
    sumRow.getCell(1).fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: '008000' } }; // 

    //  Array 
    const zoneStatusCount = [];

    // 
    let totalPassed = 0;
    let totalCommented = 0;
    let totalDefected = 0;

    //  Zone Name  Status  zoneStatusCount
    for (const patrolChecklist of patrol.patrolChecklists) {
      for (const item of patrolChecklist.checklist.items) {
        for (const itemZone of item.itemZones) {
          const zoneName = formatZoneName(itemZone.zone.name);
          const resultItem = result.find(
            (r) => r.itemId === item.id && r.zoneId === itemZone.zone.id
          );

          let statusText = "N/A"; // 
          if (resultItem) {
            if (resultItem.status === true) {
              statusText = "Passed";
            } else if (resultItem.status === false) {
              statusText = "Failed";
            }

            if (resultItem.defects && resultItem.defects.length > 0) {
              statusText = "Defected";
            }

            if (resultItem.comments && resultItem.comments.length > 0) {
              statusText = "Commented";
            }
          }

          //  zoneStatusCount 
          let zoneEntry = zoneStatusCount.find(entry => entry.zoneName === zoneName);
          if (!zoneEntry) {
            zoneEntry = { zoneName, Passed: 0, Failed: 0, Commented: 0, Defected: 0, "N/A": 0 };
            zoneStatusCount.push(zoneEntry);
          }

          // 
          zoneEntry[statusText] += 1;

          // 
          if (statusText === "Passed") totalPassed += 1;
          if (statusText === "Commented") totalCommented += 1;
          if (statusText === "Defected") totalDefected += 1;
        }
      }
    }

    // 
    const zoneSummaryHeaderRow = worksheet.addRow(["Zone Name", "Passed", "Commented", "Defected"]);
    zoneSummaryHeaderRow.eachCell((cell) => {
      cell.font = { bold: true, size: 12 }; // 
      cell.alignment = { horizontal: "center", vertical: "middle" }; // 
      cell.border = {
        top: { style: 'thin' },
        left: { style: 'thin' },
        bottom: { style: 'thin' },
        right: { style: 'thin' },
      };
      cell.fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: 'FFF1F1F1' } };  // 
    });

    // 
    zoneStatusCount.forEach(zoneEntry => {
      const summaryRow = worksheet.addRow([
        zoneEntry.zoneName,
        zoneEntry.Passed,
        zoneEntry.Commented,
        zoneEntry.Defected,
      ]);

      summaryRow.eachCell((cell) => {
        cell.alignment = { horizontal: "center", vertical: "middle" }; // 
        cell.font = { size: 12 };
        cell.border = {
          top: { style: 'thin' },
          left: { style: 'thin' },
          bottom: { style: 'thin' },
          right: { style: 'thin' },
        };
      });
    });

    // 
    const totalRow = worksheet.addRow([
      "Total",
      totalPassed,
      totalCommented,
      totalDefected
    ]);

    totalRow.eachCell((cell) => {
      cell.font = { bold: true, size: 12 }; // 
      cell.alignment = { horizontal: "center", vertical: "middle" }; // 
      cell.border = {
        top: { style: 'thin' },
        left: { style: 'thin' },
        bottom: { style: 'thin' },
        right: { style: 'thin' },
      };
      cell.fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: 'FFF1F1F1' } };  // 
    });

    worksheet.addRow([]);

    // 
    const resultRow = worksheet.addRow(["Result"]);
    // 
    const resultRowIdx = worksheet.rowCount;
    // Merge  (., ., ., .)
    worksheet.mergeCells(resultRowIdx, 0, resultRowIdx, 2);
    //   
    resultRow.getCell(1).font = { bold: true, color: { argb: 'FFFFFFFF' }, size: 14 };
    resultRow.getCell(1).alignment = { horizontal: "center", vertical: "middle" };
    resultRow.getCell(1).border = {
      top: { style: 'thin' },
      left: { style: 'thin' },
      bottom: { style: 'thin' },
      right: { style: 'thin' },
    };
    resultRow.getCell(1).fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: '00008B' } };  // 

    //  Pass  Fail
    const passFailHeaderRow = worksheet.addRow(["Pass", "Fail"]);
    passFailHeaderRow.eachCell((cell) => {
      cell.font = { bold: true, size: 12 }; // 
      cell.alignment = { horizontal: "center", vertical: "middle" }; // 
      cell.border = {
        top: { style: 'thin' },
        left: { style: 'thin' },
        bottom: { style: 'thin' },
        right: { style: 'thin' },
      };
      cell.fill = { type: 'pattern', pattern: 'solid', fgColor: { argb: 'FFF1F1F1' } };  // 
    });

    //  Pass  Fail
    const passFailCountRow = worksheet.addRow([
      totalPassed, //  Pass
      totalCommented + totalFails + totalDefected, //  Fail
    ]);

    passFailCountRow.eachCell((cell) => {
      cell.alignment = { horizontal: "center", vertical: "middle" }; // 
      cell.font = { size: 12 };
      cell.border = {
        top: { style: 'thin' },
        left: { style: 'thin' },
        bottom: { style: 'thin' },
        right: { style: 'thin' },
      };
    });

    //  workbook  buffer
    const buffer = await workbook.xlsx.writeBuffer();

    //  blob 
    const blob = new Blob([buffer], {
      type:
        "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "Patrol_Inspection_Report_" + patrol.date.toString().split('T')[0] + ".xlsx";

    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  } catch (error) {
    console.error("Error exporting data:", error);
    throw new Error(
      `Could not export data: ${error instanceof Error ? error.message : "Unknown error"
      }`
    );
  }
};

export const getInitials = (name: string) => {
  if (!name) return "";
  const nameParts = name.split(" ");

  if (nameParts.length === 1) {
    return nameParts[0].charAt(0).toUpperCase();
  } else {
    return (
      nameParts[0].charAt(0).toUpperCase() +
      nameParts[nameParts.length - 1].charAt(0).toUpperCase()
    );
  }
};

export const sortData = (data: any, sort: { by: string; order: string }) => {
  const sortedData = [...data];
  if (sort.by === "Doc No.") {
    sortedData.sort((a, b) =>
      sort.order === "Ascending"
        ? a.id - b.id  //  
        : b.id - a.id  //  
    );
  } else if (sort.by === "Date") {
    sortedData.sort((a, b) =>
      sort.order === "Ascending"
        ? new Date(a.date).getTime() - new Date(b.date).getTime()
        : new Date(b.date).getTime() - new Date(a.date).getTime()
    );
  } else if (sort.by === "Status") {
    sortedData.sort((a, b) =>
      sort.order === "Ascending"
        ? String(a.status).localeCompare(String(b.status)) //  "false" -> "true"
        : String(b.status).localeCompare(String(a.status)) //  "true" -> "false"
    );
  } else if (sort.by === "DefectDate") {
    sortedData.sort((a, b) =>
      sort.order === "Ascending"
        ? new Date(a.startTime).getTime() - new Date(b.startTime).getTime()
        : new Date(b.startTime).getTime() - new Date(a.startTime).getTime()
    );
  } else if (sort.by === "CommentDate") {
    sortedData.sort((a, b) =>
      sort.order === "Ascending"
        ? new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime()
        : new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime()
    );
  }
  return sortedData
}

// Function to calculate time ago
export function timeAgo(timestamp: string, t: any): string {
  const now = new Date();
  const notificationDate = new Date(timestamp);
  const seconds = Math.floor((now.getTime() - notificationDate.getTime()) / 1000);

  const intervals: { [key: string]: number } = {
    year: 31536000,
    month: 2592000,
    week: 604800,
    day: 86400,
    hour: 3600,
    minute: 60,
    second: 1
  };

  for (const key in intervals) {
    const interval = intervals[key];
    const result = Math.floor(seconds / interval);
    if (result >= 1) {
      return t(`${key}Ago`, { count: result });
    }
  }
  return t('justNow');
}

